'use strict'

const ConnectionEndpoint = require('./message/connection-endpoint')
const MessageProcessor = require('./message/message-processor')
const MessageDistributor = require('./message/message-distributor')
const EventHandler = require('./event/event-handler')
const messageParser = require('./message/message-parser')
const readMessage = require('./utils/read-message')
const fs = require('fs')
const path = require('path')
const util = require('util')
const utils = require('./utils/utils')
const defaultOptions = require('./default-options')
const configInitialiser = require('./config/config-initialiser')
const jsYamlLoader = require('./config/js-yaml-loader')
const RpcHandler = require('./rpc/rpc-handler')
const RecordHandler = require('./record/record-handler')
const PresenceHandler = require('./presence/presence-handler')
const DependencyInitialiser = require('./utils/dependency-initialiser')
const ClusterRegistry = require('./cluster/cluster-registry')
const UniqueRegistry = require('./cluster/cluster-unique-state-provider')
const C = require('./constants/constants')
const pkg = require('../package.json')

const EventEmitter = require('events').EventEmitter
const EOL = require('os').EOL

const STATES = C.STATES

/**
 * Deepstream is a realtime data server that scales horizontally
 * by running in clusters of interacting nodes
 *
 * @copyright 2016 deepstreamHub GmbH
 * @author deepstreamHub GmbH
 *
 * @param {Object} config Configuration object
 *
 * @constructor
 */
const Deepstream = function (config) {
  this._currentState = STATES.CLOSED
  this.constants = C
  this._loadConfig(config)
  this._connectionEndpoint = null
  this._messageProcessor = null
  this._messageDistributor = null
  this._eventHandler = null
  this._rpcHandler = null
  this._recordHandler = null
  this._plugins = [
    'messageConnector',
    'storage',
    'cache',
    'authenticationHandler',
    'permissionHandler'
  ]
}

util.inherits(Deepstream, EventEmitter)

/**
 * Sets the name of the process
 *
 * @type {String}
 */
process.title = 'deepstream server'

/**
 * Expose constants to allow consumers to access them without
 * requiring a reference to a deepstream instance.
 *
 *
*/
Deepstream.constants = C


/**
 * Utility method to return a helper object to simplify permissions assertions
 *
 * @param  {object} message description
 * @return {object}         description
 */
Deepstream.readMessage = readMessage

/**
 * Set a deepstream option. For a list of all available options
 * please see default-options.
 *
 * @param {String} key   the name of the option
 * @param {Mixed} value  the value, e.g. a portnumber for ports or an instance of a logger class
 *
 * @public
 * @returns {void}
 */
Deepstream.prototype.set = function (key, value) {
  if (key === 'message') {
    key = 'messageConnector' // eslint-disable-line
  }

  if (this._options[key] === undefined) {
    throw new Error(`Unknown option "${key}"`)
  }

  this._options[key] = value
  return this
}

/**
 * Returns true if the deepstream server is running, otherwise false
 *
 * @public
 * @returns {boolean}
 */
Deepstream.prototype.isRunning = function () {
  return this._currentState === STATES.IS_RUNNING
}

/**
 * Starts up deepstream. The startup process has three steps:
 *
 * - First of all initialise the logger and wait for it (ready event)
 * - Then initialise all other dependencies (cache connector, message connector, storage connector)
 * - Instantiate the messaging pipeline and record-, rpc- and event-handler
 * - Start WS server
 *
 * @public
 * @returns {void}
 */
Deepstream.prototype.start = function () {
  if (this._currentState !== STATES.CLOSED) {
    throw new Error(`Server can only start after it stops succesfully, currently ${this._currentState}`)
  }
  this._currentState = STATES.STARTING
  this._showStartLogo()
  const loggerInitializer = new DependencyInitialiser(this._options, 'logger')
  loggerInitializer.once('ready', this._start.bind(this))
}

/**
 * This is the actual function which starts deepstream. It is invoked after+
 * the logger was intialized or emitted the read event if it was initialized
 * asynchronously
 *
 * @private
 * @returns {void}
 */
Deepstream.prototype._start = function () {
  this._options.logger.log(C.LOG_LEVEL.INFO, C.EVENT.INFO, `deepstream version: ${pkg.version}`)

  // otherwise (no configFile) deepstream was invoked by API
  if (this._configFile != null) {
    this._options.logger.log(C.LOG_LEVEL.INFO, C.EVENT.INFO, `configuration file loaded from ${this._configFile}`)
  }

  if (global.deepstreamLibDir) {
    this._options.logger.log(C.LOG_LEVEL.INFO, C.EVENT.INFO, `library directory set to: ${global.deepstreamLibDir}`)
  }

  let i
  let initialiser

  for (i = 0; i < this._plugins.length; i++) {
    initialiser = new DependencyInitialiser(this._options, this._plugins[i])
    initialiser.once('ready', this._checkReady.bind(this, this._plugins[i], initialiser.getDependency()))
  }
  this._checkReady('logger', this._options.logger)
}

/**
 * Stops the server and closes all connections. The server can be started again,
 * but all clients have to reconnect. Will emit a 'stopped' event once done
 *
 * @public
 * @returns {void}
 */
Deepstream.prototype.stop = function () {
  if (this._currentState !== STATES.IS_RUNNING) {
    throw new Error(`Server can only be stopped after it starts succesfully, currently ${this._currentState}`)
  }
  this._currentState = STATES.CLOSING

  let i
  let plugin
  const closables = [this._connectionEndpoint]

  if (typeof this._options.logger.close === 'function') {
    closables.push(this._options.logger)
    setTimeout(this._options.logger.close.bind(this._options.logger))
  }

  for (i = 0; i < this._plugins.length; i++) {
    plugin = this._options[this._plugins[i]]
    if (typeof plugin.close === 'function') {
      closables.push(plugin)
      setTimeout(plugin.close.bind(plugin))
    }
  }

  utils.combineEvents(closables, 'close', this._onStopped.bind(this))
  this._options.clusterRegistry.leaveCluster()
  this._connectionEndpoint.close()
}

/**
 * Expose the message-parser's convertTyped method
 * so that it can be used within permissionHandlers
 *
 * @param   {String} value A String starting with a type identifier (see C.TYPES)
 *
 * @public
 * @returns {mixed} the converted value
 */
Deepstream.prototype.convertTyped = function (value) {
  return messageParser.convertTyped(value)
}

/**
 * Synchronously loads a configuration file
 * Initialization of plugins and logger will be triggered by the
 * configInitialiser, but it should not block. Instead the ready events of
 * those plugins are handled through the DependencyInitialiser in this instnace.
 *
 * @param {Object} config Configuration object
 * @private
 * @returns {void}
 */
Deepstream.prototype._loadConfig = function (config) {
  if (config === null || typeof config === 'string') {
    const result = jsYamlLoader.loadConfig(config)
    this._configFile = result.file
    config = result.config // eslint-disable-line
  } else {
    const rawConfig = utils.merge(defaultOptions.get(), config)
    config = configInitialiser.initialise(rawConfig) // eslint-disable-line
  }
  this._options = config
}

/**
 * Callback for the final stop event
 *
 * @private
 * @returns {void}
 */
Deepstream.prototype._onStopped = function () {
  this._currentState = STATES.CLOSED
  this.emit('stopped')
}

/**
 * Shows a giant ASCII art logo which is absolutely crucial
 * for the proper functioning of the server
 *
 * @private
 * @returns {void}
 */
Deepstream.prototype._showStartLogo = function () {
  if (this._options.showLogo !== true) {
    return
  }
  /* istanbul ignore next */
  let logo

  try {
    const nexeres = require('nexeres') // eslint-disable-line
    logo = nexeres.get('ascii-logo.txt').toString('ascii')
  } catch (e) {
    logo = fs.readFileSync(path.join(__dirname, '..', '/ascii-logo.txt'), 'utf8')
  }

  /* istanbul ignore next */
  process.stdout.write(logo + EOL)
  process.stdout.write(` =========================   starting   ==========================${EOL}`)
}

/**
 * Invoked once all dependencies are initialised. Instantiates the messaging pipeline and
 * the various handlers.
 * The startup sequence will be complete once the connection endpoint is started and listening
 *
 * @private
 * @returns {void}
 */
Deepstream.prototype._init = function () {
  this._connectionEndpoint = new ConnectionEndpoint(this._options, this._onStarted.bind(this))
  this._messageProcessor = new MessageProcessor(this._options)
  this._messageDistributor = new MessageDistributor(this._options)
  this._connectionEndpoint.onMessage = this._messageProcessor.process.bind(this._messageProcessor)

  this._options.clusterRegistry = new ClusterRegistry(this._options, this._connectionEndpoint)
  this._options.uniqueRegistry = new UniqueRegistry(this._options, this._options.clusterRegistry)

  this._eventHandler = new EventHandler(this._options)
  this._messageDistributor.registerForTopic(
    C.TOPIC.EVENT,
    this._eventHandler.handle.bind(this._eventHandler)
  )

  this._rpcHandler = new RpcHandler(this._options)
  this._messageDistributor.registerForTopic(
    C.TOPIC.RPC,
    this._rpcHandler.handle.bind(this._rpcHandler)
  )

  this._recordHandler = new RecordHandler(this._options)
  this._messageDistributor.registerForTopic(
    C.TOPIC.RECORD,
    this._recordHandler.handle.bind(this._recordHandler)
  )

  this._options.connectionEndpoint = this._connectionEndpoint
  this._presenceHandler = new PresenceHandler(this._options)
  this._messageDistributor.registerForTopic(
    C.TOPIC.PRESENCE,
    this._presenceHandler.handle.bind(this._presenceHandler)
  )

  this._messageProcessor.onAuthenticatedMessage =
      this._messageDistributor.distribute.bind(this._messageDistributor)

  if (this._options.permissionHandler.setRecordHandler) {
    this._options.permissionHandler.setRecordHandler(this._recordHandler)
  }

  this._currentState = STATES.INITIALIZED
}

/**
 * Called whenever a dependency emits a ready event. Once all dependencies are ready
 * deepstream moves to the init step.
 *
 * @private
 * @returns {void}
 */
Deepstream.prototype._checkReady = function (pluginName, plugin) {
  if (plugin instanceof EventEmitter) {
    plugin.on('error', this._onPluginError.bind(this, pluginName))
  }

  for (let i = 0; i < this._plugins.length; i++) {
    if (this._options[this._plugins[i]].isReady !== true) {
      return
    }
  }

  if (this._currentState === STATES.STARTING) {
    this._init()
  }
}

/**
 * Final callback - Deepstream is up and running now
 *
 * @private
 * @returns {void}
 */
Deepstream.prototype._onStarted = function () {
  this._options.logger.log(C.LOG_LEVEL.INFO, C.EVENT.INFO, 'Deepstream started')
  this._currentState = STATES.IS_RUNNING
  this.emit('started')
}

/**
 * Callback for plugin errors that occur at runtime. Errors during initialisation
 * are handled by the DependencyInitialiser
 *
 * @param   {String} pluginName
 * @param   {Error} error
 *
 * @private
 * @returns {void}
 */
Deepstream.prototype._onPluginError = function (pluginName, error) {
  const msg = `Error from ${pluginName} plugin: ${error.toString()}`
  this._options.logger.log(C.LOG_LEVEL.ERROR, C.EVENT.PLUGIN_ERROR, msg)
}

module.exports = Deepstream

'use strict'

const C = require('../constants/constants')
const messageParser = require('./message-parser')
const messageBuilder = require('./message-builder')
const SocketWrapper = require('./socket-wrapper')
const events = require('events')
const http = require('http')
const https = require('https')
const uws = require('uws')

const OPEN = 'OPEN'

/**
 * This is the frontmost class of deepstream's message pipeline. It receives
 * connections and authentication requests, authenticates sockets and
 * forwards messages it receives from authenticated sockets.
 *
 * @constructor
 *
 * @extends events.EventEmitter
 *
 * @param {Object} options the extended default options
 * @param {Function} readyCallback will be invoked once both the ws is ready
 */
module.exports = class ConnectionEndpoint extends events.EventEmitter {
  constructor (options, readyCallback) {
    super()
    this._options = options
    this._readyCallback = readyCallback

    this._wsReady = false
    this._wsServerClosed = false

    this._server = this._createHttpServer()
    this._server.listen(this._options.port, this._options.host)
    this._server.on('request', this._handleHealthCheck.bind(this))
    this._options.logger.log(
      C.LOG_LEVEL.INFO,
      C.EVENT.INFO,
      `Listening for health checks on path ${options.healthCheckPath}`
    )

    this._ws = new uws.Server({
      server: this._server,
      perMessageDeflate: false,
      path: this._options.urlPath
    })
    this._ws.startAutoPing(
      this._options.heartbeatInterval,
      messageBuilder.getMsg(C.TOPIC.CONNECTION, C.ACTIONS.PING)
    )
    this._server.once('listening', this._checkReady.bind(this))
    this._ws.on('error', this._onError.bind(this))
    this._ws.on('connection', this._onConnection.bind(this))

    this._authenticatedSockets = []
  }

  /**
   * Called for every message that's received
   * from an authenticated socket
   *
   * This method will be overridden by an external class and is used instead
   * of an event emitter to improve the performance of the messaging pipeline
   *
   * @param   {SocketWrapper} socketWrapper
   * @param   {String} message the raw message as sent by the client
   *
   * @public
   *
   * @returns {void}
   */
  onMessage (socketWrapper, message) { // eslint-disable-line
  }

  /**
   * Closes the ws server connection. The ConnectionEndpoint
   * will emit a close event once succesfully shut down
   * @public
   * @returns {void}
   */
  close () {
    this._server.removeAllListeners('request')
    this._ws.removeAllListeners('connection')
    this._ws.close()

    this._server.close(() => {
      this._wsServerClosed = true
      this._checkClosed()
    })
  }

  /**
   * Returns the number of currently connected clients. This is used by the
   * cluster module to determine loadbalancing endpoints
   *
   * @public
   * @returns {Number} connectionCount
   */
  getConnectionCount () {
    return this._authenticatedSockets.length
  }

  /**
   * Creates an HTTP or HTTPS server for ws to attach itself to,
   * depending on the options the client configured
   *
   * @private
   * @returns {http.HttpServer | http.HttpsServer}
   */
  _createHttpServer () {
    if (this._isHttpsServer()) {
      const httpsOptions = {
        key: this._options.sslKey,
        cert: this._options.sslCert
      }

      if (this._options.sslCa) {
        httpsOptions.ca = this._options.sslCa
      }

      return https.createServer(httpsOptions)
    }

    return http.createServer()
  }

  /**
   * Responds to http health checks.
   * Responds with 200(OK) if deepstream is alive.
   *
   * @private
   * @returns {void}
   */
  _handleHealthCheck (req, res) {
    if (req.method === 'GET' && req.url === this._options.healthCheckPath) {
      res.writeHead(200)
      res.end()
    }
  }

  /**
   * Called whenever either the server itself or one of its sockets
   * is closed. Once everything is closed it will emit a close event
   *
   * @private
   * @returns {void}
   */
  _checkClosed () {
    if (this._wsServerClosed === false) {
      return
    }

    this.emit('close')
  }

  /**
   * Callback for 'connection' event. Receives
   * a connected socket, wraps it in a SocketWrapper, sends a connection ack to the user and
  * subscribes to authentication messages.
   * @param {Websocket} socket
   *
   * @private
   * @returns {void}
   */
  _onConnection (socket) {
    const socketWrapper = new SocketWrapper(socket, this._options)
    const handshakeData = socketWrapper.getHandshakeData()
    const logMsg = `from ${handshakeData.referer} (${handshakeData.remoteAddress})`
    let disconnectTimer

    this._options.logger.log(C.LOG_LEVEL.INFO, C.EVENT.INCOMING_CONNECTION, logMsg)

    if (this._options.unauthenticatedClientTimeout !== null) {
      disconnectTimer = setTimeout(
        this._processConnectionTimeout.bind(this, socketWrapper),
        this._options.unauthenticatedClientTimeout
      )
      socketWrapper.once('close', clearTimeout.bind(null, disconnectTimer))
    }

    socketWrapper.connectionCallback = this._processConnectionMessage.bind(this, socketWrapper)
    socketWrapper.authCallBack = this._authenticateConnection.bind(
      this,
      socketWrapper,
      disconnectTimer
    )
    socketWrapper.sendMessage(C.TOPIC.CONNECTION, C.ACTIONS.CHALLENGE)
    socket.on('message', socketWrapper.connectionCallback)
  }

  /**
   * Always challenges the client that connects. This will be opened up later to allow users
   * to put in their own challenge authentication, but requires more work on the clustering
   * aspect first.
   *
   * @param  {SocketWrapper} socketWrapper Socket
   * @param  {Message} connectionMessage Message recieved from server
   *
   * @private
   * @returns {void}
   */
  _processConnectionMessage (socketWrapper, connectionMessage) {
    if (typeof connectionMessage !== 'string') {
      this._options.logger.log(
        C.LOG_LEVEL.WARN,
        C.EVENT.INVALID_MESSAGE,
        connectionMessage.toString()
      )
      socketWrapper.sendError(
        C.TOPIC.CONNECTION,
        C.EVENT.INVALID_MESSAGE,
        'invalid connection message'
      )
      return
    }

    const msg = messageParser.parse(connectionMessage)[0]

    if (msg === null || msg === undefined) {
      this._options.logger.log(C.LOG_LEVEL.WARN, C.EVENT.MESSAGE_PARSE_ERROR, connectionMessage)
      socketWrapper.sendError(C.TOPIC.CONNECTION, C.EVENT.MESSAGE_PARSE_ERROR, connectionMessage)
      socketWrapper.destroy()
    } else if (msg.topic !== C.TOPIC.CONNECTION) {
      this._options.logger.log(C.LOG_LEVEL.WARN, C.EVENT.INVALID_MESSAGE, `invalid connection message ${connectionMessage}`)
      socketWrapper.sendError(C.TOPIC.CONNECTION, C.EVENT.INVALID_MESSAGE, 'invalid connection message')
    } else if (msg.action === C.ACTIONS.PONG) {
      // do nothing
    } else if (msg.action === C.ACTIONS.CHALLENGE_RESPONSE) {
      socketWrapper.socket.removeListener('message', socketWrapper.connectionCallback)
      socketWrapper.socket.on('message', socketWrapper.authCallBack)
      socketWrapper.sendMessage(C.TOPIC.CONNECTION, C.ACTIONS.ACK)
    } else {
      this._options.logger.log(C.LOG_LEVEL.WARN, C.EVENT.UNKNOWN_ACTION, msg.action)
      socketWrapper.sendError(C.TOPIC.CONNECTION, C.EVENT.UNKNOWN_ACTION, `unknown action ${msg.action}`)
    }
  }

  /**
   * Callback for the first message that's received from the socket.
   * This is expected to be an auth-message. This method makes sure that's
   * the case and - if so - forwards it to the permission handler for authentication
   *
   * @param   {SocketWrapper} socketWrapper
   * @param   {Timeout} disconnectTimeout
   * @param   {String} authMsg
   *
   * @private
   *
   * @returns {void}
   */
  _authenticateConnection (socketWrapper, disconnectTimeout, authMsg) {
    if (typeof authMsg !== 'string') {
      this._options.logger.log(
        C.LOG_LEVEL.WARN,
        C.EVENT.INVALID_AUTH_MSG,
        authMsg.toString()
      )
      socketWrapper.sendError(
        C.TOPIC.AUTH,
        C.EVENT.INVALID_AUTH_MSG,
        'invalid authentication message'
      )
      return
    }

    const msg = messageParser.parse(authMsg)[0]
    let authData
    let errorMsg

    /**
     * Ignore pong messages
     */
    if (msg && msg.topic === C.TOPIC.CONNECTION && msg.action === C.ACTIONS.PONG) {
      return
    }

    /**
     * Log the authentication attempt
     */
    const logMsg = `${socketWrapper.getHandshakeData().remoteAddress}: ${authMsg}`
    this._options.logger.log(C.LOG_LEVEL.DEBUG, C.EVENT.AUTH_ATTEMPT, logMsg)

    /**
     * Ensure the message is a valid authentication message
     */
    if (!msg ||
        msg.topic !== C.TOPIC.AUTH ||
        msg.action !== C.ACTIONS.REQUEST ||
        msg.data.length !== 1
      ) {
      errorMsg = this._options.logInvalidAuthData === true ? authMsg : ''
      this._sendInvalidAuthMsg(socketWrapper, errorMsg)
      return
    }

    /**
     * Ensure the authentication data is valid JSON
     */
    try {
      authData = this._getValidAuthData(msg.data[0])
    } catch (e) {
      errorMsg = 'Error parsing auth message'

      if (this._options.logInvalidAuthData === true) {
        errorMsg += ` "${authMsg}": ${e.toString()}`
      }

      this._sendInvalidAuthMsg(socketWrapper, errorMsg)
      return
    }

    /**
     * Forward for authentication
     */
    this._options.authenticationHandler.isValidUser(
      socketWrapper.getHandshakeData(),
      authData,
      this._processAuthResult.bind(this, authData, socketWrapper, disconnectTimeout)
    )
  }

  /**
   * Will be called for syntactically incorrect auth messages. Logs
   * the message, sends an error to the client and closes the socket
   *
   * @param   {SocketWrapper} socketWrapper
   * @param   {String} msg the raw message as sent by the client
   *
   * @private
   *
   * @returns {void}
   */
  _sendInvalidAuthMsg (socketWrapper, msg) {
    this._options.logger.log(C.LOG_LEVEL.WARN, C.EVENT.INVALID_AUTH_MSG, this._options.logInvalidAuthData ? msg : '')
    socketWrapper.sendError(C.TOPIC.AUTH, C.EVENT.INVALID_AUTH_MSG, 'invalid authentication message')
    socketWrapper.destroy()
  }

  /**
   * Callback for succesfully validated sockets. Removes
   * all authentication specific logic and registeres the
   * socket with the authenticated sockets
   *
   * @param   {SocketWrapper} socketWrapper
   * @param   {String} username
   *
   * @private
   *
   * @returns {void}
   */
  _registerAuthenticatedSocket (socketWrapper, userData) {
    socketWrapper.socket.removeListener('message', socketWrapper.authCallBack)
    socketWrapper.once('close', this._onSocketClose.bind(this, socketWrapper))
    socketWrapper.socket.on('message', (msg) => { this.onMessage(socketWrapper, msg) })
    this._appendDataToSocketWrapper(socketWrapper, userData)
    if (typeof userData.clientData === 'undefined') {
      socketWrapper.sendMessage(C.TOPIC.AUTH, C.ACTIONS.ACK)
    } else {
      socketWrapper.sendMessage(
        C.TOPIC.AUTH,
        C.ACTIONS.ACK,
        [messageBuilder.typed(userData.clientData)]
      )
    }

    if (socketWrapper.user !== OPEN) {
      this.emit('client-connected', socketWrapper)
    }

    this._authenticatedSockets.push(socketWrapper)
    this._options.logger.log(C.LOG_LEVEL.INFO, C.EVENT.AUTH_SUCCESSFUL, socketWrapper.user)
  }

  /**
   * Append connection data to the socket wrapper
   *
   * @param   {SocketWrapper} socketWrapper
   * @param   {Object} userData the data to append to the socket wrapper
   *
   * @private
   *
   * @returns {void}
   */
  _appendDataToSocketWrapper (socketWrapper, userData) { // eslint-disable-line
    socketWrapper.user = userData.username || OPEN
    socketWrapper.authData = userData.serverData || null
  }

  /**
   * Callback for invalid credentials. Will notify the client
   * of the invalid auth attempt. If the number of invalid attempts
   * exceed the threshold specified in options.maxAuthAttempts
   * the client will be notified and the socket destroyed.
   *
   * @param   {Object} authData the (invalid) auth data
   * @param   {SocketWrapper} socketWrapper
   *
   * @private
   *
   * @returns {void}
   */
  _processInvalidAuth (clientData, authData, socketWrapper) {
    let logMsg = 'invalid authentication data'

    if (this._options.logInvalidAuthData === true) {
      logMsg += `: ${JSON.stringify(authData)}`
    }

    this._options.logger.log(C.LOG_LEVEL.INFO, C.EVENT.INVALID_AUTH_DATA, logMsg)
    socketWrapper.sendError(
      C.TOPIC.AUTH,
      C.EVENT.INVALID_AUTH_DATA,
      messageBuilder.typed(clientData)
    )
    socketWrapper.authAttempts++

    if (socketWrapper.authAttempts >= this._options.maxAuthAttempts) {
      this._options.logger.log(C.LOG_LEVEL.INFO, C.EVENT.TOO_MANY_AUTH_ATTEMPTS, 'too many authentication attempts')
      socketWrapper.sendError(C.TOPIC.AUTH, C.EVENT.TOO_MANY_AUTH_ATTEMPTS, messageBuilder.typed('too many authentication attempts'))
      socketWrapper.destroy()
    }
  }

  /**
   * Callback for connections that have not authenticated succesfully within
   * the expected timeframe
   *
   * @param   {SocketWrapper} socketWrapper
   *
   * @private
   *
   * @returns {void}
   */
  _processConnectionTimeout (socketWrapper) {
    const log = 'connection has not authenticated successfully in the expected time'
    this._options.logger.log(C.LOG_LEVEL.INFO, C.EVENT.CONNECTION_AUTHENTICATION_TIMEOUT, log)
    socketWrapper.sendError(
      C.TOPIC.CONNECTION,
      C.EVENT.CONNECTION_AUTHENTICATION_TIMEOUT,
      messageBuilder.typed(log)
    )
    socketWrapper.destroy()
  }

  /**
   * Callback for the results returned by the permissionHandler
   *
   * @param   {Object} authData
   * @param   {SocketWrapper} socketWrapper
   * @param   {Boolean} isAllowed
   * @param   {Object} userData
   *
   * @private
   *
   * @returns {void}
   */
  _processAuthResult (authData, socketWrapper, disconnectTimeout, isAllowed, userData) {
    userData = userData || {} // eslint-disable-line
    clearTimeout(disconnectTimeout)

    if (isAllowed === true) {
      this._registerAuthenticatedSocket(socketWrapper, userData)
    } else {
      this._processInvalidAuth(userData.clientData, authData, socketWrapper)// todo
    }
  }

  /**
   * Called for the ready event of the ws server.
   *
   * @private
   * @returns {void}
   */
  _checkReady () {
    const address = this._server.address()
    const msg = `Listening for websocket connections on ${address.address}:${address.port}${this._options.urlPath}`
    this._wsReady = true

    this._options.logger.log(C.LOG_LEVEL.INFO, C.EVENT.INFO, msg)
    this._readyCallback()
  }

  /**
   * Generic callback for connection errors. This will most often be called
   * if the configured port number isn't available
   *
   * @param   {String} error
   *
   * @private
   * @returns {void}
   */
  _onError (error) {
    this._options.logger.log(C.LOG_LEVEL.ERROR, C.EVENT.CONNECTION_ERROR, error.toString())
  }

  /**
  * Notifies the (optional) onClientDisconnect method of the permissionHandler
  * that the specified client has disconnected
  *
  * @param {SocketWrapper} socketWrapper
  *
  * @private
  * @returns {void}
  */
  _onSocketClose (socketWrapper) {
    if (this._options.authenticationHandler.onClientDisconnect) {
      this._options.authenticationHandler.onClientDisconnect(socketWrapper.user)
    }

    if (socketWrapper.user !== OPEN) {
      this.emit('client-disconnected', socketWrapper)
    }
  }

  /**
  * Returns whether or not sslKey and sslCert have been set to start a https server.
  *
  * @throws Will throw an error if only sslKey or sslCert have been specified
  *
  * @private
  * @returns {boolean}
  */
  _isHttpsServer () {
    let isHttps = false
    if (this._options.sslKey || this._options.sslCert) {
      if (!this._options.sslKey) {
        throw new Error('Must also include sslKey in order to use HTTPS')
      }
      if (!this._options.sslCert) {
        throw new Error('Must also include sslCert in order to use HTTPS')
      }
      isHttps = true
    }
    return isHttps
  }

  /**
  * Checks for authentication data and throws if null or not well formed
  *
  * @throws Will throw an error on invalid auth data
  *
  * @private
  * @returns {void}
  */
  _getValidAuthData (authData) { // eslint-disable-line
    const parsedData = JSON.parse(authData)
    if (parsedData === null || parsedData === undefined || typeof parsedData !== 'object') {
      throw new Error(`invalid authentication data ${authData}`)
    }
    return parsedData
  }
}

'use strict'

const C = require('../constants/constants')
const utils = require('../utils/utils')

/**
 * Turns the ACTION:SHORTCODE constants map
 * around to facilitate shortcode lookup
 *
 * @private
 *
 * @returns {Object} actions
*/
const actions = new Set(Object.keys(C.ACTIONS).map(key => C.ACTIONS[key]))

/**
 * Parses ASCII control character seperated
 * message strings into digestable maps
 *
 * @constructor
 */
module.exports = class MessageParser {
  /**
   * Main interface method. Receives a raw message
   * string, containing one or more messages
   * and returns an array of parsed message objects
   * or null for invalid messages
   *
   * @param   {String} message raw message
   *
   * @public
   *
   * @returns {Array} array of parsed message objects
   *                  following the format
   *                  {
   *                    raw: <original message string>
   *                    topic: <string>
   *                    action: <string - shortcode>
   *                    data: <array of strings>
   *                  }
   */
  static parse (message) {
    const parsedMessages = []
    const rawMessages = message.split(C.MESSAGE_SEPERATOR)

    for (let i = 0; i < rawMessages.length; i++) {
      if (rawMessages[i].length < 3) {
        continue
      }

      const parts = rawMessages[i].split(C.MESSAGE_PART_SEPERATOR)
      parsedMessages.push(parts.length < 2 || !actions.has(parts[1]) ? null : {
        raw: rawMessages[i],
        topic: parts[0],
        action: parts[1],
        data: parts.splice(2)
      })
    }

    return parsedMessages
  }

  /**
   * Deserializes values created by MessageBuilder.typed to
   * their original format
   *
   * @param {String} value
   *
   * @public
   * @returns {Mixed} original value
   */
  static convertTyped (value) {
    const type = value.charAt(0)

    if (type === C.TYPES.STRING) {
      return value.substr(1)
    }

    if (type === C.TYPES.OBJECT) {
      const result = utils.parseJSON(value.substr(1))
      if (result.value) {
        return result.value
      }
      return result.error
    }

    if (type === C.TYPES.NUMBER) {
      return parseFloat(value.substr(1))
    }

    if (type === C.TYPES.NULL) {
      return null
    }

    if (type === C.TYPES.TRUE) {
      return true
    }

    if (type === C.TYPES.FALSE) {
      return false
    }

    if (type === C.TYPES.UNDEFINED) {
      return undefined
    }

    return new Error('Unknown type')
  }
}

'use strict'
/* eslint-disable valid-typeof */
const url = require('url')

const OBJECT = 'object'

/**
 * Returns a unique identifier
 *
 * @returns {String} uid
 */
exports.getUid = function () {
  return `${Date.now().toString(36)}-${(Math.random() * 10000000000000000000).toString(36)}`
}

/**
 * Calls <callback> once all <emitters> have emitted <event>
 *
 * @param {Array} emitters Array of objects extending events.EventEmitter
 * @param {String} event
 * @param {Function} callback Will be called once every emitter has emitted the event
 *
 * @public
 * @returns {void}
 */
exports.combineEvents = function (emitters, event, callback) {
  let i
  let count = 0
  const increment = function () {
    count++

    if (count === emitters.length) {
      callback()
    }
  }

  for (i = 0; i < emitters.length; i++) {
    emitters[i].once(event, increment)
  }
}

/**
 * Takes a key-value map and returns
 * a map with { value: key } of the old map
 *
 * @param  {Object} map
 *
 * @public
 * @return {Object} reversed map
 */
exports.reverseMap = function (map) {
  const reversedMap = {}

  for (const key in map) {
    reversedMap[map[key]] = key
  }

  return reversedMap
}

/**
 * Extended version of the typeof operator. Also supports 'array'
 * and 'url' to check for valid URL schemas
 *
 * @param   {Mixed}   input
 * @param   {String}  expectedType
 *
 * @public
 * @returns {Boolean}
 */
exports.isOfType = function (input, expectedType) {
  if (input === null) {
    return expectedType === 'null'
  } else if (expectedType === 'array') {
    return Array.isArray(input)
  } else if (expectedType === 'url') {
    return !!url.parse(input).host
  }
  return typeof input === expectedType
}

/**
 * Takes a map and validates it against a basic
 * json schema in the form { key: type }
 *
 * @param   {Object}  map        the map to validate
 * @param   {Boolean} throwError if true, errors will be thrown rather than returned
 * @param   {Object}  schema     json schema in the form { key: type }
 *
 * @public
 * @returns {Boolean|Error}
 */
exports.validateMap = function (map, throwError, schema) {
  let error
  let key

  for (key in schema) {
    if (typeof map[key] === 'undefined') {
      error = new Error(`Missing key ${key}`)
      break
    }

    if (!exports.isOfType(map[key], schema[key])) {
      error = new Error(`Invalid type ${typeof map[key]} for ${key}`)
      break
    }
  }

  if (error) {
    if (throwError) {
      throw error
    } else {
      return error
    }
  } else {
    return true
  }
}

/**
 * Tests have shown that JSON stringify outperforms any attempt of
 * a code based implementation by 50% - 100% whilst also handling edge-cases and keeping
 * implementation complexity low.
 *
 * If ES6/7 ever decides to implement deep copying natively (what happened to Object.clone?
 * that was briefly a thing...), let's switch it for the native implementation. For now though,
 * even Object.assign({}, obj) only provides a shallow copy.
 *
 * Please find performance test results backing these statements here:
 *
 * http://jsperf.com/object-deep-copy-assign
 *
 * @param   {Mixed} obj the object that should be cloned
 *
 * @public
 * @returns {Mixed} clone
 */
exports.deepCopy = function (obj) {
  if (typeof obj === OBJECT) {
    return JSON.parse(JSON.stringify(obj))
  }
  return obj
}

/**
 * Multi Object recoursive merge
 *
 * @param {Object} multiple objects to be merged into each other recoursively
 *
 * @public
 * @returns {Object} merged result
 */
exports.merge = function () {
  const result = {}
  const objs = Array.prototype.slice.apply(arguments) // eslint-disable-line
  let i

  const _merge = (objA, objB) => {
    let key

    for (key in objB) {
      if (objB[key] && objB[key].constructor === Object) {
        objA[key] = objA[key] || {}
        _merge(objA[key], objB[key])
      } else if (objB[key] !== undefined) {
        objA[key] = objB[key]
      }
    }
  }

  for (i = 0; i < objs.length; i++) {
    _merge(result, objs[i])
  }

  return result
}

/**
 * Set timeout utility that adds support for disabling a timeout
 * by passing null
 *
 * @param {Function} callback        the function that will be called after the given time
 * @param {Number}   timeoutDuration the duration of the timeout in milliseconds
 *
 * @public
 * @returns {Number} timeoutId
 */
exports.setTimeout = function (callback, timeoutDuration) {
  if (timeoutDuration !== null) {
    return setTimeout(callback, timeoutDuration)
  }
  return -1
}

/**
 * Set Interval utility that adds support for disabling an interval
 * by passing null
 *
 * @param {Function} callback        the function that will be called after the given time
 * @param {Number}   intervalDuration the duration of the interval in milliseconds
 *
 * @public
 * @returns {Number} intervalId
 */
exports.setInterval = function (callback, intervalDuration) {
  if (intervalDuration !== null) {
    return setInterval(callback, intervalDuration)
  }
  return -1
}

exports.getRandomIntInRange = function (min, max) {
  return min + Math.floor(Math.random() * (max - min))
}

exports.spliceRandomElement = function (array) {
  const randomIndex = exports.getRandomIntInRange(0, array.length)
  return array.splice(randomIndex, 1)[0]
}

/**
 * Randomize array element order in-place.
 * Using Durstenfeld shuffle algorithm.
 *
 * @param  {Array} array The array to shuffle
 */
exports.shuffleArray = function (array) {
  for (let i = array.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1))
    const temp = array[i]
    array[i] = array[j]
    array[j] = temp
  }
  return array
}

/**
 * This method tries to parse a value, and returns
 * an object containing the value or error.
 *
 * This is an optimization to avoid doing try/catch
 * inline since it incurs a massive performance hit
 * in most versions of node.
 */
exports.parseJSON = function (text, reviver) {
  try {
    return {
      value: JSON.parse(text, reviver)
    }
  } catch (err) {
    return {
      error: err
    }
  }
}

'use strict'

const C = require('../constants/constants')

const SEP = C.MESSAGE_PART_SEPERATOR

/**
 * Creates a deepstream message string, based on the
 * provided parameters
 *
 * @param   {String} topic  One of CONSTANTS.TOPIC
 * @param   {String} action One of CONSTANTS.ACTIONS
 * @param   {Array} data An array of strings or JSON-serializable objects
 *
 * @returns {String} deepstream message string
 */
exports.getMsg = function (topic, action, data) {
  const sendData = [topic, action]

  if (data) {
    for (let i = 0; i < data.length; i++) {
      if (typeof data[i] === 'object') {
        sendData.push(JSON.stringify(data[i]))
      } else {
        sendData.push(data[i])
      }
    }
  }

  return sendData.join(SEP) + C.MESSAGE_SEPERATOR
}

/**
 * Creates a deepstream error message string based on the provided
 * arguments
 *
 * @param   {String} topic   One of CONSTANTS.TOPIC - error messages might either be send on
 *                           the generic ERROR topic or on the topic that caused the error
 *
 * @param   {String} type    One of CONSTANTS.EVENT
 * @param   {String | Array } message a message text or an array of data
 *
 * @returns {String } deepstream error message string
 */
exports.getErrorMsg = function (topic, type, message) {
  if (message instanceof Array) {
    return `${topic + SEP}E${SEP}${type}${SEP}${message.join(SEP)}${C.MESSAGE_SEPERATOR}`
  }

  return `${topic + SEP}E${SEP}${type}${SEP}${message}${C.MESSAGE_SEPERATOR}`
}

/**
 * Converts a serializable value into its string-representation and adds
 * a flag that provides instructions on how to deserialize it.
 *
 * Please see messageParser.convertTyped for the counterpart of this method
 *
 * @param {Mixed} value
 *
 * @public
 * @returns {String} string representation of the value
 */
exports.typed = function (value) {
  const type = typeof value

  if (type === 'string') {
    return C.TYPES.STRING + value
  }

  if (value === null) {
    return C.TYPES.NULL
  }

  if (type === 'object') {
    return C.TYPES.OBJECT + JSON.stringify(value)
  }

  if (type === 'number') {
    return C.TYPES.NUMBER + value.toString()
  }

  if (value === true) {
    return C.TYPES.TRUE
  }

  if (value === false) {
    return C.TYPES.FALSE
  }

  if (value === undefined) {
    return C.TYPES.UNDEFINED
  }

  throw new Error(`Can't serialize type ${value}`)
}

