const C = require('../constants/constants')
const messageBuilder = require('./message-builder')
const utils = require('util')
const uws = require('uws')

const EventEmitter = require('events').EventEmitter

/**
 * This class wraps around a websocket
 * and provides higher level methods that are integrated
 * with deepstream's message structure
 *
 * @param {WebSocket} socket
 * @param {Object} options
 *
 * @extends EventEmitter
 *
 * @constructor
 */
const SocketWrapper = function (socket, options) {
  this.socket = socket
  this.isClosed = false
  this.socket.once('close', this._onSocketClose.bind(this))
  this._options = options
  this.user = null
  this.authCallBack = null
  this.authAttempts = 0
  this.setMaxListeners(0)
  this.uuid = Math.random()
  this._handshakeData = null
  this._setUpHandshakeData()

  this._queuedMessages = []
  this._currentPacketMessageCount = 0
  this._sendNextPacketTimeout = null
  this._currentMessageResetTimeout = null
}

utils.inherits(SocketWrapper, EventEmitter)
SocketWrapper.lastPreparedMessage = null

/**
 * Updates lastPreparedMessage and returns the [uws] prepared message.
 *
 * @param {String} message the message to be prepared
 *
 * @public
 * @returns {External} prepared message
 */
SocketWrapper.prepareMessage = function (message) {
  SocketWrapper.lastPreparedMessage = message
  return uws.native.server.prepareMessage(message, uws.OPCODE_TEXT)
}

/**
 * Sends the [uws] prepared message, or in case of testing sends the
 * last prepared message.
 *
 * @param {External} preparedMessage the prepared message
 *
 * @public
 * @returns {void}
 */
SocketWrapper.prototype.sendPrepared = function (preparedMessage) {
  if (this.socket.external) {
    uws.native.server.sendPrepared(this.socket.external, preparedMessage)
  } else if (this.socket.external !== null) {
    this.socket.send(SocketWrapper.lastPreparedMessage)
  }
}

/**
 * Variant of send with no particular checks or appends of message.
 *
 * @param {String} message the message to send
 *
 * @public
 * @returns {void}
 */
SocketWrapper.prototype.sendNative = function (message) {
  this.socket.send(message)
}

/**
 * Finalizes the [uws] perpared message.
 *
 * @param {External} preparedMessage the prepared message to finalize
 *
 * @public
 * @returns {void}
 */
SocketWrapper.finalizeMessage = function (preparedMessage) {
  uws.native.server.finalizeMessage(preparedMessage)
}

/**
 * Returns a map of parameters that were collected
 * during the initial http request that established the
 * connection
 *
 * @public
 * @returns {Object} handshakeData
 */
SocketWrapper.prototype.getHandshakeData = function () {
  return this._handshakeData
}

/**
 * Sends an error on the specified topic. The
 * action will automatically be set to C.ACTION.ERROR
 *
 * @param {String} topic one of C.TOPIC
 * @param {String} type one of C.EVENT
 * @param {String} msg generic error message
 *
 * @public
 * @returns {void}
 */
SocketWrapper.prototype.sendError = function (topic, type, msg) {
  if (this.isClosed === false) {
    this.send(messageBuilder.getErrorMsg(topic, type, msg))
  }
}

/**
 * Sends a message based on the provided action and topic
 *
 * @param {String} topic one of C.TOPIC
 * @param {String} action one of C.ACTIONS
 * @param {Array} data Array of strings or JSON-serializable objects
 *
 * @public
 * @returns {void}
 */
SocketWrapper.prototype.sendMessage = function (topic, action, data) {
  if (this.isClosed === false) {
    this.send(messageBuilder.getMsg(topic, action, data))
  }
}

/**
 * Checks the passed message and appends missing end separator if
 * needed, and then sends this message immediately.
 *
 * @param   {String} message deepstream message
 *
 * @public
 * @returns {void}
 */
SocketWrapper.prototype.send = function (message) {
  if (message.charAt(message.length - 1) !== C.MESSAGE_SEPERATOR) {
    message += C.MESSAGE_SEPERATOR // eslint-disable-line
  }

  if (this.isClosed === true) {
    return
  }

  this.socket.send(message)
}

/**
 * Destroyes the socket. Removes all deepstream specific
 * logic and closes the connection
 *
 * @public
 * @returns {void}
 */
SocketWrapper.prototype.destroy = function () {
  this.socket.close()
  this.authCallBack = null
}

/**
 * Callback for closed sockets
 *
 * @private
 * @returns {void}
 */
SocketWrapper.prototype._onSocketClose = function () {
  this.isClosed = true
  this.emit('close', this)
  this._options.logger.log(C.LOG_LEVEL.INFO, C.EVENT.CLIENT_DISCONNECTED, this.user)
  this.socket.removeAllListeners()
}

/**
 * Initialise the handshake data from the initial connection
 *
 * @private
 * @returns void
 */
SocketWrapper.prototype._setUpHandshakeData = function () {
  this._handshakeData = {
    remoteAddress: this.socket._socket.remoteAddress
  }

  if (this.socket.upgradeReq) {
    this._handshakeData.headers = this.socket.upgradeReq.headers
    this._handshakeData.referer = this.socket.upgradeReq.headers.referer
  }
  return this._handshakeData
}

module.exports = SocketWrapper

'use strict'

const messageParser = require('./message-parser')
const C = require('../constants/constants')

/**
 * The MessageProcessor consumes blocks of parsed messages emitted by the
 * ConnectionEndpoint, checks if they are permissioned and - if they
 * are - forwards them.
 *
 * @constructor
 *
 * @param {Object} options deepstream options
 */
module.exports = class MessageProcessor {
  constructor (options) {
    this._options = options
  }

  /**
   * There will only ever be one consumer of forwarded messages. So rather than using
   * events - and their performance overhead - the messageProcessor exposes
   * this method that's expected to be overwritten.
   *
   * @param   {SocketWrapper} socketWrapper
   * @param   {Object} message the parsed message
   *
   * @overwrite
   *
   * @returns {void}
   */
  onAuthenticatedMessage (socketWrapper, message) { // eslint-disable-line
  }

  /**
   * This method is the way the message processor accepts input. It receives arrays
   * of parsed messages, iterates through them and issues permission requests for
   * each individual message
   *
   * @todo The responses from the permissionHandler might arive in any arbitrary order - order them
   * @todo Handle permission handler timeouts
   *
   * @param   {SocketWrapper} socketWrapper
   * @param   {Object} message parsed message
   *
   * @returns {void}
   */
  process (socketWrapper, message) {
    if (typeof message !== 'string') {
      this._options.logger.log(
        C.LOG_LEVEL.WARN,
        C.EVENT.INVALID_MESSAGE,
        'non text based message recieved'
      )
      socketWrapper.sendError(
        C.TOPIC.ERROR,
        C.EVENT.MESSAGE_PARSE_ERROR,
        'non text based message recieved'
      )
      return
    }

    const parsedMessages = messageParser.parse(message)
    let parsedMessage

    const length = parsedMessages.length
    for (let i = 0; i < length; i++) {
      parsedMessage = parsedMessages[i]

      if (parsedMessage === null) {
        this._options.logger.log(C.LOG_LEVEL.WARN, C.EVENT.MESSAGE_PARSE_ERROR, message)
        socketWrapper.sendError(C.TOPIC.ERROR, C.EVENT.MESSAGE_PARSE_ERROR, message)
        continue
      }

      if (parsedMessage.topic === C.TOPIC.CONNECTION && parsedMessage.action === C.ACTIONS.PONG) {
        continue
      }

      this._options.permissionHandler.canPerformAction(
        socketWrapper.user,
        parsedMessage,
        this._onPermissionResponse.bind(this, socketWrapper, parsedMessage),
        socketWrapper.authData
      )
    }
  }

  /**
   * Processes the response that's returned by the permissionHandler.
   *
   * @param   {SocketWrapper}   socketWrapper
   * @param   {Object} message  parsed message - might have been manipulated
   *                              by the permissionHandler
   * @param   {Error} error     error or null if no error. Denied permissions will be expressed
   *                            by setting result to false
   * @param   {Boolean} result    true if permissioned
   *
   * @returns {void}
   */
  _onPermissionResponse (socketWrapper, message, error, result) {
    if (error !== null) {
      this._options.logger.log(C.LOG_LEVEL.WARN, C.EVENT.MESSAGE_PERMISSION_ERROR, error.toString())
      socketWrapper.sendError(
        message.topic,
        C.EVENT.MESSAGE_PERMISSION_ERROR,
        this._getPermissionErrorData(message)
      )
      return
    }

    if (result !== true) {
      socketWrapper.sendError(
        message.topic,
        C.EVENT.MESSAGE_DENIED,
        this._getPermissionErrorData(message)
      )
      return
    }

    this.onAuthenticatedMessage(socketWrapper, message)
  }

  /**
   * Create data in the correct format expected in a MESSAGE_DENIED or MESSAGE_PERMISSION_ERROR
   *
   * @param   {Object} message  parsed message - might have been manipulated
   *                              by the permissionHandler
   * @returns {Object}
   */
  _getPermissionErrorData (message) { // eslint-disable-line
    let data = [message.data[0], message.action]
    if (message.data.length > 1) {
      data = data.concat(message.data.slice(1))
    }
    return data
  }
}

'use strict'

const C = require('../constants/constants')

/**
 * The MessageDistributor routes valid and permissioned messages to
 * various, previously registered handlers, e.g. event-, rpc- or recordHandler
 *
 * @param {Object} options deepstream options
 */
module.exports = class MessageDistributor {
  constructor (options) {
    this._callbacks = {}
    this._options = options
  }

  /**
   * Accepts a socketWrapper and a parsed message as input and distributes
   * it to its subscriber, based on the message's topic
   *
   * @param   {SocketWrapper} socketWrapper
   * @param   {Object} message parsed and permissioned message
   *
   * @public
   * @returns {void}
   */
  distribute (socketWrapper, message) {
    if (this._callbacks[message.topic] === undefined) {
      this._options.logger.log(C.LOG_LEVEL.WARN, C.EVENT.UNKNOWN_TOPIC, message.topic)
      socketWrapper.sendError(C.TOPIC.ERROR, C.EVENT.UNKNOWN_TOPIC, message.topic)
      return
    }

    socketWrapper.emit(message.topic, message)

    if (message.isCompleted !== true) {
      this._callbacks[message.topic](socketWrapper, message)
    }
  }

  /**
   * Allows handlers (event, rpc, record) to register for topics. Subscribes them
   * to both messages passed to the distribute method as well as messages received
   * from the messageConnector
   *
   * @param   {String}   topic    One of C.TOPIC
   * @param   {Function} callback The method that should be called for every message
   *                              on the specified topic. Will be called with socketWrapper
   *                              and message
   *
   * @public
   * @returns {void}
   */
  registerForTopic (topic, callback) {
    if (this._callbacks[topic] !== undefined) {
      throw new Error(`Callback already registered for topic ${topic}`)
    }

    this._callbacks[topic] = callback
    this._options.messageConnector.subscribe(
      topic,
      this._onMessageConnectorMessage.bind(this, callback)
    )
  }

  /**
   * Whenever a message from the messageConnector is received it is passed
   * to the relevant handler, but with SOURCE_MESSAGE_CONNECTOR instead of
   * a socketWrapper as sender
   *
   * @param   {Function} callback the handler callback
   * @param   {Object}   message  the already parsed and validated message
   *
   * @private
   * @returns {void}
   */
  _onMessageConnectorMessage (callback, message) { // eslint-disable-line
    callback(C.SOURCE_MESSAGE_CONNECTOR, message)
  }
}

'use strict'
/* eslint-disable valid-typeof */
const C = require('../constants/constants')
const SubscriptionRegistry = require('../utils/subscription-registry')
const ListenerRegistry = require('../listen/listener-registry')
const messageBuilder = require('../message/message-builder')

/**
 * Handles incoming and outgoing messages for the EVENT topic.
 *
 * @param {Object} options deepstream options
 *
 * @constructor
 */
const EventHandler = function (options) {
  this._options = options
  this._subscriptionRegistry = new SubscriptionRegistry(options, C.TOPIC.EVENT)
  this._listenerRegistry = new ListenerRegistry(C.TOPIC.EVENT, options, this._subscriptionRegistry)
  this._subscriptionRegistry.setSubscriptionListener(this._listenerRegistry)
  this._logger = options.logger
  this._message = options.messageConnector
}

/**
 * The main distribution method. Routes messages to functions
 * based on the provided action parameter of the message
 *
 * @param {SocketWrapper} socket
 * @param {Object} message parsed and permissioned deepstream message
 *
 * @public
 * @returns {void}
 */
EventHandler.prototype.handle = function (socket, message) {
  if (message.action === C.ACTIONS.SUBSCRIBE) {
    this._addSubscriber(socket, message)
  } else if (message.action === C.ACTIONS.UNSUBSCRIBE) {
    this._removeSubscriber(socket, message)
  } else if (message.action === C.ACTIONS.EVENT) {
    this._triggerEvent(socket, message)
  } else if (message.action === C.ACTIONS.LISTEN ||
    message.action === C.ACTIONS.UNLISTEN ||
    message.action === C.ACTIONS.LISTEN_ACCEPT ||
    message.action === C.ACTIONS.LISTEN_REJECT) {
    this._listenerRegistry.handle(socket, message)
  } else {
    this._sendError(socket, C.EVENT.UNKNOWN_ACTION, `unknown action ${message.action}`)
  }
}

/**
 * Handler for the SUBSCRIBE action. Adds the socket as
 * a subscriber to the specified event name
 *
 * @param {SocketWrapper} socket
 * @param {Object} message parsed and permissioned deepstream message
 *
 * @private
 * @returns {void}
 */
EventHandler.prototype._addSubscriber = function (socket, message) {
  if (validateSubscriptionMessage(socket, message)) {
    this._subscriptionRegistry.subscribe(message.data[0], socket)
  }
}

/**
 * Handler for the UNSUBSCRIBE action. Removes the socket as
 * a subscriber from the specified event name
 *
 * @param {SocketWrapper} socket
 * @param {Object} message parsed and permissioned deepstream message
 *
 * @private
 * @returns {void}
 */
EventHandler.prototype._removeSubscriber = function (socket, message) {
  if (validateSubscriptionMessage(socket, message)) {
    this._subscriptionRegistry.unsubscribe(message.data[0], socket)
  }
}

/**
 * Notifies subscribers of events. This method is invoked for the EVENT action. It can
 * be triggered by messages coming in from both clients and the message connector.
 *
 * @param {String|SocketWrapper} socket If socket is the constant SOURCE_MESSAGE_CONNECTOR
 *                        the message was received from the message connector
 *
 * @param {Object} message parsed and permissioned deepstream message
 *
 * @private
 * @returns {void}
 */
EventHandler.prototype._triggerEvent = function (socket, message) {
  if (typeof message.data[0] !== 'string') {
    this._sendError(socket, C.EVENT.INVALID_MESSAGE_DATA, message.raw)
    return
  }

  this._logger.log(C.LOG_LEVEL.DEBUG, C.EVENT.TRIGGER_EVENT, message.raw)

  if (socket !== C.SOURCE_MESSAGE_CONNECTOR) {
    this._message.publish(C.TOPIC.EVENT, message)
  }

  this._subscriptionRegistry.sendToSubscribers(
    message.data[0],
    messageBuilder.getMsg(C.TOPIC.EVENT, C.ACTIONS.EVENT, message.data),
    false,
    socket
  )
}

EventHandler.prototype._sendError = function (socket, event, message) {
  if (socket && socket.sendError) {
    socket.sendError(C.TOPIC.EVENT, event, message)
  }
  this._logger.log(C.LOG_LEVEL.ERROR, event, message)
}

/**
 * Makes sure that subscription message contains the name of the event. Sends an error to the client
 * if not
 *
 * @param {SocketWrapper} socket
 * @param {Object} message parsed and permissioned deepstream message
 *
 * @private
 * @returns {Boolean} is valid subscription message
 */
function validateSubscriptionMessage (socket, message) {
  if (message.data && message.data.length === 1 && typeof message.data[0] === 'string') {
    return true
  }

  socket.sendError(C.TOPIC.EVENT, C.EVENT.INVALID_MESSAGE_DATA, message.raw)

  return false
}

module.exports = EventHandler

'use strict'

const C = require('../constants/constants')
const DistributedStateRegistry = require('../cluster/distributed-state-registry')
const SocketWrapper = require('../message/socket-wrapper')

class SubscriptionRegistry {
  /**
   * A generic mechanism to handle subscriptions from sockets to topics.
   * A bit like an event-hub, only that it registers SocketWrappers rather
   * than functions
   *
   * @constructor
   *
   * @param {Object} options deepstream options
   * @param {String} topic one of C.TOPIC
   * @param {[String]} clusterTopic A unique cluster topic, if not created uses format:
   *                                topic_SUBSCRIPTIONS
   */
  constructor (options, topic, clusterTopic) {
    this._delayedBroadcasts = new Map()
    this._delay = -1
    if (options.broadcastTimeout !== undefined) {
      this._delay = options.broadcastTimeout
    }
    this._names = new Map()
    this._subscriptions = new Map()
    this._options = options
    this._topic = topic
    this._subscriptionListener = null
    this._constants = {
      MULTIPLE_SUBSCRIPTIONS: C.EVENT.MULTIPLE_SUBSCRIPTIONS,
      SUBSCRIBE: C.ACTIONS.SUBSCRIBE,
      UNSUBSCRIBE: C.ACTIONS.UNSUBSCRIBE,
      NOT_SUBSCRIBED: C.EVENT.NOT_SUBSCRIBED
    }
    this._onBroadcastTimeout = this._onBroadcastTimeout.bind(this)
    this._onSocketClose = this._onSocketClose.bind(this)

    this._setupRemoteComponents(clusterTopic)
  }

  /**
   * Setup all the remote components and actions required to deal with the subscription
   * via the cluster.
   */
  _setupRemoteComponents (clusterTopic) {
    this._clusterSubscriptions = new DistributedStateRegistry(
      clusterTopic ||
      `${this._topic}_${C.TOPIC.SUBSCRIPTIONS}`,
      this._options
    )
    this._clusterSubscriptions.on('add', this._onClusterSubscriptionAdded.bind(this))
    this._clusterSubscriptions.on('remove', this._onClusterSubscriptionRemoved.bind(this))
  }

  /**
   * Return all the servers that have this subscription.
   *
   * @param  {String} subscriptionName the subscriptionName to look for
   *
   * @public
   * @return {Array}  An array of all the servernames with this subscription
   */
  getAllServers (subscriptionName) {
    return this._clusterSubscriptions.getAllServers(subscriptionName)
  }

  /**
   * Return all the servers that have this subscription excluding the current
   * server name
   *
   * @param  {String} subscriptionName the subscriptionName to look for
   *
   * @public
   * @return {Array}  An array of all the servernames with this subscription
   */
  getAllRemoteServers (subscriptionName) {
    const serverNames = this._clusterSubscriptions.getAllServers(subscriptionName)
    const localServerIndex = serverNames.indexOf(this._options.serverName)
    if (localServerIndex > -1) {
      serverNames.splice(serverNames.indexOf(this._options.serverName), 1)
    }
    return serverNames
  }

  /**
   * Returns a list of all the topic this registry
   * currently has subscribers for
   *
   * @public
   * @returns {Array} names
   */
  getNames () {
    return this._clusterSubscriptions.getAll()
  }

  /**
   * Returns true if the subscription exists somewhere
   * in the cluster
   *
   * @public
   * @returns {Array} names
   */
  hasName (subscriptionName) {
    return this._clusterSubscriptions.has(subscriptionName)
  }

  /**
  * This method allows you to customise the SubscriptionRegistry so that it can send
  * custom events and ack messages back.
  * For example, when using the C.ACTIONS.LISTEN, you would override SUBSCRIBE with
  * C.ACTIONS.SUBSCRIBE and UNSUBSCRIBE with UNSUBSCRIBE
  *
  * @param {string} name The name of the the variable to override. This can be either
  * MULTIPLE_SUBSCRIPTIONS, SUBSCRIBE, UNSUBSCRIBE, NOT_SUBSCRIBED
  *
  * @param {string} value The value to override with.
  *
  * @public
  * @returns {void}
  */
  setAction (name, value) {
    this._constants[name.toUpperCase()] = value
  }

  _onSocketClose (socket) {
    const names = this._names.get(socket) || new Set()
    for (const name of names) {
      this.unsubscribe(name, socket, true)
    }
  }

  /**
   * Broadcasts the enqueued messages for the timed out subscription room.
   *
   * @param   {Object} delayedBroadcasts holds information of what messages to send and where
   *
   * @public
   * @returns {void}
   */
  _onBroadcastTimeout () {
    this._delayedBroadcastsTimer = null
    for (const entry of this._delayedBroadcasts) {
      const name = entry[0]
      const delayedBroadcasts = entry[1]
      const uniqueSenders = delayedBroadcasts.uniqueSenders
      const sharedMessages = delayedBroadcasts.sharedMessages

      if (sharedMessages.length === 0) {
        this._delayedBroadcasts.delete(name)
        continue
      }

      // for all unique senders and their gaps, build their special messages
      for (const uniqueSender of uniqueSenders) {
        const socket = uniqueSender[0]
        const gaps = uniqueSender[1]
        let i = 0
        let message = sharedMessages.substring(0, gaps[i++])
        let lastStop = gaps[i++]
        while (i < gaps.length) {
          message += sharedMessages.substring(lastStop, gaps[i++])
          lastStop = gaps[i++]
        }
        message += sharedMessages.substring(lastStop, sharedMessages.length)

        if (message) {
          socket.sendNative(message)
        }
      }

      // for all sockets in this subscription name, send either sharedMessage or this socket's
      // specialized message. only sockets that sent something will have a special message, all
      // other sockets are only listeners and receive the exact same (sharedMessage) message.
      const sockets = this._subscriptions.get(name)
      if (sockets) {
        const preparedMessage = SocketWrapper.prepareMessage(sharedMessages)
        for (const socket of sockets) {
          if (!uniqueSenders.has(socket)) {
            socket.sendPrepared(preparedMessage)
          }
        }
        SocketWrapper.finalizeMessage(preparedMessage)
      }

      delayedBroadcasts.uniqueSenders.clear()
      delayedBroadcasts.sharedMessages = ''
    }
  }

  /**
   * Enqueues a message string to be broadcast to all subscribers. Broadcasts will potentially
   * be reordered in relation to *other* subscription names, but never in relation to the same
   * subscription name. Each broadcast is given 'broadcastTimeout' ms to coalesce into one big
   * broadcast.
   *
   * @param   {String} name      the name/topic the subscriber was previously registered for
   * @param   {String} msgString the message as string
   * @param   {Boolean} noDelay flay to disable broadcast delay for message
   * @param   {[SocketWrapper]} socket an optional socket that shouldn't receive the message
   *
   * @public
   * @returns {void}
   */
  sendToSubscribers (name, msgString, noDelay, socket) {
    if (!this._subscriptions.has(name)) {
      return
    }

    // not all messages are valid, this should be fixed elsewhere!
    if (msgString.charAt(msgString.length - 1) !== C.MESSAGE_SEPERATOR) {
      msgString += C.MESSAGE_SEPERATOR // eslint-disable-line
    }

    // if not already a delayed broadcast, create it
    let delayedBroadcasts = this._delayedBroadcasts.get(name)
    if (delayedBroadcasts === undefined) {
      delayedBroadcasts = {
        uniqueSenders: new Map(),
        sharedMessages: ''
      }
      this._delayedBroadcasts.set(name, delayedBroadcasts)
    }

    // append this message to the sharedMessage, the message that
    // is shared in the broadcast to every listener-only
    const start = delayedBroadcasts.sharedMessages.length
    delayedBroadcasts.sharedMessages += msgString
    const stop = delayedBroadcasts.sharedMessages.length

    // uniqueSendersMap maps from uuid to offset in uniqueSendersVector
    // each uniqueSender has a vector of "gaps" in relation to sharedMessage
    // sockets should not receive what they sent themselves, so a gap is inserted
    // for every send from this socket
    if (socket && socket.uuid !== undefined) {
      const uniqueSenders = delayedBroadcasts.uniqueSenders
      let gaps = uniqueSenders.get(socket)

      if (!gaps) {
        gaps = []
        uniqueSenders.set(socket, gaps)
      }

      gaps.push(start, stop)
    }

    // reuse the same timer if already started
    if (!this._delayedBroadcastsTimer) {
      if (this._delay !== -1 && !noDelay) {
        this._delayedBroadcastsTimer = setTimeout(this._onBroadcastTimeout, this._delay)
      } else {
        this._onBroadcastTimeout()
      }
    }
  }

  /**
   * Adds a SocketWrapper as a subscriber to a topic
   *
   * @param   {String} name
   * @param   {SocketWrapper} socket
   *
   * @public
   * @returns {void}
   */
  subscribe (name, socket) {
    const sockets = this._subscriptions.get(name) || new Set()

    if (sockets.size === 0) {
      this._subscriptions.set(name, sockets)
    } else if (sockets.has(socket)) {
      const msg = `repeat supscription to "${name}" by ${socket.user}`
      this._options.logger.log(C.LOG_LEVEL.WARN, this._constants.MULTIPLE_SUBSCRIPTIONS, msg)
      socket.sendError(this._topic, this._constants.MULTIPLE_SUBSCRIPTIONS, name)
      return
    }

    sockets.add(socket)

    if (socket.listeners('close').indexOf(this._onSocketClose) === -1) {
      socket.once('close', this._onSocketClose)
    }

    const names = this._names.get(socket) || new Set()
    if (names.size === 0) {
      this._names.set(socket, names)
    }
    names.add(name)

    if (this._subscriptionListener) {
      this._subscriptionListener.onSubscriptionMade(
        name,
        socket,
        sockets.size
      )
    }

    this._clusterSubscriptions.add(name)

    const logMsg = `for ${this._topic}:${name} by ${socket.user}`
    this._options.logger.log(C.LOG_LEVEL.DEBUG, this._constants.SUBSCRIBE, logMsg)
    socket.sendMessage(this._topic, C.ACTIONS.ACK, [this._constants.SUBSCRIBE, name])
  }

  /**
   * Removes a SocketWrapper from the list of subscriptions for a topic
   *
   * @param   {String} name
   * @param   {SocketWrapper} socket
   * @param   {Boolean} silent supresses logs and unsubscribe ACK messages
   *
   * @public
   * @returns {void}
   */
  unsubscribe (name, socket, silent) {
    const sockets = this._subscriptions.get(name)

    if (!sockets || !sockets.has(socket)) {
      const msg = `${socket.user} is not subscribed to ${name}`
      this._options.logger.log(C.LOG_LEVEL.WARN, this._constants.NOT_SUBSCRIBED, msg)
      socket.sendError(this._topic, this._constants.NOT_SUBSCRIBED, name)
      return
    }

    sockets.delete(socket)

    this._clusterSubscriptions.remove(name)

    if (sockets.size === 0) {
      this._subscriptions.delete(name)
    }

    const names = this._names.get(socket)
    names.delete(name)

    if (names.size === 0) {
      this._names.delete(socket)
    }

    if (this._subscriptionListener) {
      this._subscriptionListener.onSubscriptionRemoved(
        name,
        socket,
        sockets.size,
        this.getAllRemoteServers(name).length
      )
    }

    if (!silent) {
      const logMsg = `for ${this._topic}:${name} by ${socket.user}`
      this._options.logger.log(C.LOG_LEVEL.DEBUG, this._constants.UNSUBSCRIBE, logMsg)
      socket.sendMessage(this._topic, C.ACTIONS.ACK, [this._constants.UNSUBSCRIBE, name])
    }
  }

  /**
   * Returns an array of SocketWrappers that are subscribed
   * to <name> or null if there are no subscribers
   *
   * @param   {String} name
   *
   * @public
   * @returns {Array} SocketWrapper[]
   */
  getLocalSubscribers (name) {
    return this._subscriptions.get(name) || new Set()
  }

  /**
   * Returns true if there are SocketWrappers that
   * are subscribed to <name> or false if there
   * aren't any subscribers
   *
   * @param   {String}  name
   *
   * @public
   * @returns {Boolean} hasLocalSubscribers
   */
  hasLocalSubscribers (name) {
    return this._subscriptions.has(name)
  }

  /**
   * Allows to set a subscriptionListener after the class had been instantiated
   *
   * @param {SubscriptionListener} subscriptionListener a class exposing a onSubscriptionMade
   *                                                    and onSubscriptionRemoved method
   *
   * @public
   * @returns {void}
   */
  setSubscriptionListener (subscriptionListener) {
    this._subscriptionListener = subscriptionListener
  }

  /**
   * Called when a subscription has been added to the cluster
   * This can be invoked locally or remotely, so we check if it
   * is a local invocation and ignore it if so in favour of the
   * call done from subscribe
   * @param  {String} name the name that was added
   */
  _onClusterSubscriptionAdded (name) {
    if (this._subscriptionListener && !this.hasLocalSubscribers(name)) {
      this._subscriptionListener.onSubscriptionMade(name, null, 1)
    }
  }

  /**
   * Called when a subscription has been removed from the cluster
   * This can be invoked locally or remotely, so we check if it
   * is a local invocation and ignore it if so in favour of the
   * call done from unsubscribe
   * @param  {String} name the name that was removed
   */
  _onClusterSubscriptionRemoved (name) {
    if (this._subscriptionListener && !this.hasLocalSubscribers(name)) {
      this._subscriptionListener.onSubscriptionRemoved(name, null, 0, 0)
    }
  }
}

module.exports = SubscriptionRegistry

'use strict'

const C = require('../constants/constants')
const EventEmitter = require('events').EventEmitter

const DATA_LENGTH = {}
DATA_LENGTH[C.EVENT.DISTRIBUTED_STATE_REQUEST_FULL_STATE] = 1
DATA_LENGTH[C.EVENT.DISTRIBUTED_STATE_FULL_STATE] = 2
DATA_LENGTH[C.EVENT.DISTRIBUTED_STATE_ADD] = 3
DATA_LENGTH[C.EVENT.DISTRIBUTED_STATE_REMOVE] = 3

/**
 * This class provides a generic mechanism that allows to maintain
 * a distributed state amongst the nodes of a cluster. The state is an
 * array of unique strings in arbitrary order.
 *
 * Whenever a string is added by any node within the cluster for the first time,
 * an 'add' event is emitted. Whenever its removed by the last node within the cluster,
 * a 'remove' event is emitted.
 *
 * @extends {EventEmitter}
 *
 * @event 'add' emitted whenever an entry is added for the first time
 * @event 'remove' emitted whenever an entry is removed by the last node
 *
 * @author DeepstreamHub GmbH 2016
 */
module.exports = class DistributedStateRegistry extends EventEmitter {

  /**
  * Initialises the DistributedStateRegistry and subscribes to the provided cluster topic
  *
  * @param   {String} topic   A TOPIC constant
  * @param   {Object} options Global deepstream server options
  *
  * @constructor
  */
  constructor (topic, options) {
    super()
    this._topic = topic
    this._options = options
    this._options.messageConnector.subscribe(topic, this._processIncomingMessage.bind(this))
    this._options.clusterRegistry.on('remove', this.removeAll.bind(this))
    this._data = {}
    this._reconciliationTimeouts = {}
    this._fullStateSent = false
    this._requestFullState(C.ALL)
  }

  /**
  * Checks if a given entry exists within the registry
  *
  * @param   {String}  name       the name of the entry
  *
  * @public
  * @returns {Boolean} exists
  */
  has (name) {
    return !!this._data[name]
  }

  /**
  * Add a name/entry to the registry. If the entry doesn't exist yet,
  * this will notify the other nodes within the cluster
  *
  * @param {String} name any string key
  *
  * @public
  * @returns {void}
  */
  add (name) {
    if (!this._has(name, this._options.serverName)) {
      this._add(name, this._options.serverName)
      this._sendMessage(name, C.EVENT.DISTRIBUTED_STATE_ADD)
    }
  }

  /**
  * Removes a name/entry from the registry. If the entry doesn't exist,
  * this will exit silently
  *
  * @param {String} name any previously added name
  *
  * @public
  * @returns {void}
  */
  remove (name) {
    if (this._has(name, this._options.serverName)) {
      this._remove(name, this._options.serverName)
      this._sendMessage(name, C.EVENT.DISTRIBUTED_STATE_REMOVE)
    }
  }

  /**
  * Removes all entries for a given serverName. This is intended to be called
  * whenever a node leaves the cluster
  *
  * @param   {String} serverName The serverName of a node within the cluster
  *
  * @returns {[type]}
  */
  removeAll (serverName) {
    let name
    for (name in this._data) {
      if (this._data[name].nodes[serverName]) {
        this._remove(name, serverName)
      }
    }
  }

  /**
  * Returns all the servers that hold a given state
  *
  * @public
  * @returns {Object} entries
  */
  getAllServers (name) {
    if (this._data[name]) {
      return Object.keys(this._data[name].nodes)
    }

    return []
  }

  /**
  * Returns all currently registered entries
  *
  * @public
  * @returns {Array} entries
  */
  getAll () {
    return Object.keys(this._data)
  }

  /**
  * Removes an entry for a given serverName. If the serverName
  * was the last node that held the entry, the entire entry will
  * be removed and a `remove` event will be emitted
  *
  * @param   {String} name       the name of the entry
  * @param   {String} serverName the name of the server that no longer holds the entry
  *
  * @private
  * @returns {void}
  */
  _remove (name, serverName) {
    let exists = false

    if (!this._data[name]) {
      return
    }

    delete this._data[name].nodes[serverName]

    for (const nodeName in this._data[name].nodes) {
      if (this._data[name].nodes[nodeName] === true) {
        exists = true
      }
    }

    if (exists === false) {
      delete this._data[name]
      this.emit('remove', name)
    }
  }

  /**
  * Adds a new entry to this registry, either as a result of a remote or
  * a local addition. Will emit an `add` event if the entry wasn't present before
  *
  * @param {String} name       the name of the new entry
  * @param {String} serverName the name of the server that added the entry
  *
  * @private
  * @returns {void}
  */
  _add (name, serverName) {
    if (!this._data[name]) {
      this._data[name] = {
        nodes: {},
        checkSum: this._createCheckSum(name)
      }
      this.emit('add', name)
    }

    this._data[name].nodes[serverName] = true
  }

  /**
  * Checks if a given entry exists for a given serverName
  *
  * @param   {String}  name       the name of the entry
  * @param   {String}  serverName the name of the server that might hold the entry
  *
  * @private
  * @returns {Boolean} exists
  */
  _has (name, serverName) {
    return this._data[name] && this._data[name].nodes[serverName]
  }

  /**
  * Generic messaging function for add and remove messages
  *
  * @param   {String} name   the name of the entry that's added or removed
  * @param   {String} action action-constant, one of C.EVENT.<>
  *
  * @private
  * @returns {void}
  */
  _sendMessage (name, action) {
    const message = {
      topic: this._topic,
      action,
      data: [name, this._options.serverName, this._getCheckSumTotal(this._options.serverName)]
    }

    this._options.messageConnector.publish(this._topic, message)
  }

  /**
  * This method calculates the total checkSum for all local entries of
  * a given serverName
  *
  * @param {String} serverName the name of the server for which the checkSum should be calculated
  *
  * @private
  * @returns {Number} totalCheckSum
  */
  _getCheckSumTotal (serverName) {
    let totalCheckSum = 0
    let name

    for (name in this._data) {
      if (this._data[name].nodes[serverName]) {
        totalCheckSum += this._data[name].checkSum
      }
    }

    return totalCheckSum
  }

  /**
  * Calculates a simple checkSum for a given name. This is done up-front and cached
  * to increase performance for local add and remove operations. Arguably this is a generic
  * method and might be moved to the utils class if we find another usecase for it.
  *
  * @param   {String} name the name of the entry
  *
  * @private
  * @returns {Number} checkSum
  */
  _createCheckSum (name) { // eslint-disable-line
    let checkSum = 0
    let i

    for (i = 0; i < name.length; i++) {
      checkSum = ((checkSum << 5) - checkSum) + name.charCodeAt(i) // eslint-disable-line
    }

    return checkSum
  }

  /**
  * Checks a remote checkSum for a given serverName against the
  * actual checksum for all local entries for the given name.
  *
  * - If the checksums match, it removes all possibly pending
  *   reconciliationTimeouts
  *
  * - If the checksums don't match, it schedules a reconciliation request. If
  *   another message from the remote server arrives before the reconciliation request
  *   is send, it will be cancelled.
  *
  * @param   {String} serverName     the name of the remote server for which the checkSum
  *                                  should be calculated
  * @param   {Number} remoteCheckSum The checksum the remote server has calculated for all
  *                                  its local entries
  *
  * @private
  * @returns {void}
  */
  _verifyCheckSum (serverName, remoteCheckSum) {
    if (this._getCheckSumTotal(serverName) !== remoteCheckSum) {
      this._reconciliationTimeouts[serverName] = setTimeout(
                this._requestFullState.bind(this, serverName),
                this._options.stateReconciliationTimeout
            )
    } else if (this._reconciliationTimeouts[serverName]) {
      clearTimeout(this._reconciliationTimeouts[serverName])
      delete this._reconciliationTimeouts[serverName]
    }
  }

  /**
  * Sends a reconciliation request for a server with a given name (technically, its send to
  * every node within the cluster, but will be ignored by all but the one with a matching name)
  *
  * The matching node will respond with a DISTRIBUTED_STATE_FULL_STATE message
  *
  * @param   {String} serverName The name of the node with the compromised state
  *
  * @private
  * @returns {void}
  */
  _requestFullState (serverName) {
    this._options.messageConnector.publish(this._topic, {
      topic: this._topic,
      action: C.EVENT.DISTRIBUTED_STATE_REQUEST_FULL_STATE,
      data: [serverName]
    })
  }

  /**
  * Creates a full state message containing an array of all local entries that
  * will be used to reconcile compromised states as well as provide the full state
  * for new nodes that joined the cluster
  *
  * When a state gets compromised, more than one remote registry might request a full state update.
  * This method will  schedule a timeout in which no additional full state messages are sent to
  * make sure only a single full state message is sent in reply.
  *
  * @private
  * @returns {void}
  */
  _sendFullState () {
    const localState = []
    let name

    for (name in this._data) {
      if (this._data[name].nodes[this._options.serverName]) {
        localState.push(name)
      }
    }

    this._options.messageConnector.publish(this._topic, {
      topic: this._topic,
      action: C.EVENT.DISTRIBUTED_STATE_FULL_STATE,
      data: [this._options.serverName, localState]
    })

    this._fullStateSent = true
    setTimeout(this._resetFullStateSent.bind(this), this._options.stateReconciliationTimeout)
  }

  /**
  * This will apply the data from an incoming full state message. Entries that are not within
  * the incoming array will be removed for that node from the local registry and new entries will
  * be added.
  *
  * @param   {String} serverName the name of the server that send the full state message
  * @param   {Array}  names      a full list of all local entries of that server in arbitrary order
  *
  * @private
  * @returns {void}
  */
  _applyFullState (serverName, names) {
    let name
    let i

    for (name in this._data) {
            // please note: only checking if the name exists is sufficient as the registry will just
            // set node[serverName] to false if the entry exists, but not for the remote server.
      if (names.indexOf(name) === -1) {
        this._remove(name, serverName)
      }
    }

    for (i = 0; i < names.length; i++) {
      this._add(names[i], serverName)
    }
  }

  /**
  * Will be called after a full state message has been sent and
  * stateReconciliationTimeout has passed. This will allow further reconciliation
  * messages to be sent again.
  *
  * @private
  * @returns {void}
  */
  _resetFullStateSent () {
    this._fullStateSent = false
  }

  /**
  * This is the main routing point for messages coming in from
  * the message connector.
  *
  * @param   {Object} message a message connector message with topic, action and data fields
  *
  * @private
  * @returns {void}
  */
  _processIncomingMessage (message) {
    if (!this._isValidMessage(message)) {
      return
    }

    if (message.action === C.EVENT.DISTRIBUTED_STATE_ADD) {
      this._add(message.data[0], message.data[1])
      this._verifyCheckSum(message.data[1], message.data[2])
    } else if (message.action === C.EVENT.DISTRIBUTED_STATE_REMOVE) {
      this._remove(message.data[0], message.data[1])
      this._verifyCheckSum(message.data[1], message.data[2])
    } else if (message.action === C.EVENT.DISTRIBUTED_STATE_REQUEST_FULL_STATE) {
      if (
        message.data[0] === C.ALL ||
        (message.data[0] === this._options.serverName && this._fullStateSent === false)
      ) {
        this._sendFullState()
      }
    } else if (message.action === C.EVENT.DISTRIBUTED_STATE_FULL_STATE) {
      this._applyFullState(message.data[0], message.data[1])
    }
  }

  /**
  * Performs basic validations for incoming messages, based on action and data-length
  *
  * @param   {Object} message a message connector message with topic, action and data fields
  *
  * @private
  * @returns {Boolean} isValid
  */
  _isValidMessage (message) {
    if (DATA_LENGTH[message.action] === undefined) {
      this._options.logger.log(C.LOG_LEVEL.WARN, C.EVENT.UNKNOWN_ACTION, message.action)
      return false
    }

    if (message.data.length !== DATA_LENGTH[message.action]) {
      this._options.logger.log(C.LOG_LEVEL.WARN, C.EVENT.INVALID_MESSAGE_DATA, message.data)
      return false
    }

    return true
  }
}

'use strict'

const C = require('../constants/constants')
const SubscriptionRegistry = require('../utils/subscription-registry')
const DistributedStateRegistry = require('../cluster/distributed-state-registry')
const TimeoutRegistry = require('./listener-timeout-registry')
const messageBuilder = require('../message/message-builder')
const utils = require('../utils/utils')

module.exports = class ListenerRegistry {
  /**
  * Deepstream.io allows clients to register as listeners for subscriptions.
  * This allows for the creation of 'active' data-providers,
  * e.g. data providers that provide data on the fly, based on what clients
  * are actually interested in.
  *
  * When a client registers as a listener, it provides a regular expression.
  * It will then immediatly get a number of callbacks for existing record subscriptions
  * whose names match that regular expression.
  *
  * After that, whenever a record with a name matching that regular expression is subscribed
  * to for the first time, the listener is notified.
  *
  * Whenever the last subscription for a matching record is removed, the listener is also
  * notified with a SUBSCRIPTION_FOR_PATTERN_REMOVED action
  *
  * This class manages the matching of patterns and record names. The subscription /
  * notification logic is handled by this._providerRegistry
  *
  * @constructor
  *
  * @param {Object} options       DeepStream options
  * @param {SubscriptionRegistry} clientRegistry The SubscriptionRegistry containing the record
  *                                              subscriptions to allow new listeners to be
  *                                              notified of existing subscriptions
  */
  constructor (topic, options, clientRegistry) {
    this._topic = topic
    this._options = options
    this._clientRegistry = clientRegistry
    this._uniqueLockName = `${topic}_LISTEN_LOCK`

    this._uniqueStateProvider = this._options.uniqueRegistry
    this._messageConnector = this._options.messageConnector

    this._patterns = {}
    this._localListenInProgress = {}
    this._listenerTimeoutRegistry = new TimeoutRegistry(topic, options)

    this._locallyProvidedRecords = {}

    this._leadListen = {}
    this._leadingListen = {}

    this._setupProviderRegistry()
    this._setupRemoteComponents()
  }

  /**
   * Setup all the remote components and actions required to deal with the subscription
   * via the cluster.
   */
  _setupProviderRegistry () {
    this._providerRegistry = new SubscriptionRegistry(
      this._options,
      this._topic,
      `${this._topic}_${C.TOPIC.LISTEN_PATTERNS}`
    )
    this._providerRegistry.setAction('subscribe', C.ACTIONS.LISTEN)
    this._providerRegistry.setAction('unsubscribe', C.ACTIONS.UNLISTEN)
    this._providerRegistry.setSubscriptionListener({
      onSubscriptionRemoved: this._removePattern.bind(this),
      onSubscriptionMade: this._addPattern.bind(this)
    })
  }

  /**
   * Setup all the remote components and actions required to deal with the subscription
   * via the cluster.
   */
  _setupRemoteComponents () {
    this._clusterProvidedRecords = new DistributedStateRegistry(
      `${this._topic}_${C.TOPIC.PUBLISHED_SUBSCRIPTIONS}`,
      this._options
    )
    this._clusterProvidedRecords.on('add', this._onRecordStartProvided.bind(this))
    this._clusterProvidedRecords.on('remove', this._onRecordStopProvided.bind(this))

    this._messageConnector.subscribe(
      this._getMessageBusTopic(this._options.serverName, this._topic),
      this._onIncomingMessage.bind(this)
    )
  }

  /**
  * Used primarily for tests. Returns whether or not a provider exists for
  * the specific subscriptionName
  * @public
  * @returns {boolean}
  */
  hasActiveProvider (susbcriptionName) {
    return this._clusterProvidedRecords.has(susbcriptionName)
  }

  /**
  * The main entry point to the handle class.
  * Called on any of the following actions:
  *
  * 1) C.ACTIONS.LISTEN
  * 2) C.ACTIONS.UNLISTEN
  * 3) C.ACTIONS.LISTEN_ACCEPT
  * 4) C.ACTIONS.LISTEN_REJECT
  * 5) C.ACTIONS.LISTEN_SNAPSHOT
  *
  * @param   {SocketWrapper} socketWrapper the socket that send the request
  * @param   {Object} message parsed and validated message
  *
  * @public
  * @returns {void}
  */
  handle (socketWrapper, message) {
    const pattern = message.data[0]
    const subscriptionName = message.data[1]
    if (message.action === C.ACTIONS.LISTEN) {
      this._addListener(socketWrapper, message)
    } else if (message.action === C.ACTIONS.UNLISTEN) {
      this._providerRegistry.unsubscribe(pattern, socketWrapper)
      this._removeListener(socketWrapper, message)
    } else if (this._listenerTimeoutRegistry.isALateResponder(socketWrapper, message)) {
      this._listenerTimeoutRegistry.handle(socketWrapper, message)
    } else if (this._localListenInProgress[subscriptionName]) {
      this._processResponseForListenInProgress(socketWrapper, subscriptionName, message)
    } else {
      this._onMsgDataError(socketWrapper, message.raw, C.EVENT.INVALID_MESSAGE)
    }
  }

  /**
  * Handle messages that arrive via the message bus
  *
  * This can either be messages by the leader indicating that this
  * node is responsible for starting a local discovery phase
  * or from a resulting node with an ACK to allow the leader
  * to move on and release its lock
  *
  * @param  {Object} message The received message
  *
  * @private
  * @returns {void}
  */
  _onIncomingMessage (message) {
    if (this._options.serverName !== message.data[0]) {
      return
    }
    if (message.action === C.ACTIONS.LISTEN) {
      this._leadListen[message.data[1]] = message.data[2]
      this._startLocalDiscoveryStage(message.data[1])
    } else if (message.action === C.ACTIONS.ACK) {
      this._nextDiscoveryStage(message.data[1])
    } else if (message.action === C.ACTIONS.UNSUBSCRIBE) {
      this.onSubscriptionRemoved(
        message.data[1],
        null,
        this._clientRegistry.getLocalSubscribers(message.data[1]).size,
        this._clientRegistry.getAllServers(message.data[1]).length - 1
      )
    }
  }

  /**
  * Process an accept or reject for a listen that is currently in progress
  * and hasn't timed out yet.
  *
  * @param   {SocketWrapper} socketWrapper   The socket endpoint of the listener
  * @param   {String} subscriptionName       The name of the subscription that a listen
  *                                          is in process for
  * @param   {Object} message                Deepstream message object
  *
  * @private
  * @returns {void}
  */
  _processResponseForListenInProgress (socketWrapper, subscriptionName, message) {
    if (message.action === C.ACTIONS.LISTEN_ACCEPT) {
      this._accept(socketWrapper, message)
      this._listenerTimeoutRegistry.rejectLateResponderThatAccepted(subscriptionName)
      this._listenerTimeoutRegistry.clear(subscriptionName)
    } else if (message.action === C.ACTIONS.LISTEN_REJECT) {
      const provider = this._listenerTimeoutRegistry.getLateResponderThatAccepted(subscriptionName)
      if (provider) {
        this._accept(provider.socketWrapper, message)
        this._listenerTimeoutRegistry.clear(subscriptionName)
      } else {
        this._triggerNextProvider(subscriptionName)
      }
    }
  }

  /**
  * Called by the record subscription registry whenever the subscription count decrements.
  * Part of the subscriptionListener interface.
  *
  * @param   {String} name
  *
  * @public
  * @returns {void}
  */
  onSubscriptionMade (subscriptionName, socketWrapper, localCount) {
    if (this.hasActiveProvider(subscriptionName)) {
      this._sendHasProviderUpdateToSingleSubscriber(true, socketWrapper, subscriptionName)
      return
    }

    if (localCount > 1) {
      return
    }

    this._startDiscoveryStage(subscriptionName)
  }

  /**
  * Called by the record subscription registry whenever the subscription count increments.
  * Part of the subscriptionListener interface.
  *
  * @param   {String} subscriptionName
  *
  * @public
  * @returns {void}
  */
  onSubscriptionRemoved (subscriptionName, socketWrapper, localCount, remoteCount) {
    const provider = this._locallyProvidedRecords[subscriptionName]

    if (this.hasActiveProvider(subscriptionName) && !provider) {
      const serverName = this._clusterProvidedRecords.getAllServers(subscriptionName)[0]
      this._sendLastSubscriberRemoved(serverName, subscriptionName)
      return
    }

    if (!provider) {
      return
    }

    if (localCount > 1 || remoteCount > 0) {
      return
    }

    // provider discarded, but there is still another active subscriber
    if (localCount === 1 && provider.socketWrapper === socketWrapper) {
      return
    }

    // provider isn't a subscriber, meaning we should wait for 0
    const subscribers = this._clientRegistry.getLocalSubscribers(subscriptionName)
    if (localCount === 1 && !subscribers.has(provider.socketWrapper)) {
      return
    }

    this._sendSubscriptionForPatternRemoved(provider, subscriptionName)
    this._removeActiveListener(subscriptionName)
  }

  /**
  * Register callback for when the server recieves an accept message from the client
  *
  * @private
  * @returns {void}
  */
  _accept (socketWrapper, message) {
    const subscriptionName = message.data[1]

    this._listenerTimeoutRegistry.clearTimeout(subscriptionName)

    this._locallyProvidedRecords[subscriptionName] = {
      socketWrapper,
      pattern: message.data[0],
      closeListener: this._removeListener.bind(this, socketWrapper, message)
    }
    socketWrapper.once('close', this._locallyProvidedRecords[subscriptionName].closeListener)
    this._clusterProvidedRecords.add(subscriptionName)

    this._stopLocalDiscoveryStage(subscriptionName)
  }

  /**
  * Register a client as a listener for record subscriptions
  *
  * @param   {SocketWrapper} socketWrapper the socket that send the request
  * @param   {Object} message parsed and validated message
  *
  * @private
  * @returns {void}
  */
  _addListener (socketWrapper, message) {
    const pattern = this._getPattern(socketWrapper, message)
    const regExp = this._validatePattern(socketWrapper, pattern)

    if (!regExp) {
      return
    }

    if (!this._providerRegistry.getLocalSubscribers(pattern).has(socketWrapper)) {
      this._providerRegistry.subscribe(pattern, socketWrapper)
    }

    this._reconcileSubscriptionsToPatterns(regExp, pattern, socketWrapper)
  }

  /**
  * Find subscriptions that match pattern, and notify them that
  * they can be provided.
  *
  * We will attempt to notify all possible providers rather than
  * just the single provider for load balancing purposes and
  * so that the one listener doesnt potentially get overwhelmed.
  *
  * @private
  * @returns {Message}
  */
  _reconcileSubscriptionsToPatterns (regExp, pattern, socketWrapper) {
    for (const subscriptionName of this._clientRegistry.getNames()) {
      if (!subscriptionName.match(regExp)) {
        continue
      }

      if (this._locallyProvidedRecords[subscriptionName]) {
        continue
      }

      const listenInProgress = this._localListenInProgress[subscriptionName]

      if (listenInProgress) {
        listenInProgress.push({ socketWrapper, pattern })
      } else {
        this._startDiscoveryStage(subscriptionName)
      }
    }
  }

  /**
  * De-register a client as a listener for record subscriptions
  *
  * @param   {SocketWrapper} socketWrapper the socket that send the request
  * @param   {Object} message parsed and validated message
  *
  * @private
  * @returns {void}
  */
  _removeListener (socketWrapper, message) {
    const pattern = message.data[0]

    this._removeListenerFromInProgress(this._localListenInProgress, pattern, socketWrapper)
    this._removeListenerIfActive(pattern, socketWrapper)
  }

  /**
  * Removes the listener if it is the currently active publisher, and retriggers
  * another listener discovery phase
  *
  * @private
  * @returns {Message}
  */
  _removeListenerIfActive (pattern, socketWrapper) {
    for (const subscriptionName in this._locallyProvidedRecords) {
      const provider = this._locallyProvidedRecords[subscriptionName]
      if (
        provider.socketWrapper === socketWrapper &&
        provider.pattern === pattern
      ) {
        provider.socketWrapper.removeListener('close', provider.closeListener)
        this._removeActiveListener(subscriptionName)
        if (this._clientRegistry.hasLocalSubscribers(subscriptionName)) {
          this._startDiscoveryStage(subscriptionName)
        }
      }
    }
  }

  /**
  * @private
  * @returns {Void}
    */
  _removeActiveListener (subscriptionName) {
    delete this._locallyProvidedRecords[subscriptionName]
    this._clusterProvidedRecords.remove(subscriptionName)
  }

  /**
  * Start discovery phase once a lock is obtained from the leader within
  * the cluster
  *
  * @param   {String} subscriptionName the subscription name
  *
  * @private
  * @returns {void}
  */
  _startDiscoveryStage (subscriptionName) {
    const localListenArray = this._createLocalListenArray(
      this._patterns,
      this._providerRegistry,
      subscriptionName
    )

    if (localListenArray.length === 0) {
      return
    }

    this._uniqueStateProvider.get(this._getUniqueLockName(subscriptionName), (success) => {
      if (!success) {
        return
      }

      if (this.hasActiveProvider(subscriptionName)) {
        this._uniqueStateProvider.release(this._getUniqueLockName(subscriptionName))
        return
      }

      this._options.logger.log(
        C.LOG_LEVEL.DEBUG,
        C.EVENT.LEADING_LISTEN,
        `started for ${this._topic}:${subscriptionName}`
      )

      const remoteListenArray = this._createRemoteListenArray(
        this._patterns,
        this._providerRegistry,
        subscriptionName
      )
      this._leadingListen[subscriptionName] = remoteListenArray

      this._startLocalDiscoveryStage(subscriptionName, localListenArray)
    })
  }

  /**
  * called when a subscription has been provided to clear down the discovery stage,
  * or when an ack has been recieved via the message bus
  *
  * @param  {String} subscriptionName check if the subscription has a provider yet,
  *                                   if not trigger the next request via the message
  *                                   bus
  *
  * @private
  * @returns {void}
  */
  _nextDiscoveryStage (subscriptionName) {
    if (
      this.hasActiveProvider(subscriptionName) ||
      this._leadingListen[subscriptionName].length === 0
    ) {
      this._options.logger.log(
        C.LOG_LEVEL.DEBUG,
        C.EVENT.LEADING_LISTEN,
        `finished for ${this._topic}:${subscriptionName}`
      )
      delete this._leadingListen[subscriptionName]
      this._uniqueStateProvider.release(this._getUniqueLockName(subscriptionName))
    } else {
      const nextServerName = this._leadingListen[subscriptionName].shift()
      this._options.logger.log(
        C.LOG_LEVEL.DEBUG,
        C.EVENT.LEADING_LISTEN,
        `started for ${this._topic}:${subscriptionName}`
      )
      this._sendRemoteDiscoveryStart(nextServerName, subscriptionName)
    }
  }

  /**
  * Start discovery phase once a lock is obtained from the leader within
  * the cluster
  *
  * @param   {String} subscriptionName the subscription name
  * @param   {Object} [localListenMap] map of all listeners
  *
  * @private
  * @returns {void}
  */
  _startLocalDiscoveryStage (subscriptionName, localListenArray) {
    if (!localListenArray) {
      // eslint-disable-next-line
      localListenArray = this._createLocalListenArray(
        this._patterns,
        this._providerRegistry,
        subscriptionName
      )
    }

    if (localListenArray.length > 0) {
      this._options.logger.log(
        C.LOG_LEVEL.DEBUG,
        C.EVENT.LOCAL_LISTEN,
        `started for ${this._topic}:${subscriptionName}`
      )
      this._localListenInProgress[subscriptionName] = localListenArray
      this._triggerNextProvider(subscriptionName)
    }
  }

  /**
  * Finalises a local listener discovery stage
  *
  * @param   {String} subscriptionName the subscription a listener is searched for
  *
  * @private
  * @returns {void}
  */
  _stopLocalDiscoveryStage (subscriptionName) {
    delete this._localListenInProgress[subscriptionName]

    this._options.logger.log(
      C.LOG_LEVEL.DEBUG,
      C.EVENT.LOCAL_LISTEN,
      `stopped for ${this._topic}:${subscriptionName}`
    )

    if (this._leadingListen[subscriptionName]) {
      this._nextDiscoveryStage(subscriptionName)
    } else {
      this._sendRemoteDiscoveryStop(this._leadListen[subscriptionName], subscriptionName)
      delete this._leadListen[subscriptionName]
    }
  }

  /**
  * Trigger the next provider in the map of providers capable of publishing
  * data to the specific subscriptionName
  *
  * @param   {String} subscriptionName the subscription a listener is searched for
  *
  * @private
  * @returns {void}
  */
  _triggerNextProvider (subscriptionName) {
    const listenInProgress = this._localListenInProgress[subscriptionName]

    if (typeof listenInProgress === 'undefined') {
      return
    }

    if (listenInProgress.length === 0) {
      this._stopLocalDiscoveryStage(subscriptionName)
      return
    }

    const provider = listenInProgress.shift()
    const subscribers = this._clientRegistry.getLocalSubscribers(subscriptionName)

    if (subscribers && subscribers.has(provider.socketWrapper)) {
      this._stopLocalDiscoveryStage(subscriptionName)
      return
    }

    this._listenerTimeoutRegistry.addTimeout(
      subscriptionName,
      provider,
      this._triggerNextProvider.bind(this)
    )

    this._sendSubscriptionForPatternFound(provider, subscriptionName)
  }

  /**
  * Triggered when a subscription is being provided by a node in the cluster
  *
  * @param   {String} subscriptionName the subscription a listener is searched for
  *
  * @private
  * @returns {void}
  */
  _onRecordStartProvided (subscriptionName) {
    this._sendHasProviderUpdate(true, subscriptionName)
    if (this._leadingListen[subscriptionName]) {
      this._nextDiscoveryStage(subscriptionName)
    }
  }

  /**
  * Triggered when a subscription is stopped being provided by a node in the cluster
  *
  * @param   {String} subscriptionName the subscription a listener is searched for
  *
  * @private
  * @returns {void}
  */
  _onRecordStopProvided (subscriptionName) {
    this._sendHasProviderUpdate(false, subscriptionName)
    if (
      !this.hasActiveProvider(subscriptionName) &&
      this._clientRegistry.hasName(subscriptionName)
    ) {
      this._startDiscoveryStage(subscriptionName)
    }
  }

  /**
  * Compiles a regular expression from an incoming pattern
  *
  * @param {String} pattern       the raw pattern
  * @param {SocketWrapper} socketWrapper connection to the client that provided the pattern
  * @param {Number} count         the amount of times this pattern is present
  *
  * @private
  * @returns {void}
  */
  _addPattern (pattern /* , socketWrapper, count */) {
    if (!this._patterns[pattern]) {
      this._patterns[pattern] = new RegExp(pattern)
    }
  }

  /**
  * Deletes the pattern regex when removed
  *
  * @param {String} pattern       the raw pattern
  * @param {SocketWrapper} socketWrapper connection to the client that provided the pattern
  * @param {Number} count         the amount of times this pattern is present
  *
  * @private
  * @returns {void}
  */
  _removePattern (pattern, socketWrapper, count) {
    if (socketWrapper) {
      this._listenerTimeoutRegistry.removeProvider(socketWrapper)
      this._removeListenerFromInProgress(this._localListenInProgress, pattern, socketWrapper)
      this._removeListenerIfActive(pattern, socketWrapper)
    }

    if (count === 0) {
      delete this._patterns[pattern]
    }
  }

  /**
  * Remove provider from listen in progress map if it unlistens during
  * discovery stage
  *
  * @param  {Object} listensCurrentlyInProgress the listeners currently in progress
  * @param  {String} pattern the pattern that has been unlistened to
  * @param  {SocketWrapper} socketWrapper the socket wrapper of the provider that unlistened
  */
  _removeListenerFromInProgress (listensCurrentlyInProgress, pattern, socketWrapper) { // eslint-disable-line
    for (const subscriptionName in listensCurrentlyInProgress) {
      const listenInProgress = listensCurrentlyInProgress[subscriptionName]
      for (let i = 0; i < listenInProgress.length; i++) {
        if (
          listenInProgress[i].socketWrapper === socketWrapper &&
          listenInProgress[i].pattern === pattern
        ) {
          listenInProgress.splice(i, 1)
        }
      }
    }
  }

  /**
  * Sends a has provider update to a single subcriber
  * @param  {Boolean} hasProvider      send T or F so provided status
  * @param  {SocketWrapper}  socketWrapper    the socket wrapper to send to,
  *                                           if it doesn't exist then don't do anything
  * @param  {String}  subscriptionName The subscription name which provided status changed
  */
  _sendHasProviderUpdateToSingleSubscriber (hasProvider, socketWrapper, subscriptionName) {
    if (socketWrapper && this._topic === C.TOPIC.RECORD) {
      socketWrapper.send(this._createHasProviderMessage(hasProvider, this._topic, subscriptionName))
    }
  }

  /**
  * Sends a has provider update to all subcribers
  * @param  {Boolean} hasProvider      send T or F so provided status
  * @param  {String}  subscriptionName The subscription name which provided status changed
  */
  _sendHasProviderUpdate (hasProvider, subscriptionName) {
    if (this._topic !== C.TOPIC.RECORD) {
      return
    }
    this._clientRegistry.sendToSubscribers(
      subscriptionName,
      this._createHasProviderMessage(hasProvider, this._topic, subscriptionName)
    )
  }

  /**
  * Sent by the listen leader, and is used to inform the next server in the cluster to
  * start a local discovery
  *
  * @param  {String} serverName       the name of the server to notify
  * @param  {String} subscriptionName the subscription to find a provider for
  */
  _sendRemoteDiscoveryStart (serverName, subscriptionName) {
    const messageTopic = this._getMessageBusTopic(serverName, this._topic)
    this._messageConnector.publish(messageTopic, {
      topic: messageTopic,
      action: C.ACTIONS.LISTEN,
      data: [serverName, subscriptionName, this._options.serverName]
    })
  }

  /**
  * Sent by the listen follower, and is used to inform the leader that it has
  * complete its local discovery start
  *
  * @param  {String} listenLeaderServerName  the name of the listen leader
  * @param  {String} subscriptionName the subscription to that has just finished
  */
  _sendRemoteDiscoveryStop (listenLeaderServerName, subscriptionName) {
    const messageTopic = this._getMessageBusTopic(listenLeaderServerName, this._topic)
    this._messageConnector.publish(messageTopic, {
      topic: messageTopic,
      action: C.ACTIONS.ACK,
      data: [listenLeaderServerName, subscriptionName]
    })
  }

  /**
    * Send by a node when all local subscriptions are discarded, allowing other nodes
    * to do a provider cleanup if necessary
    */
  _sendLastSubscriberRemoved (serverName, subscriptionName) {
    const messageTopic = this._getMessageBusTopic(serverName, this._topic)
    this._messageConnector.publish(messageTopic, {
      topic: messageTopic,
      action: C.ACTIONS.UNSUBSCRIBE,
      data: [serverName, subscriptionName]
    })
  }

  /**
  * Send a subscription found to a provider
  *
  * @param  {{patten:String, socketWrapper:SocketWrapper}} provider A client that can
  *                                                        provide the subscription
  * @param  {String} subscriptionName the subscription to find a provider for
  */
  _sendSubscriptionForPatternFound (provider, subscriptionName) {
    provider.socketWrapper.send(
      messageBuilder.getMsg(
        this._topic,
        C.ACTIONS.SUBSCRIPTION_FOR_PATTERN_FOUND,
        [provider.pattern, subscriptionName]
      )
    )
  }

  /**
  * Send a subscription removed to a provider
  *
  * @param  {{patten:String, socketWrapper:SocketWrapper}} provider An object containing the
  *                                                        client that is currently the active
  *                                                        provider
  * @param  {String} subscriptionName the subscription to stop providing
  */
  _sendSubscriptionForPatternRemoved (provider, subscriptionName) {
    provider.socketWrapper.send(
      messageBuilder.getMsg(
        this._topic,
        C.ACTIONS.SUBSCRIPTION_FOR_PATTERN_REMOVED,
        [provider.pattern, subscriptionName]
      )
    )
  }

  /**
  * Create a map of all the listeners that patterns match the subscriptionName locally
  * @param  {Object} patterns         All patterns currently on this deepstream node
  * @param  {SusbcriptionRegistry} providerRegistry All the providers currently registered
  * @param  {String} subscriptionName the subscription to find a provider for
  * @return {Array}                  An array of all the providers that can provide the subscription
  */
  _createRemoteListenArray (patterns, providerRegistry, subscriptionName) {
    let servers = []
    const providerPatterns = providerRegistry.getNames()

    for (let i = 0; i < providerPatterns.length; i++) {
      const pattern = providerPatterns[i]
      let p = patterns[pattern]
      if (p == null) {
        this._options.logger.log(C.LOG_LEVEL.WARN, '', `can't handle pattern ${pattern}`)
        this._addPattern(pattern)
        p = patterns[pattern]
      }
      if (p.test(subscriptionName)) {
        servers = servers.concat(providerRegistry.getAllServers(pattern))
      }
    }

    const set = new Set(servers)
    set.delete(this._options.serverName)

    if (!this._options.shuffleListenProviders) {
      return Array.from(set)
    }
    return utils.shuffleArray(Array.from(set))
  }

  /**
  * Create a map of all the listeners that patterns match the subscriptionName locally
  * @param  {Object} patterns         All patterns currently on this deepstream node
  * @param  {SusbcriptionRegistry} providerRegistry All the providers currently registered
  * @param  {String} subscriptionName the subscription to find a provider for
  * @return {Array}                  An array of all the providers that can provide the subscription
  */
  _createLocalListenArray (patterns, providerRegistry, subscriptionName) { // eslint-disable-line
    const providers = []
    for (const pattern in patterns) {
      if (patterns[pattern].test(subscriptionName)) {
        for (const socketWrapper of providerRegistry.getLocalSubscribers(pattern)) {
          providers.push({ pattern, socketWrapper })
        }
      }
    }

    if (!this._options.shuffleListenProviders) {
      return providers
    }
    return utils.shuffleArray(providers)
  }

  /**
  * Extracts the subscription pattern from the message and notifies the sender
  * if something went wrong
  *
  * @param   {SocketWrapper} socketWrapper
  * @param   {Object} message
  *
  * @returns {String}
  */
  _getPattern (socketWrapper, message) {
    if (message.data.length > 2) {
      this._onMsgDataError(socketWrapper, message.raw)
      return null
    }

    const pattern = message.data[0]

    if (typeof pattern !== 'string') {
      this._onMsgDataError(socketWrapper, pattern)
      return null
    }

    return pattern
  }

  /**
  * Validates that the pattern is not empty and is a valid regular expression
  *
  * @param   {SocketWrapper} socketWrapper
  * @param   {String} pattern
  *
  * @returns {RegExp}
  */
  _validatePattern (socketWrapper, pattern) {
    if (!pattern) {
      return false
    }

    try {
      return new RegExp(pattern)
    } catch (e) {
      this._onMsgDataError(socketWrapper, e.toString())
      return false
    }
  }

  /**
  * Processes errors for invalid messages
  *
  * @param   {SocketWrapper} socketWrapper
  * @param   {String} errorMsg
  * @param   {Event} [errorEvent] Default to C.EVENT.INVALID_MESSAGE_DATA
  */
  _onMsgDataError (socketWrapper, errorMsg, errorEvent) {
    errorEvent = errorEvent || C.EVENT.INVALID_MESSAGE_DATA // eslint-disable-line
    socketWrapper.sendError(this._topic, errorEvent, errorMsg)
    // TODO: This isn't a CRITICAL error, would we say its an info
    this._options.logger.log(C.LOG_LEVEL.ERROR, errorEvent, errorMsg)
  }

  /**
  * Get the unique topic to use for the message bus
  * @param  {String} serverName the name of the server
  * @param  {Topic} topic
  * @return {String}
  */
  _getMessageBusTopic (serverName, topic) { // eslint-disable-line
    return C.TOPIC.LEADER_PRIVATE + serverName + topic + C.ACTIONS.LISTEN
  }

  /**
  * Returns the unique lock when leading a listen discovery phase
  *
  * @param  {String} subscriptionName the subscription to find a provider for
  *
  * @return {String}
  */
  _getUniqueLockName (subscriptionName) {
    return `${this._uniqueLockName}_${subscriptionName}`
  }

  /**
  * Create a has provider update message
  *
  * @returns {Message}
  */
  _createHasProviderMessage (hasProvider, topic, subscriptionName) { // eslint-disable-line
    return messageBuilder.getMsg(
      topic,
      C.ACTIONS.SUBSCRIPTION_HAS_PROVIDER,
      [subscriptionName, (hasProvider ? C.TYPES.TRUE : C.TYPES.FALSE)]
    )
  }
}

'use strict'

const messageBuilder = require('../message/message-builder')
const C = require('../constants/constants')

module.exports = class ListenerTimeoutRegistry {

  /**
  * The ListenerTimeoutRegistry is responsible for keeping track of listeners that have
  * been asked whether they want to provide a certain subscription, but have not yet
  * responded.
  *
  * @param {Topic} type
  * @param {Map} options
  *
  * @constructor
  */
  constructor (type, options) {
    this._type = type
    this._options = options
    this._timeoutMap = {}
    this._timedoutProviders = {}
    this._acceptedProvider = {}
  }

  /**
    * The main entry point, which takes a message from a provider
    * that has already timed out and does the following:
    *
    * 1) If reject, remove from map
    * 2) If accept, store as an accepted and reject all following accepts
    *
    * @param {SocketWrapper} socketWrapper
    * @param {Object} message deepstream message
    *
    * @private
    * @returns {void}
    */
  handle (socketWrapper, message) {
    const subscriptionName = message.data[1]
    const index = this._getIndex(socketWrapper, message)
    const provider = this._timedoutProviders[subscriptionName][index]
    if (message.action === C.ACTIONS.LISTEN_ACCEPT) {
      if (!this._acceptedProvider[subscriptionName]) {
        this._acceptedProvider[subscriptionName] = this._timedoutProviders[subscriptionName][index]
      } else {
        provider.socketWrapper.send(
          messageBuilder.getMsg(
            this._type,
            C.ACTIONS.SUBSCRIPTION_FOR_PATTERN_REMOVED,
            [provider.pattern, subscriptionName]
          )
        )
      }
    } else if (message.action === C.ACTIONS.LISTEN_REJECT) {
      this._timedoutProviders[subscriptionName].splice(index, 1)
    }
  }

  /**
    * Clear cache once discovery phase is complete
    *
    * @param {String} subscriptionName the subscription that needs to be removed
    *
    * @public
    * @returns {void}
    */
  clear (subscriptionName) {
    delete this._timeoutMap[subscriptionName]
    delete this._timedoutProviders[subscriptionName]
    delete this._acceptedProvider[subscriptionName]
  }

  /**
    * Called whenever a provider closes to ensure cleanup
    *
    * @param {SocketWrapper} socketWrapper the now closed connection endpoint
    *
    * @private
    * @returns {void}
    */
  removeProvider (socketWrapper) {
    for (const acceptedProvider in this._acceptedProvider) {
      if (this._acceptedProvider[acceptedProvider].socketWrapper === socketWrapper) {
        delete this._acceptedProvider[acceptedProvider]
      }
    }
    for (const subscriptionName in this._timeoutMap) {
      if (this._timeoutMap[subscriptionName]) {
        this.clearTimeout(subscriptionName)
      }
    }
  }
  /**
  * Starts a timeout for a provider. The following cases can apply
  *
  * Provider accepts within the timeout: We stop here
  * Provider rejects within the timeout: We ask the next provider
  * Provider doesn't respond within the timeout: We ask the next provider
  *
  * Provider accepts after the timeout:
  *  If no other provider accepted yet, we'll wait for the current request to end and stop here
  *  If another provider has accepted already, we'll immediatly send a SUBSCRIPTION_REMOVED message
  *
  * @param {String}   subscriptionName The subscription name
  * @param {Object}   provider         The provider that may timeout
  * @param {Function} callback         The callback if the timeout occurs
  *
  * @public
  * @returns {void}
  */
  addTimeout (subscriptionName, provider, callback) {
    const timeoutId = setTimeout(() => {
      if (this._timedoutProviders[subscriptionName] == null) {
        this._timedoutProviders[subscriptionName] = []
      }
      this._timedoutProviders[subscriptionName].push(provider)
      callback(subscriptionName)
    }, this._options.listenResponseTimeout)
    this._timeoutMap[subscriptionName] = timeoutId
  }

  /**
    * Clear the timeout for a LISTEN_ACCEPT or LISTEN_REJECt recieved
    * by the listen registry
    *
    * @public
    */
  clearTimeout (subscriptionName) {
    clearTimeout(this._timeoutMap[subscriptionName])
    delete this._timeoutMap[subscriptionName]
  }

  /**
    * Return if the socket is a provider that previously timeout
    *
    * @public
    */
  isALateResponder (socketWrapper, message) {
    const index = this._getIndex(socketWrapper, message)
    return this._timedoutProviders[message.data[1]] && index !== -1
  }

  /**
    * Return if the socket is a provider that previously timeout
    *
    * @public
    */
  rejectLateResponderThatAccepted (subscriptionName) {
    const provider = this._acceptedProvider[subscriptionName]
    if (provider) {
      provider.socketWrapper.send(
                messageBuilder.getMsg(
                    this._type,
                    C.ACTIONS.SUBSCRIPTION_FOR_PATTERN_REMOVED,
                    [provider.pattern, subscriptionName]
                )
            )
    }
  }

  /**
    * Return if the socket is a provider that previously timeout
    *
    * @public
    */
  getLateResponderThatAccepted (subscriptionName) {
    return this._acceptedProvider[subscriptionName]
  }

  /**
    * Return if the socket is a provider that previously timeout
    *
    * @private
    */
  _getIndex (socketWrapper, message) {
    const pattern = message.data[0]
    const subscriptionName = message.data[1]
    const timedoutProviders = this._timedoutProviders[subscriptionName]

    if (timedoutProviders) {
      for (let i = 0; i < timedoutProviders.length; i++) {
        if (
          timedoutProviders[i].socketWrapper === socketWrapper &&
          timedoutProviders[i].pattern === pattern
        ) {
          return i
        }
      }
    }

    return -1
  }
}

'use strict'

const DefaultLogger = require('../default-plugins/std-out-logger')
const fs = require('fs')
const utils = require('../utils/utils')
const C = require('../constants/constants')
const fileUtils = require('./file-utils')

const LOG_LEVEL_KEYS = Object.keys(C.LOG_LEVEL)

let commandLineArguments

/**
 * Takes a configuration object and instantiates functional properties.
 * CLI arguments will be considered.
 *
 * @param   {Object} config configuration
 *
 * @returns {Object} configuration
 */
exports.initialise = function (config) {
  commandLineArguments = global.deepstreamCLI || {}

  handleUUIDProperty(config)
  handleSSLProperties(config)
  handleLogger(config)
  handlePlugins(config)
  handleAuthStrategy(config)
  handlePermissionStrategy(config)

  return config
}

/**
 * Transform the UUID string config to a UUID in the config object.
 *
 * @param {Object} config deepstream configuration object
 *
 * @private
 * @returns {void}
 */
function handleUUIDProperty (config) {
  if (config.serverName === 'UUID') {
    config.serverName = utils.getUid()
  }
}

/**
 * Load the SSL files
 * CLI arguments will be considered.
 *
 * @param {Object} config deepstream configuration object
 *
 * @private
 * @returns {void}
 */
function handleSSLProperties (config) {
  const sslFiles = ['sslKey', 'sslCert', 'sslCa']
  let key
  let resolvedFilePath
  let filePath
  for (let i = 0; i < sslFiles.length; i++) {
    key = sslFiles[i]
    filePath = config[key]
    if (!filePath) {
      continue
    }
    resolvedFilePath = fileUtils.lookupConfRequirePath(filePath)
    try {
      config[key] = fs.readFileSync(resolvedFilePath, 'utf8')
    } catch (e) {
      throw new Error(`The file path "${resolvedFilePath}" provided by "${key}" does not exist.`)
    }
  }
}

/**
 * Initialize the logger and overwrite the root logLevel if it's set
 * CLI arguments will be considered.
 *
 * @param {Object} config deepstream configuration object
 *
 * @private
 * @returns {void}
 */
function handleLogger (config) {
  const configOptions = (config.logger || {}).options
  let Logger
  if (config.logger == null || config.logger.name === 'default') {
    Logger = DefaultLogger
  } else {
    Logger = resolvePluginClass(config.logger, 'logger')
  }

  if (configOptions instanceof Array) {
    // Note: This will not work on options without filename, and
    // is biased against for the winston logger
    let options
    for (let i = 0; i < configOptions.length; i++) {
      options = configOptions[i].options
      if (options && options.filename) {
        options.filename = fileUtils.lookupConfRequirePath(options.filename)
      }
    }
  }

  config.logger = new Logger(configOptions)
  if (LOG_LEVEL_KEYS.indexOf(config.logLevel) !== -1) {
    // NOTE: config.logLevel has highest priority, compare to the level defined
    // in the nested logger object
    config.logLevel = C.LOG_LEVEL[config.logLevel]
    config.logger.setLogLevel(config.logLevel)
  }
}

/**
 * Handle the plugins property in the config object the connectors.
 * Allowed types: {message|cache|storage}
 * Plugins can be passed either as a __path__ property or as a __name__ property with
 * a naming convetion: *{cache: {name: 'redis'}}* will be resolved to the
 * npm module *deepstream.io-cache-redis*
 * Exception: *message* will be resolved to *msg*
 * Options to the constructor of the plugin can be passed as *options* object.
 *
 * CLI arguments will be considered.
 *
 * @param {Object} config deepstream configuration object
 *
 * @private
 * @returns {void}
 */
function handlePlugins (config) {
  if (config.plugins == null) {
    return
  }
  // mappnig between the root properties which contains the plugin instance
  // and the plugin configuration objects
  const connectorMap = {
    messageConnector: 'message',
    cache: 'cache',
    storage: 'storage'
  }
  // mapping between the plugin configuration properties and the npm module
  // name resolution
  const typeMap = {
    message: 'msg',
    cache: 'cache',
    storage: 'storage'
  }
  const plugins = {
    messageConnector: config.plugins.message,
    cache: config.plugins.cache,
    storage: config.plugins.storage
  }

  for (const key in plugins) {
    const plugin = plugins[key]
    if (plugin != null) {
      const PluginConstructor = resolvePluginClass(plugin, typeMap[connectorMap[key]])
      config[key] = new PluginConstructor(plugin.options)
    }
  }
}

/**
 * Instantiate the given plugin, which either needs a path property or a name
 * property which fits to the npm module name convention. Options will be passed
 * to the constructor.
 *
 * CLI arguments will be considered.
 *
 * @param {Object} config deepstream configuration object
 *
 * @private
 * @returns {Function} Instance return be the plugin constructor
 */
function resolvePluginClass (plugin, type) {
  // nexe needs *global.require* for __dynamic__ modules
  // but browserify and proxyquire can't handle *global.require*
  const req = global && global.require ? global.require : require
  let requirePath
  let pluginConstructor
  if (plugin.path != null) {
    requirePath = fileUtils.lookupLibRequirePath(plugin.path)
    pluginConstructor = req(requirePath)
  } else if (plugin.name != null) {
    if (type != null) {
      requirePath = `deepstream.io-${type}-${plugin.name}`
      requirePath = fileUtils.lookupLibRequirePath(requirePath)
      pluginConstructor = req(requirePath)
    }
  } else {
    throw new Error(`Neither name nor path property found for ${type}`)
  }
  return pluginConstructor
}

/**
 * Instantiates the authentication handler registered for *config.auth.type*
 *
 * CLI arguments will be considered.
 *
 * @param   {Object} config deepstream configuration object
 *
 * @private
 * @returns {void}
 */
function handleAuthStrategy (config) {
  const authStrategies = {
    none: require('../authentication/open-authentication-handler'), // eslint-disable-line
    file: require('../authentication/file-based-authentication-handler'), // eslint-disable-line
    http: require('../authentication/http-authentication-handler') // eslint-disable-line
  }

  if (!config.auth) {
    throw new Error('No authentication type specified')
  }

  if (!authStrategies[config.auth.type]) {
    throw new Error(`Unknown authentication type ${config.auth.type}`)
  }

  if (commandLineArguments.disableAuth) {
    config.auth.type = 'none'
    config.auth.options = {}
  }

  if (config.auth.options && config.auth.options.path) {
    config.auth.options.path = fileUtils.lookupConfRequirePath(config.auth.options.path)
  }

  config.authenticationHandler =
    new (authStrategies[config.auth.type])(config.auth.options, config.logger)
}

/**
 * Instantiates the permission handler registered for *config.permission.type*
 *
 * CLI arguments will be considered.
 *
 * @param   {Object} config deepstream configuration object
 *
 * @private
 * @returns {void}
 */
function handlePermissionStrategy (config) {
  const permissionStrategies = {
    config: require('../permission/config-permission-handler'), // eslint-disable-line
    none: require('../permission/open-permission-handler') // eslint-disable-line
  }

  if (!config.permission) {
    throw new Error('No permission type specified')
  }

  if (!permissionStrategies[config.permission.type]) {
    throw new Error(`Unknown permission type ${config.permission.type}`)
  }

  if (commandLineArguments.disablePermissions) {
    config.permission.type = 'none'
    config.permission.options = {}
  }

  if (config.permission.options && config.permission.options.path) {
    config.permission.options.path = fileUtils.lookupConfRequirePath(config.permission.options.path)
  }

  config.permissionHandler = new (permissionStrategies[config.permission.type])(config)
}

'use strict'

require('colors')
const C = require('../constants/constants')

const EOL = require('os').EOL

/**
 * Logs to the operatingsystem's standard-out and standard-error streams.
 *
 * Consoles / Terminals as well as most log-managers and logging systems
 * consume messages from these streams
 *
 * @constructor
 */
const StdOutLogger = function (options) {
  this._options = options || {}
  this.isReady = true
  this._$useColors = this._options.colors === undefined ? true : this._options.colors
  this._logLevelColors = [
    'white',
    'green',
    'yellow',
    'red'
  ]

  this._currentLogLevel = C.LOG_LEVEL[this._options.logLevel] || C.LOG_LEVEL.DEBUG
}

/**
 * Logs a line
 *
 * @param   {Number} logLevel   One of the C.LOG_LEVEL constants
 * @param   {String} event      One of the C.EVENT constants
 * @param   {String} logMessage Any string
 *
 * @public
 * @returns {void}
 */
StdOutLogger.prototype.log = function (logLevel, event, logMessage) {
  if (logLevel < this._currentLogLevel) {
    return
  }

  const msg = `${event} | ${logMessage}`
  let outputStream

  if (logLevel === C.LOG_LEVEL.ERROR || logLevel === C.LOG_LEVEL.WARN) {
    outputStream = 'stderr'
  } else {
    outputStream = 'stdout'
  }

  if (this._$useColors) {
    process[outputStream].write(msg[this._logLevelColors[logLevel]] + EOL)
  } else {
    process[outputStream].write(msg + EOL)
  }
}

/**
 * Sets the log-level. This can be called at runtime.
 *
 * @param   {Number} logLevel   One of the C.LOG_LEVEL constants
 *
 * @public
 * @returns {void}
 */
StdOutLogger.prototype.setLogLevel = function (logLevel) {
  this._currentLogLevel = logLevel
}

module.exports = StdOutLogger

'use strict'

const fs = require('fs')
const path = require('path')

/**
* Append the global library directory as the prefix to any path
* used here
*
* @param {String} filePath
*
* @private
* @returns {String} file path with the library prefix used
*/
exports.lookupLibRequirePath = function (filePath) {
  return exports.lookupRequirePath(filePath, global.deepstreamLibDir)
}

/**
* Append the global configuration directory as the prefix to any path
* used here
*
* @param {String} filePath
*
* @private
* @returns {String} file path with the configuration prefix used
*/
exports.lookupConfRequirePath = function (filePath) {
  return exports.lookupRequirePath(filePath, global.deepstreamConfDir)
}

/**
 * Resolve a path which will be passed to *require*.
 *
 * If a prefix is not set the filePath will be returned
 * Otherwise it will either replace return a new path prepended with the prefix.
 * If the prefix is not an absolute path it will also prepend the CWD.
 *
 * file        || relative (starts with .) | absolute | else (npm module path)
 * -----------------------------------------------------------------------------
 * *prefix     || *CWD + prefix + file     | file     | *CWD + prefix + file
 * *no prefix  ||  CWD + file              | file     | file (resolved by nodes require)
 *
 * *CWD = ignore CWD if prefix is absolute
 *
 * @param {String} filePath
 * @param {String} prefix
 *
 * @private
 * @returns {String} file path with the prefix
 */
exports.lookupRequirePath = function (filePath, prefix) {
  // filePath is absolute
  if (path.parse(filePath).root !== '') {
    return filePath
  }

  // filePath is not relative (and not absolute)
  if (filePath[0] !== '.') {
    if (prefix == null) {
      return filePath
    }
    return resolvePrefixAndFile(filePath, prefix)
  }

  // filePath is relative, starts with .
  if (prefix == null) {
    return path.resolve(process.cwd(), filePath)
  }
  return resolvePrefixAndFile(filePath, prefix)
}

/**
 * Returns true if a file exists for a given path
 *
 * @param   {String} path
 *
 * @private
 * @returns {Boolean} exists
 */
exports.fileExistsSync = function (filePath) {
  try {
    fs.lstatSync(filePath)
    return true
  } catch (e) {
    return false
  }
}

/**
* Append the prefix to the current working directory,
* or use it as an absolute path
*
* @param   {String} nonAbsoluteFilePath
* @param   {String} prefix
*
* @private
* @returns {String} resolvedPath
*/
function resolvePrefixAndFile (nonAbsoluteFilePath, prefix) {
  // prefix is not absolute
  if (path.parse(prefix).root === '') {
    return path.resolve(process.cwd(), prefix, nonAbsoluteFilePath)
  }

  // prefix is absolute
  return path.resolve(prefix, nonAbsoluteFilePath)
}

'use strict'

const fs = require('fs')
const path = require('path')
const yaml = require('js-yaml')

const defaultOptions = require('../default-options')
const utils = require('../utils/utils')

const configInitialiser = require('./config-initialiser')
const fileUtils = require('./file-utils')

const SUPPORTED_EXTENSIONS = ['.yml', '.yaml', '.json', '.js']; path.join('.', 'conf', 'config')
const DEFAULT_CONFIG_DIRS = [path.join('.', 'conf', 'config'), '/etc/deepstream/config', '/usr/local/etc/deepstream/config']

/**
 * Reads and parse a general configuration file content.
 *
 * @param {String} filePath
 * @param {Function} callback
 *
 * @public
 * @returns {void}
 */
exports.readAndParseFile = function (filePath, callback) {
  try {
    fs.readFile(filePath, 'utf8', (error, fileContent) => {
      if (error) {
        return callback(error)
      }

      try {
        const config = parseFile(filePath, fileContent)
        return callback(null, config)
      } catch (parseError) {
        return callback(parseError)
      }
    })
  } catch (error) {
    callback(error)
  }
}

/**
 * Parse a general configuration file
 * These file extension ans formats are allowed:
 * .yml, .js, .json
 *
 * If no fileContent is passed the file is read synchronously
 *
 * @param {String} filePath
 * @param {String} fileContent
 *
 * @private
 * @returns {Object} config
 */
function parseFile (filePath, fileContent) {
  let config = null
  const extension = path.extname(filePath)

  if (extension === '.yml' || extension === '.yaml') {
    config = yaml.safeLoad(replaceEnvironmentVariables(fileContent))
  } else if (extension === '.js') {
    config = require(path.resolve(filePath)) // eslint-disable-line
  } else if (extension === '.json') {
    config = JSON.parse(replaceEnvironmentVariables(fileContent))
  } else {
    throw new Error(`${extension} is not supported as configuration file`)
  }

  return config
}

/**
 * Loads a config file without having to initialise it. Useful for one
 * off operations such as generating a hash via cli
 *
 * @param {Object|String} args commander arguments or path to config
 *
 * @public
 * @returns {Object} config deepstream configuration object
 */
module.exports.loadConfigWithoutInitialisation = function (filePath, /* test only */ args) {
  const argv = args || global.deepstreamCLI || {}
  const configPath = setGlobalConfigDirectory(argv, filePath)
  const configString = fs.readFileSync(configPath, { encoding: 'utf8' })
  const rawConfig = parseFile(configPath, configString)
  const config = extendConfig(rawConfig, argv)
  setGlobalLibDirectory(argv, config)
  return {
    config,
    configPath
  }
}

/**
 * Loads a file as deepstream config. CLI args have highest priority after the
 * configuration file. If some properties are not set they will be defaulted
 * to default values defined in the defaultOptions.js file.
 * Configuraiton file will be transformed to a deepstream object by evaluating
 * some properties like the plugins (logger and connectors).
 *
 * @param {Object|String} args commander arguments or path to config
 *
 * @public
 * @returns {Object} config deepstream configuration object
 */
module.exports.loadConfig = function (filePath, /* test only */ args) {
  const config = exports.loadConfigWithoutInitialisation(filePath, args)
  return {
    config: configInitialiser.initialise(config.config),
    file: config.configPath
  }
}

/**
* Set the globalConfig prefix that will be used as the directory for ssl, permissions and auth
* relative files within the config file
*/
function setGlobalConfigDirectory (argv, filePath) {
  const customConfigPath =
      argv.c ||
      argv.config ||
      filePath ||
      process.env.DEEPSTREAM_CONFIG_DIRECTORY
  const configPath = customConfigPath
    ? verifyCustomConfigPath(customConfigPath)
    : getDefaultConfigPath()
  global.deepstreamConfDir = path.dirname(configPath)
  return configPath
}

/**
* Set the globalLib prefix that will be used as the directory for the logger
* and plugins within the config file
*/
function setGlobalLibDirectory (argv, config) {
  const libDir =
      argv.l ||
      argv.libDir ||
      (config.libDir && fileUtils.lookupConfRequirePath(config.libDir)) ||
      process.env.DEEPSTREAM_LIBRARY_DIRECTORY
  global.deepstreamLibDir = libDir
}

/**
 * Augments the basic configuration with command line parameters
 * and normalizes paths within it
 *
 * @param   {Object} config    configuration
 * @param   {Object} argv      command line arguments
 * @param   {String} configDir config directory
 *
 * @private
 * @returns {Object} extended config
 */
function extendConfig (config, argv) {
  const cliArgs = {}
  let key

  for (key in defaultOptions.get()) {
    cliArgs[key] = typeof argv[key] === 'undefined' ? undefined : argv[key]
  }

  return utils.merge({ plugins: {} }, defaultOptions.get(), config, cliArgs)
}

/**
 * Checks if a config file is present at a given path
 *
 * @param   {String} configPath the path to the config file
 *
 * @private
 * @returns {String} verified path
 */
function verifyCustomConfigPath (configPath) {
  if (fileUtils.fileExistsSync(configPath)) {
    return configPath
  }

  throw new Error(`Configuration file not found at: ${configPath}`)
}

/**
 * Fallback if no config path is specified. Will attempt to load the file from the default directory
 *
 * @private
 * @returns {String} filePath
 */
function getDefaultConfigPath () {
  let filePath
  let i
  let k

  for (k = 0; k < DEFAULT_CONFIG_DIRS.length; k++) {
    for (i = 0; i < SUPPORTED_EXTENSIONS.length; i++) {
      filePath = DEFAULT_CONFIG_DIRS[k] + SUPPORTED_EXTENSIONS[i]
      if (fileUtils.fileExistsSync(filePath)) {
        return filePath
      }
    }
  }

  throw new Error('No config file found')
}

/**
 * Handle the introduction of global enviroment variables within
 * the yml file, allowing value substitution.
 *
 * For example:
 * ```
 * host: $HOST_NAME
 * port: $HOST_PORT
 * ```
 *
 * @param {String} fileContent The loaded yml file
 *
 * @private
 * @returns {void}
 */
function replaceEnvironmentVariables (fileContent) {
  const environmentVariable = new RegExp(/\${([^}]+)}/g)
  // eslint-disable-next-line
  fileContent = fileContent.replace(environmentVariable, (a, b) => process.env[b] || b)
  return fileContent
}

'use strict'

const C = require('../constants/constants')
const SubscriptionRegistry = require('../utils/subscription-registry')
const Rpc = require('./rpc')
const RpcProxy = require('./rpc-proxy')
const utils = require('../utils/utils')

module.exports = class RpcHandler {
  /**
  * Handles incoming messages for the RPC Topic.
  *
  * @param {Object} options deepstream options
  */
  constructor (options) {
    this._options = options
    this._subscriptionRegistry = new SubscriptionRegistry(options, C.TOPIC.RPC)

    this._privateTopic = C.TOPIC.PRIVATE + this._options.serverName
    this._options.messageConnector.subscribe(
      this._privateTopic,
      this._onPrivateMessage.bind(this)
    )

    this._rpcs = new Map()
  }

  /**
  * Main interface. Handles incoming messages
  * from the message distributor
  *
  * @param   {SocketWrapper} socketWrapper
  * @param   {Object} message parsed and validated deepstream message
  *
  * @public
  * @returns {void}
  */
  handle (socketWrapper, message) {
    if (message.action === C.ACTIONS.SUBSCRIBE) {
      this._registerProvider(socketWrapper, message)
    } else if (message.action === C.ACTIONS.UNSUBSCRIBE) {
      this._unregisterProvider(socketWrapper, message)
    } else if (message.action === C.ACTIONS.REQUEST) {
      this._makeRpc(socketWrapper, message)
    } else if (
      message.action === C.ACTIONS.RESPONSE ||
      message.action === C.ACTIONS.ACK ||
      message.action === C.ACTIONS.REJECTION ||
      message.action === C.ACTIONS.ERROR
    ) {
      const rpcNameIndex = (message.action === C.ACTIONS.ACK || message.action === C.ACTIONS.ERROR)
        ? 1 : 0
      const correlationId = message.data[rpcNameIndex + 1]
      const rpcData = this._rpcs.get(correlationId)
      if (rpcData) {
        rpcData.rpc.handle(message)
        if (rpcData.rpc.isComplete) {
          this._rpcs.delete(correlationId)
        }
      } else {
        socketWrapper.sendError(
          C.TOPIC.RPC,
          C.EVENT.INVALID_MESSAGE_DATA,
          `unexpected state for rpc ${message.data[rpcNameIndex]} with action ${message.action}`
        )
      }
    } else {
      /*
      *  RESPONSE-, ERROR-, REJECT- and ACK messages from the provider are processed
      * by the Rpc class directly
      */
      this._options.logger.log(C.LOG_LEVEL.WARN, C.EVENT.UNKNOWN_ACTION, message.action)

      if (socketWrapper !== C.SOURCE_MESSAGE_CONNECTOR) {
        socketWrapper.sendError(C.TOPIC.RPC, C.EVENT.UNKNOWN_ACTION, `unknown action ${message.action}`)
      }
    }
  }

  /**
  * This method is called by Rpc to reroute its request
  *
  * If a provider is temporarily unable to service a request, it can reject it. Deepstream
  * will then try to reroute it to an alternative provider. Finding an alternative provider
  * happens in this method.
  *
  * Initially, deepstream will look for a local provider that hasn't been used by the RPC yet.
  * If non can be found, it will go through the currently avaiblable remote providers and try
  * find one that hasn't been used yet.
  *
  * If a remote provider couldn't be found or all remote-providers have been tried already
  * this method will return null - which in turn will prompt the RPC to send a NO_RPC_PROVIDER
  * error to the client
  *
  * @param {String}  rpcName
  * @param {String}  correlationId
  *
  * @public
  * @returns {SocketWrapper|RpcProxy} alternativeProvider
  */
  getAlternativeProvider (rpcName, correlationId) {
    const rpcData = this._rpcs.get(correlationId)

    const subscribers = Array.from(this._subscriptionRegistry.getLocalSubscribers(rpcName))
    let index = utils.getRandomIntInRange(0, subscribers.length)

    for (let n = 0; n < subscribers.length; ++n) {
      if (!rpcData.providers.has(subscribers[index])) {
        rpcData.providers.add(subscribers[index])
        return subscribers[index]
      }
      index = (index + 1) % subscribers.length
    }

    if (!rpcData.servers) {
      return null
    }

    const servers = this._subscriptionRegistry.getAllRemoteServers(rpcName)
    index = utils.getRandomIntInRange(0, servers.length)
    for (let n = 0; n < servers.length; ++n) {
      if (!rpcData.servers.has(servers[index])) {
        rpcData.servers.add(servers[index])
        return new RpcProxy(this._options, C.TOPIC.PRIVATE + servers[index], rpcName, correlationId)
      }
      index = (index + 1) % servers.length
    }

    return null
  }

  /**
  * Callback for subscription messages. Registers
  * a client as a provider for specific remote
  * procedure calls as identified by <rpcName>
  *
  * @param   {SocketWrapper} socketWrapper
  * @param   {Object} message parsed and validated deepstream message
  *
  * @private
  * @returns {void}
  */
  _registerProvider (socketWrapper, message) {
    if (isValidMessage(1, socketWrapper, message)) {
      this._subscriptionRegistry.subscribe(message.data[0], socketWrapper)
    }
  }

  /**
  * Callback for unsubscribe messages. Removes
  * a client as a provider for specific remote
  * procedure calls as identified by <rpcName>
  *
  * @param   {SocketWrapper} socketWrapper
  * @param   {Object} message parsed and validated deepstream message
  *
  * @private
  * @returns {void}
  */
  _unregisterProvider (socketWrapper, message) {
    if (isValidMessage(1, socketWrapper, message)) {
      this._subscriptionRegistry.unsubscribe(message.data[0], socketWrapper)
    }
  }

  /**
  * Executes a RPC. If there are clients connected to
  * this deepstream instance that can provide the rpc, it
  * will be routed to a random one of them, otherwise it will be routed
  * to the message connector
  *
  * @param   {SocketWrapper} socketWrapper
  * @param   {Object} message parsed and validated deepstream message
  *
  * @private
  * @returns {void}
  */
  _makeRpc (socketWrapper, message, source) {
    if (!isValidMessage(2, socketWrapper, message)) {
      return
    }

    const rpcName = message.data[0]
    const correlationId = message.data[1]

    const rpcData = {
      providers: new Set(),
      servers: source !== C.SOURCE_MESSAGE_CONNECTOR ? new Set() : null,
      rpc: null
    }
    this._rpcs.set(correlationId, rpcData)

    const subscribers = Array.from(this._subscriptionRegistry.getLocalSubscribers(rpcName))
    const provider = subscribers[utils.getRandomIntInRange(0, subscribers.length)]

    if (provider) {
      rpcData.providers.add(provider)
      rpcData.rpc = new Rpc(this, socketWrapper, provider, this._options, message)
    } else if (source === C.SOURCE_MESSAGE_CONNECTOR) {
      socketWrapper.sendError(C.TOPIC.RPC, C.EVENT.NO_RPC_PROVIDER, [rpcName, correlationId])
    } else {
      this._makeRemoteRpc(socketWrapper, message)
    }
  }

  /**
  * Callback to remoteProviderRegistry.getProviderProxy()
  *
  * If a remote provider is available this method will route the rpc to it.
  *
  * If no remote provider could be found this class will return a
  * NO_RPC_PROVIDER error to the requestor. The RPC won't continue from
  * thereon
  *
  * @param   {SocketWrapper} requestor
  * @param   {Object} message   RPC Request message
  *
  * @private
  * @returns {void}
  */
  _makeRemoteRpc (requestor, message) {
    const rpcName = message.data[0]
    const correlationId = message.data[1]
    const rpcData = this._rpcs.get(correlationId)

    const servers = this._subscriptionRegistry.getAllRemoteServers(rpcName)
    const server = servers[Math.round(Math.random() * (servers.length - 1))]

    if (server) {
      const rpcProxy = new RpcProxy(this._options, C.TOPIC.PRIVATE + server, rpcName, correlationId)
      rpcData.rpc = new Rpc(this, requestor, rpcProxy, this._options, message)
      return
    }

    this._rpcs.delete(correlationId)

    this._options.logger.log(C.LOG_LEVEL.WARN, C.EVENT.NO_RPC_PROVIDER, rpcName)

    if (requestor !== C.SOURCE_MESSAGE_CONNECTOR) {
      requestor.sendError(C.TOPIC.RPC, C.EVENT.NO_RPC_PROVIDER, [rpcName, correlationId])
    }
  }

  /**
  * Callback for messages that are send directly to
  * this deepstream instance.
  *
  * Please note: Private messages are generic, so the RPC
  * specific ones need to be filtered out.
  *
  * @param   {Object} msg
  *
  * @private
  * @returns {void}
  */
  _onPrivateMessage (msg) {
    if (msg.originalTopic !== C.TOPIC.RPC) {
      return
    }

    if (!msg.data || msg.data.length < 2) {
      this._options.logger.log(C.LOG_LEVEL.WARN, C.EVENT.INVALID_MSGBUS_MESSAGE, msg.data)
      return
    }

    msg.topic = msg.originalTopic

    if (msg.action === C.ACTIONS.ERROR && msg.data[0] === C.EVENT.NO_RPC_PROVIDER) {
      msg.action = C.ACTIONS.REJECTION
      msg.data = msg.data[1]
    }

    if (msg.action === C.ACTIONS.REQUEST) {
      const proxy = new RpcProxy(this._options, msg.remotePrivateTopic, msg.data[0], msg.data[1])
      this._makeRpc(proxy, msg, C.SOURCE_MESSAGE_CONNECTOR)
    } else if ((msg.action === C.ACTIONS.ACK || msg.action === C.ACTIONS.ERROR) && msg.data[2]) {
      this._rpcs.get(msg.data[2]).rpc.handle(msg)
    } else if (this._rpcs.get(msg.data[1])) {
      this._rpcs.get(msg.data[1]).rpc.handle(msg)
    } else {
      this._options.logger.log(C.LOG_LEVEL.WARN, C.EVENT.UNSOLICITED_MSGBUS_MESSAGE, msg)
    }
  }


}

/**
* Checks if the incoming message is valid, e.g. if rpcName
* is present for subscribe / unsubscribe messages or if
* rpcName and correlationId is present for rpc calls.
*
* @param   {Number}  dataLength    The expected number of entries in the data array
* @param   {SocketWrapper} socketWrapper
* @param   {Object} message parsed and validated deepstream message
*
* @private
* @returns {Boolean} isValid
*/
function isValidMessage (dataLength, socketWrapper, message) {
  if (message.data && message.data.length >= dataLength && typeof message.data[0] === 'string') {
    return true
  }

  socketWrapper.sendError(C.TOPIC.RPC, C.EVENT.INVALID_MESSAGE_DATA, message.raw)

  return false
}

'use strict'
/* eslint-disable max-len */

const C = require('../constants/constants')
const RpcProxy = require('./rpc-proxy')

/**
 * Relays a remote procedure call from a requestor to a provider and routes
 * the providers response to the requestor. Provider might either be a locally
 * connected SocketWrapper or a RpcProviderProxy that forwards messages
 * from a remote provider within the network
 */
module.exports = class Rpc {
  /**
  * @param {RpcHandler}           rpcHandler
  * @param {String|SocketWrapper} requestor SocketWrapper if requested locally,
  *                                         C.SOURCE_MESSAGE_CONNECTOR if requested
  *                                         by the message connector
  * @param {SocketWrapper|RpcProviderProxy} provider  A SocketWrapper like class, able to provide the rpc
  * @param {Object} options
  * @param {Object} message
  *
  * @constructor
  */
  constructor (rpcHandler, requestor, provider, options, message) {
    this._rpcHandler = rpcHandler
    this._rpcName = message.data[0]
    this._correlationId = message.data[1]
    this._requestor = requestor
    this._provider = provider
    this._options = options
    this._message = message
    this._isAcknowledged = false
    this.isComplete = false

    this._setProvider(provider)
  }

  /**
  * Processor for incoming messages from the RPC provider. The
  * RPC provider is expected to send two messages,
  *
  * RPC|A|REQ|<rpcName>|<correlationId>
  *
  * and
  *
  * RPC|RES|<rpcName>|<correlationId|[<data>]
  *
  * Both of these messages will just be forwarded directly
  * to the requestor
  *
  * @param   {Object} message parsed and validated provider message
  *
  * @private
  * @returns {void}
  */
  handle (message) {
        // This guard is for backwards compatability. Having multiple responses or recieving an ack
        // prior to a response should not actually occur
    if (this.isComplete === true) {
      return
    }

    if (message.data[1] !== this._correlationId && message.data[2] !== this._correlationId) {
      return
    }

    if (message.action === C.ACTIONS.ACK) {
      this._handleAck(message)
    } else if (message.action === C.ACTIONS.REJECTION) {
      this._reroute()
    } else if (message.action === C.ACTIONS.RESPONSE || message.action === C.ACTIONS.ERROR) {
      this._handleResponse(message)
    }
  }

  /**
  * Destroyes this Rpc, either because its completed
  * or because a timeout has occured
  *
  * @public
  * @returns {void}
  */
  destroy () {
    clearTimeout(this._ackTimeout)
    clearTimeout(this._responseTimeout)

    this.isComplete = true
    this._requestor = null
    this._provider = null
    this._options = null
    this._message = null
  }

  /**
  * By default, a RPC is the communication between one requestor
  * and one provider. If the original provider however rejects
  * the request, deepstream will try to re-route it to another provider.
  *
  * This happens in the reroute method. This method will query
  * the rpc-handler for an alternative provider and - if it has
  * found one - call this method to replace the provider and re-do
  * the second leg of the rpc
  *
  * @param {SocketWrapper} provider
  *
  * @private
  * @returns {void}
  */
  _setProvider (provider) {
    clearTimeout(this._ackTimeout)
    clearTimeout(this._responseTimeout)

    this._provider = provider
    this._ackTimeout = setTimeout(this._onAckTimeout.bind(this), this._options.rpcAckTimeout)
    this._responseTimeout = setTimeout(this._onResponseTimeout.bind(this), this._options.rpcTimeout)
    this._send(this._provider, this._message, this._requestor)
  }

  /**
  * Handles rpc acknowledgement messages from the provider.
  * If more than one Ack is received an error will be returned
  * to the provider
  *
  * @param   {Object} message parsed and validated deepstream message
  *
  * @private
  * @returns {void}
  */
  _handleAck (message) {
    if (this._isAcknowledged === true) {
      this._provider.sendError(C.TOPIC.RPC, C.EVENT.MULTIPLE_ACK, [this._rpcName, this._correlationId])
      return
    }

    clearTimeout(this._ackTimeout)
    this._isAcknowledged = true
    this._send(this._requestor, message, this._provider)
  }

  /**
  * Forwards response messages from the provider. If the provider
  * sends more than one response subsequent messages will just
  * be ignored
  *
  * @param   {Object} message parsed and validated deepstream message
  *
  * @private
  * @returns {void}
  */
  _handleResponse (message) {
    clearTimeout(this._responseTimeout)
    this._send(this._requestor, message, this._provider)
    this.destroy()
  }

  /**
  * This method handles rejection messages from the current provider. If
  * a provider is temporarily unable to serve a request, it can reject it
  * and deepstream will try to reroute to an alternative provider
  *
  * If no alternative provider could be found, this method will send a NO_RPC_PROVIDER
  * error to the client and destroy itself
  *
  * @private
  * @returns {void}
  */
  _reroute () {
    const alternativeProvider = this._rpcHandler.getAlternativeProvider(this._rpcName, this._correlationId)

    if (alternativeProvider) {
      this._setProvider(alternativeProvider)
    } else {
      this._requestor.sendError(C.TOPIC.RPC, C.EVENT.NO_RPC_PROVIDER, [this._rpcName, this._correlationId])
      this.destroy()
    }
  }

  /**
  * Callback if the acknowledge message hasn't been returned
  * in time by the provider
  *
  * @private
  * @returns {void}
  */
  _onAckTimeout () {
    this._requestor.sendError(C.TOPIC.RPC, C.EVENT.ACK_TIMEOUT, [this._rpcName, this._correlationId])
    this.destroy()
  }

  /**
  * Callback if the response message hasn't been returned
  * in time by the provider
  *
  * @private
  * @returns {void}
  */
  _onResponseTimeout () {
    this._requestor.sendError(C.TOPIC.RPC, C.EVENT.RESPONSE_TIMEOUT, [this._rpcName, this._correlationId])
    this.destroy()
  }

  /**
  * Sends a message to a receiver.
  *
  * @param   {SocketWrapper} receiver    the SocketWrapper that will receive the message. Can be a RpcProxy
  * @param   {Object}        message     deepstream message object
  *
  * @private
  * @returns {void}
  */
  _send (receiver, message) { // eslint-disable-line
    if (receiver instanceof RpcProxy) {
      receiver.send(message)
      return
    }

    receiver.sendMessage(message.topic, message.action, message.data)
  }
}

'use strict'

const C = require('../constants/constants')

/**
 * This class exposes an interface that mimicks the behaviour
 * of a SocketWrapper, connected to a local rpc provider, but
 * infact relays calls from and to the message connector - sneaky.
 */
module.exports = class RpcProxy {

  /**
  * @param {Object} options
  * @param {String} receiverPrivateTopic
  * @return {[type]}
  * @constructor
  */
  constructor (options, receiverPrivateTopic) {
    this._options = options
    this._receiverPrivateTopic = receiverPrivateTopic
    this._privateTopic = C.TOPIC.PRIVATE + this._options.serverName
  }

  /**
  * Mimicks the SocketWrapper's send method, but expects a message object,
  * instead of a string.
  *
  * Adds additional information to the message that enables the counterparty
  * to identify the sender
  *
  * @param   {Object} message
  *
  * @public
  * @returns {void}
  */
  send (message) {
    message.remotePrivateTopic = this._privateTopic
    message.topic = this._receiverPrivateTopic
    message.originalTopic = C.TOPIC.RPC
    this._options.messageConnector.publish(this._receiverPrivateTopic, message)
    message.isCompleted = true
  }

  /**
  * Mimicks the SocketWrapper's sendError method.
  * Sends an error on the specified topic. The
  * action will automatically be set to C.ACTION.ERROR
  *
  * @param {String} topic one of C.TOPIC - ignored in this instance
  * @param {String} type one of C.EVENT
  * @param {String} msg generic error message
  *
  * @public
  * @returns {void}
  */
  sendError (topic, type, msg) {
    this._options.messageConnector.publish(this._receiverPrivateTopic, {
      topic: this._receiverPrivateTopic,
      originalTopic: C.TOPIC.RPC,
      action: C.ACTIONS.ERROR,
      data: [type, msg]
    })
  }
}

'use strict'

const C = require('../constants/constants')
const SubscriptionRegistry = require('../utils/subscription-registry')
const ListenerRegistry = require('../listen/listener-registry')
const RecordRequest = require('./record-request')
const RecordTransition = require('./record-transition')
const RecordDeletion = require('./record-deletion')

/**
 * The entry point for record related operations
 *
 * @param {Object} options deepstream options
 */
const RecordHandler = function (options) {
  this._options = options
  this._subscriptionRegistry = new SubscriptionRegistry(options, C.TOPIC.RECORD)
  this._listenerRegistry = new ListenerRegistry(C.TOPIC.RECORD, options, this._subscriptionRegistry)
  this._subscriptionRegistry.setSubscriptionListener(this._listenerRegistry)
  this._transitions = {}
  this._recordRequestsInProgress = {}
}

/**
 * Handles incoming record requests.
 *
 * Please note that neither CREATE nor READ is supported as a
 * client send action. Instead the client sends CREATEORREAD
 * and deepstream works which one it will be
 *
 * @param   {SocketWrapper} socketWrapper the sender
 * @param   {Object} message parsed and validated deepstream message
 *
 * @public
 * @returns {void}
 */
RecordHandler.prototype.handle = function (socketWrapper, message) {
  /*
   * All messages have to provide at least the name of the record they relate to
   * or a pattern in case of listen
   */
  if (!message.data || message.data.length < 1) {
    socketWrapper.sendError(C.TOPIC.RECORD, C.EVENT.INVALID_MESSAGE_DATA, message.raw)
    return
  }

  if (message.action === C.ACTIONS.CREATEORREAD) {
    /*
     * Return the record's contents and subscribes for future updates.
     * Creates the record if it doesn't exist
     */
    this._createOrRead(socketWrapper, message)
  } else if (message.action === C.ACTIONS.SNAPSHOT) {
    /*
     * Return the current state of the record in cache or db
     */
    this._snapshot(socketWrapper, message)
  } else if (message.action === C.ACTIONS.HEAD) {
    /*
     * Return the current state of the record in cache or db
     */
    this._head(socketWrapper, message)
  } else if (message.action === C.ACTIONS.HAS) {
    /*
     * Return a Boolean to indicate if record exists in cache or database
     */
    this._hasRecord(socketWrapper, message)
  } else if (message.action === C.ACTIONS.UPDATE || message.action === C.ACTIONS.PATCH) {
    /*
     * Handle complete (UPDATE) or partial (PATCH) updates
     */
    this._update(socketWrapper, message)
  } else if (message.action === C.ACTIONS.DELETE) {
    /*
     * Deletes the record
     */
    this._delete(socketWrapper, message)
  } else if (message.action === C.ACTIONS.UNSUBSCRIBE) {
  /*
   * Unsubscribes (discards) a record that was previously subscribed to
   * using read()
   */
    this._subscriptionRegistry.unsubscribe(message.data[0], socketWrapper)
  } else if (message.action === C.ACTIONS.LISTEN ||
  /*
   * Listen to requests for a particular record or records
   * whose names match a pattern
   */
    message.action === C.ACTIONS.UNLISTEN ||
    message.action === C.ACTIONS.LISTEN_ACCEPT ||
    message.action === C.ACTIONS.LISTEN_REJECT ||
    message.action === C.ACTIONS.LISTEN_SNAPSHOT) {
    this._listenerRegistry.handle(socketWrapper, message)
  } else {
  /*
   * Default for invalid messages
   */
    this._options.logger.log(C.LOG_LEVEL.WARN, C.EVENT.UNKNOWN_ACTION, message.action)

    if (socketWrapper !== C.SOURCE_MESSAGE_CONNECTOR) {
      socketWrapper.sendError(C.TOPIC.RECORD, C.EVENT.UNKNOWN_ACTION, `unknown action ${message.action}`)
    }
  }
}

/**
 * Tries to retrieve the record from the cache or storage. If not found in either
 * returns false, otherwise returns true.
 *
 * @param   {SocketWrapper} socketWrapper the socket that send the request
 * @param   {Object} message parsed and validated message
 *
 * @private
 * @returns {void}
 */
RecordHandler.prototype._hasRecord = function (socketWrapper, message) {
  const recordName = message.data[0]

  const onComplete = function (record) {
    const hasRecord = record ? C.TYPES.TRUE : C.TYPES.FALSE
    socketWrapper.sendMessage(C.TOPIC.RECORD, C.ACTIONS.HAS, [recordName, hasRecord])
  }
  const onError = function (error) {
    socketWrapper.sendError(C.TOPIC.RECORD, C.ACTIONS.HAS, [recordName, error])
  }

  // eslint-disable-next-line
  new RecordRequest(recordName,
    this._options,
    socketWrapper,
    onComplete.bind(this),
    onError.bind(this)
  )
}

/**
 * Sends the records data current data once loaded from the cache, and null otherwise
 *
 * @param {SocketWrapper} socketWrapper the socket that send the request
 * @param   {Object} message parsed and validated message
 * @private
 * @returns {void}
 */
RecordHandler.prototype._snapshot = function (socketWrapper, message) {
  const recordName = message.data[0]

  const onComplete = function (record) {
    if (record) {
      this._sendRecord(recordName, record, socketWrapper)
    } else {
      socketWrapper.sendError(
        C.TOPIC.RECORD,
        C.ACTIONS.SNAPSHOT,
        [recordName, C.EVENT.RECORD_NOT_FOUND]
      )
    }
  }
  const onError = function (error) {
    socketWrapper.sendError(C.TOPIC.RECORD, C.ACTIONS.SNAPSHOT, [recordName, error])
  }

  // eslint-disable-next-line
  new RecordRequest(
    recordName,
    this._options,
    socketWrapper,
    onComplete.bind(this),
    onError.bind(this)
  )
}

/**
 * Similar to snapshot, but will only return the current version number
 *
 * @param {SocketWrapper} socketWrapper the socket that send the request
 * @param   {Object} message parsed and validated message
 * @private
 * @returns {void}
 */
RecordHandler.prototype._head = function (socketWrapper, message) {
  const recordName = message.data[0]

  const onComplete = function (record) {
    if (record) {
      socketWrapper.sendMessage(C.TOPIC.RECORD, C.ACTIONS.HEAD, [recordName, record._v])
    } else {
      socketWrapper.sendError(
        C.TOPIC.RECORD,
        C.ACTIONS.HEAD,
        [recordName, C.EVENT.RECORD_NOT_FOUND]
      )
    }
  }
  const onError = function (error) {
    socketWrapper.sendError(C.TOPIC.RECORD, C.ACTIONS.HEAD, [recordName, error])
  }

  // eslint-disable-next-line
  new RecordRequest(
    recordName,
    this._options,
    socketWrapper,
    onComplete.bind(this),
    onError.bind(this)
  )
}


/**
 * Tries to retrieve the record and creates it if it doesn't exist. Please
 * note that create also triggers a read once done
 *
 * @param   {SocketWrapper} socketWrapper the socket that send the request
 * @param   {Object} message parsed and validated message
 *
 * @private
 * @returns {void}
 */
RecordHandler.prototype._createOrRead = function (socketWrapper, message) {
  const recordName = message.data[0]

  const onComplete = function (record) {
    if (record) {
      this._read(recordName, record, socketWrapper)
    } else {
      this._permissionAction(
        C.ACTIONS.CREATE,
        recordName,
        socketWrapper,
        this._create.bind(this, recordName, socketWrapper)
      )
    }
  }

  // eslint-disable-next-line
  new RecordRequest(
    recordName,
    this._options,
    socketWrapper,
    onComplete.bind(this)
  )
}

/**
 * Creates a new, empty record and triggers a read operation once done
 *
 * @param   {SocketWrapper} socketWrapper the socket that send the request
 * @param   {Object} message parsed and validated message
 *
 * @private
 * @returns {void}
 */
RecordHandler.prototype._create = function (recordName, socketWrapper) {
  const record = {
    _v: 0,
    _d: {}
  }

  // store the records data in the cache and wait for the result
  this._options.cache.set(recordName, record, (error) => {
    if (error) {
      this._options.logger.log(C.LOG_LEVEL.ERROR, C.EVENT.RECORD_CREATE_ERROR, recordName)
      socketWrapper.sendError(C.TOPIC.RECORD, C.EVENT.RECORD_CREATE_ERROR, recordName)
    } else {
      this._read(recordName, record, socketWrapper)
    }
  })

  if (!this._options.storageExclusion || !this._options.storageExclusion.test(recordName)) {
    // store the record data in the persistant storage independently and don't wait for the result
    this._options.storage.set(recordName, record, (error) => {
      if (error) {
        this._options.logger.log(C.LOG_LEVEL.ERROR, C.EVENT.RECORD_CREATE_ERROR, `storage:${error}`)
      }
    })
  }
}

/**
 * Subscribes to updates for a record and sends its current data once done
 *
 * @param {String} recordName
 * @param {Object} record
 * @param {SocketWrapper} socketWrapper the socket that send the request
 *
 * @private
 * @returns {void}
 */
RecordHandler.prototype._read = function (recordName, record, socketWrapper) {
  this._permissionAction(C.ACTIONS.READ, recordName, socketWrapper, () => {
    this._subscriptionRegistry.subscribe(recordName, socketWrapper)
    this._sendRecord(recordName, record, socketWrapper)
  })
}

/**
 * Sends the records data current data once done
 *
 * @param {String} recordName
 * @param {Object} record
 * @param {SocketWrapper} socketWrapper the socket that send the request
 *
 * @private
 * @returns {void}
 */
RecordHandler.prototype._sendRecord = function (recordName, record, socketWrapper) {
  socketWrapper.sendMessage(C.TOPIC.RECORD, C.ACTIONS.READ, [recordName, record._v, record._d])
}

 /**
 * Applies both full and partial updates. Creates a new record transition that will live as
 * long as updates are in flight and new updates come in
 *
 * @param   {SocketWrapper} socketWrapper the socket that send the request
 * @param   {Object} message parsed and validated message
 *
 * @private
 * @returns {void}
 */
RecordHandler.prototype._update = function (socketWrapper, message) {
  if (message.data.length < 3) {
    socketWrapper.sendError(C.TOPIC.RECORD, C.EVENT.INVALID_MESSAGE_DATA, message.data[0])
    return
  }

  const recordName = message.data[0]
  const version = parseInt(message.data[1], 10)

  /*
   * If the update message is received from the message bus, rather than from a client,
   * assume that the original deepstream node has already updated the record in cache and
   * storage and only broadcast the message to subscribers
   */
  if (socketWrapper === C.SOURCE_MESSAGE_CONNECTOR) {
    this._$broadcastUpdate(recordName, message, false, socketWrapper)
    return
  }

  if (isNaN(version)) {
    socketWrapper.sendError(C.TOPIC.RECORD, C.EVENT.INVALID_VERSION, [recordName, version])
    return
  }

  if (this._transitions[recordName] && this._transitions[recordName].hasVersion(version)) {
    this._transitions[recordName].sendVersionExists({ message, version, sender: socketWrapper })
    return
  }

  if (!this._transitions[recordName]) {
    this._transitions[recordName] = new RecordTransition(recordName, this._options, this)
  }

  this._transitions[recordName].add(socketWrapper, version, message)
}

/**
 * Invoked by RecordTransition. Notifies local subscribers and other deepstream
 * instances of record updates
 *
 * @param   {String} name           record name
 * @param   {Object} message        parsed and validated deepstream message
 * @param   {Boolean} noDelay       Flag as to wether event allows delay
 * @param   {SocketWrapper} originalSender the socket the update message was received from
 *
 * @package private
 * @returns {void}
 */
RecordHandler.prototype._$broadcastUpdate = function (name, message, noDelay, originalSender) {
  this._subscriptionRegistry.sendToSubscribers(name, message.raw, noDelay, originalSender)

  if (originalSender !== C.SOURCE_MESSAGE_CONNECTOR) {
    this._options.messageConnector.publish(C.TOPIC.RECORD, message)
  }
}

/**
 * Called by a RecordTransition, either if it is complete or if an error occured. Removes
 * the transition from the registry
 *
 * @todo  refactor - this is a bit of a mess
 * @param   {String} recordName record name
 *
 * @package private
 * @returns {void}
 */
RecordHandler.prototype._$transitionComplete = function (recordName) {
  delete this._transitions[recordName]
}

/**
 * Executes or schedules a callback function once all transitions are complete
 *
 * This is called from the PermissionHandler destroy method, which
 * could occur in cases where 'runWhenRecordStable' is never called,
 * such as when no cross referencing or data loading is used.
 *
 * @param   {String}   recordName the name of the record
 *
 * @private
 * @returns {void}
 */
RecordHandler.prototype.removeRecordRequest = function (recordName) {
  if (!this._recordRequestsInProgress[recordName]) {
    return
  }

  if (this._recordRequestsInProgress[recordName].length === 0) {
    delete this._recordRequestsInProgress[recordName]
    return
  }

  const callback = this._recordRequestsInProgress[recordName].splice(0, 1)[0]
  callback(recordName)
}

/**
 * Executes or schedules a callback function once all record requests are removed.
 * This is critical to block reads until writes have occured for a record, which is
 * only from permissions when a rule is required to be run and the cache has not
 * verified it has the latest version
 *
 * @param   {String}   recordName the name of the record
 * @param   {Function} callback   function to be executed once all writes to this record
 *                                are complete
 *
 * @public
 * @returns {void}
 */
RecordHandler.prototype.runWhenRecordStable = function (recordName, callback) {
  if (
    !this._recordRequestsInProgress[recordName] ||
    this._recordRequestsInProgress[recordName].length === 0
  ) {
    this._recordRequestsInProgress[recordName] = []
    callback(recordName)
  } else {
    this._recordRequestsInProgress[recordName].push(callback)
  }
}

/**
 * Deletes a record. If a transition is in progress it will be stopped. Once the
 * deletion is complete, an Ack is returned.
 *
 * If the deletion message is received from the message bus, rather than from a client,
 * we assume that the original deepstream node has already deleted the record from cache and
 * storage and we only need to broadcast the message to subscribers
 *
 * @param   {SocketWrapper} socketWrapper the socket that send the request
 * @param   {Object} message parsed and validated message
 *
 * @private
 * @returns {void}
 */
RecordHandler.prototype._delete = function (socketWrapper, message) {
  const recordName = message.data[0]

  if (this._transitions[recordName]) {
    this._transitions[recordName].destroy()
    delete this._transitions[recordName]
  }

  if (socketWrapper === C.SOURCE_MESSAGE_CONNECTOR) {
    this._onDeleted(recordName, message, socketWrapper)
  } else {
    // eslint-disable-next-line
    new RecordDeletion(this._options, socketWrapper, message, this._onDeleted.bind(this))
  }
}

/*
 * Callback for completed deletions. Notifies subscribers of the delete and unsubscribes them
 *
 * @param   {String} name           record name
 * @param   {Object} message        parsed and validated deepstream message
 * @param   {SocketWrapper} originalSender the socket the update message was received from
 *
 * @package private
 * @returns {void}
 */
RecordHandler.prototype._onDeleted = function (name, message, originalSender) {
  this._$broadcastUpdate(name, message, true, originalSender)

  for (const subscriber of this._subscriptionRegistry.getLocalSubscribers(name)) {
    this._subscriptionRegistry.unsubscribe(name, subscriber, true)
  }
}

/**
 * A secondary permissioning step that is performed once we know if the record exists (READ)
 * or if it should be created (CREATE)
 *
 * @param   {String} action          One of C.ACTIONS, either C.ACTIONS.READ or C.ACTIONS.CREATE
 * @param   {String} recordName      The name of the record
 * @param   {SocketWrapper} socketWrapper the socket that send the request
 * @param   {Function} successCallback A callback that will only be invoked if the operation was
 *                                     successful
 *
 * @private
 * @returns {void}
 */
RecordHandler.prototype._permissionAction = function (
  action, recordName, socketWrapper, successCallback
  ) {
  const message = {
    topic: C.TOPIC.RECORD,
    action,
    data: [recordName]
  }

  const onResult = function (error, canPerformAction) {
    if (error !== null) {
      socketWrapper.sendError(message.topic, C.EVENT.MESSAGE_PERMISSION_ERROR, error.toString())
    } else if (canPerformAction !== true) {
      socketWrapper.sendError(message.topic, C.EVENT.MESSAGE_DENIED, [recordName, action])
    } else {
      successCallback()
    }
  }

  this._options.permissionHandler.canPerformAction(
    socketWrapper.user,
    message,
    onResult,
    socketWrapper.authData
  )
}

module.exports = RecordHandler

'use strict'

const C = require('../constants/constants')

/**
 * This class retrieves a single record from the cache or - if it isn't in the
 * cache - from storage. If it isn't there either it will notify its initiator
 * by passing null to onComplete (but not call onError).
 *
 * It also handles all the timeout and destruction steps around this operation
 *
 * @param {String} recordName       the unique name of the record
 * @param {Object} options        deepstream options
 * @param {SocketWrapper} socketWrapper the sender whos message initiated the recordRequest
 * @param {Function} onComplete       callback for successful requests
 *                                      (even if the record wasn't found)
 * @param {[Function]} onError          callback for errors
 *
 * @constructor
 */
const RecordRequest = function (recordName, options, socketWrapper, onComplete, onError) {
  this._recordName = recordName
  this._options = options
  this._socketWrapper = socketWrapper
  this._storageRetrievalTimeout = null
  this._onComplete = onComplete
  this._onError = onError
  this._isDestroyed = false

  this._cacheRetrievalTimeout = setTimeout(
    this._sendError.bind(this, C.EVENT.CACHE_RETRIEVAL_TIMEOUT, this._recordName),
    this._options.cacheRetrievalTimeout
  )

  this._options.cache.get(this._recordName, this._onCacheResponse.bind(this))
}

/**
 * Callback for responses returned by the cache connector
 *
 * @param   {String} error  null if no error has occured
 * @param   {Object} record the record data structure, e.g. { _v: 33, _d: { some: 'data' } }
 *
 * @private
 * @returns {void}
 */
RecordRequest.prototype._onCacheResponse = function (error, record) {
  clearTimeout(this._cacheRetrievalTimeout)

  if (this._isDestroyed === true) {
    return
  }

  if (error) {
    this._sendError(C.EVENT.RECORD_LOAD_ERROR, `error while loading ${this._recordName} from cache:${error.toString()}`)
  } else if (record) {
    this._onComplete(record)
  } else if (
      !this._options.storageExclusion ||
      !this._options.storageExclusion.test(this._recordName)
    ) {
    this._storageRetrievalTimeout = setTimeout(
      this._sendError.bind(this, C.EVENT.STORAGE_RETRIEVAL_TIMEOUT, this._recordName),
      this._options.storageRetrievalTimeout
    )
    this._options.storage.get(this._recordName, this._onStorageResponse.bind(this))
  } else {
    this._onComplete(null)
  }
}

/**
 * Callback for responses returned by the storage connector. The request will complete or error
 * here, if the record couldn't be found in storage no further attempts to retrieve it will be made
 *
 * @param   {String} error  null if no error has occured
 * @param   {Object} record the record data structure, e.g. { _v: 33, _d: { some: 'data' } }
 *
 * @private
 * @returns {void}
 */
RecordRequest.prototype._onStorageResponse = function (error, record) {
  clearTimeout(this._storageRetrievalTimeout)

  if (this._isDestroyed === true) {
    return
  }

  if (error) {
    this._sendError(
      C.EVENT.RECORD_LOAD_ERROR,
      `error while loading ${this._recordName} from storage:${error.toString()}`
    )
  } else {
    this._onComplete(record || null)

    if (record) {
      /*
       * Load record from storage into cache
       */
      this._options.cache.set(this._recordName, record, () => {})
    }

    this._destroy()
  }
}

/**
 * Sends an error to the socketWrapper that requested the
 * record
 *
 * @param   {String} event      Error event
 * @param   {String} message    Error message
 *
 * @private
 * @returns {void}
 */
RecordRequest.prototype._sendError = function (event, message) {
  this._options.logger.log(C.LOG_LEVEL.ERROR, event, message)
  if (this._socketWrapper) {
    this._socketWrapper.sendError(C.TOPIC.RECORD, event, message)
  }
  if (this._onError) {
    this._onError(event, message)
  }
  this._destroy()
}

/**
 * Destroys the record request. Clears down all references and stops
 * all pending timeouts
 *
 * @private
 * @returns {void}
 */
RecordRequest.prototype._destroy = function () {
  clearTimeout(this._cacheRetrievalTimeout)
  clearTimeout(this._storageRetrievalTimeout)
  this._recordName = null
  this._options = null
  this._socketWrapper = null
  this._storageRetrievalTimeout = null
  this._onComplete = null
  this._onError = null
  this._isDestroyed = true
}

module.exports = RecordRequest

'use strict'

const C = require('../constants/constants')
const JsonPath = require('./json-path')
const RecordRequest = require('./record-request')
const messageParser = require('../message/message-parser')
const messageBuilder = require('../message/message-builder')
const utils = require('../utils/utils')

/**
 * This class manages one or more simultanious updates to the data of a record.
 * But: Why does that need to be so complicated and why does this class even exist?
 *
 * In short: Cross-network concurrency. If your record is written to by a single datasource
 * and consumed by many clients, this class is admittably overkill, but if deepstream is used to
 * build an app that allows many users to collaboratively edit the same dataset, sooner or later
 * two of them will do so at the same time and clash.
 *
 * Every deepstream record therefor has a version number that's incremented with every change.
 * Every client sends this version number along with the changed data. If no other update has
 * been received for the same version in the meantime, the update is accepted and not much more
 * happens.
 *
 * If, however, another clients was able to send its updated version before this update was
 * processed, the second (later) update for the same version number is rejected and the issuing
 * client is notified of the change.
 *
 * The client is then expected to merge its changes on top of the new version and re-issue the
 * update message.
 *
 * Please note: For performance reasons, succesful updates are not explicitly acknowledged.
 *
 * It's this class' responsibility to manage this. It will be created when an update arrives and
 * only exist as long as it takes to apply it and make sure that no subsequent updates for the
 * same version are requested.
 *
 * Once the update is applied it will notify the record-handler to broadcast the
 * update and delete the instance of this class.
 *
 * @param {String} name the name of the record that the transition will be applied to
 * @param {Object} deepstream options
 * @param {RecordHandler} recordHandler the instance of recordHandler that created this transition
 *
 * @constructor
 */
const RecordTransition = function (name, options, recordHandler) {
  this._name = name
  this._options = options
  this._recordHandler = recordHandler
  this._steps = []
  this._record = null
  this._currentStep = null
  this._recordRequest = null
  this._sendVersionExists = []
  this.isDestroyed = false
  this._pendingUpdates = {}
  this._ending = false
  this._storageResponses = 0
  this._cacheResponses = 0
  this._lastVersion = null
  this._lastError = null
}

/**
 * Checks if a specific version number is already processed or
 * queued for processing
 *
 * @param   {Number}  version
 *
 * @returns {Boolean} hasVersion
 */
RecordTransition.prototype.hasVersion = function (version) {
  return version !== -1 && version <= this._lastVersion
}

/**
 * Send version exists error if the record has been already loaded, else
 * store the version exists error to send to the sockerWrapper once the
 * record is loaded
 *
 * @param   {SocketWrapper} socketWrapper the sender
 * @param   {Number} version The version number
 *
 * @public
 */
RecordTransition.prototype.sendVersionExists = function (step) {
  const socketWrapper = step.sender
  const version = step.version
  const config = step.message.data[4]

  if (this._record) {
    const data = config === undefined
    ? [this._name, this._record._v, JSON.stringify(this._record._d)]
    : [this._name, this._record._v, JSON.stringify(this._record._d), config]
    socketWrapper.sendError(C.TOPIC.RECORD, C.EVENT.VERSION_EXISTS, data)

    const msg = `${socketWrapper.user} tried to update record ${this._name} to version ${version} but it already was ${this._record._v}`
    this._options.logger.log(C.LOG_LEVEL.WARN, C.EVENT.VERSION_EXISTS, msg)
  } else {
    this._sendVersionExists.push({
      version,
      sender: socketWrapper,
      config,
      message: step.message
    })
  }
}

/**
 * Adds a new step (either an update or a patch) to the record. The step
 * will be queued or executed immediatly if the queue is empty
 *
 * This method will also retrieve the current record's data when called
 * for the first time
 *
 * @param {SocketWrapper} socketWrapper that send the message
 * @param {Number} version the expected version that this update will apply
 * @param {Object} message parsed deepstream message. Data will still be stringified JSON
 *
 * @public
 * @returns {void}
 */
RecordTransition.prototype.add = function (socketWrapper, version, message) {
  const update = {
    message,
    version,
    upsert: false,
    sender: socketWrapper
  }
  let data

  try {
    const config = RecordTransition._getRecordConfig(message)
    this._applyConfig(config, update)
  } catch (e) {
    update.sender.sendError(
      C.TOPIC.RECORD,
      C.EVENT.INVALID_CONFIG_DATA,
      message.data[4] || message.data[3]
    )
    return
  }

  if (message.action === C.ACTIONS.UPDATE) {
    if (message.data.length !== 4 && message.data.length !== 3) {
      socketWrapper.sendError(C.TOPIC.RECORD, C.EVENT.INVALID_MESSAGE_DATA, message.raw)
      return
    }

    try {
      data = JSON.parse(message.data[2])
    } catch (e) {
      socketWrapper.sendError(C.TOPIC.RECORD, C.EVENT.INVALID_MESSAGE_DATA, message.raw)
      return
    }

    if (!utils.isOfType(data, 'object') && !utils.isOfType(data, 'array')) {
      socketWrapper.sendError(C.TOPIC.RECORD, C.EVENT.INVALID_MESSAGE_DATA, message.raw)
      return
    }

    update.isPatch = false
    update.data = data
  }

  if (message.action === C.ACTIONS.PATCH) {
    if (message.data.length !== 5 && message.data.length !== 4) {
      socketWrapper.sendError(C.TOPIC.RECORD, C.EVENT.INVALID_MESSAGE_DATA, message.raw)
      return
    }

    update.isPatch = true
    update.data = messageParser.convertTyped(message.data[3])

    if (update.data instanceof Error) {
      socketWrapper.sendError(C.TOPIC.RECORD, C.EVENT.INVALID_MESSAGE_DATA, `${update.data.toString()}:${message.data[3]}`)
      return
    }

    update.path = message.data[2]
  }

  if (this._lastVersion !== null && this._lastVersion !== version - 1) {
    this.sendVersionExists(update)
    return
  }

  if (version !== -1) {
    this._lastVersion = version
  }
  this._cacheResponses++
  this._steps.push(update)

  if (this._recordRequest === null) {
    this._recordRequest = new RecordRequest(
      this._name,
      this._options,
      socketWrapper,
      this._onRecord.bind(this, update),
      this._onFatalError.bind(this)
    )
  } else if (this._steps.length === 1 && this._cacheResponses === 1) {
    this._next()
  }
}

/**
 * Destroys the instance
 *
 * @private
 * @returns {void}
 */
RecordTransition.prototype.destroy = function (errorMessage) {
  if (this.isDestroyed) {
    return
  }

  this._sendWriteAcknowledgements(errorMessage || this._writeError)
  this._recordHandler._$transitionComplete(this._name)
  this.isDestroyed = true
  this._options = null
  this._name = null
  this._record = null
  this._recordHandler = null
  this._steps = null
  this._currentStep = null
  this._recordRequest = null
  this._pendingUpdates = null
  this._lastVersion = null
  this._cacheResponses = 0
  this._storageResponses = 0
}

/**
 * Tries to apply config given from a socketWrapper on an
 * incoming message
 *
 * @param {Object} step the current step of the transition
 * @param {String} message
 *
 * @private
 * @returns {void}
 */
RecordTransition.prototype._applyConfig = function (config, step) {
  if (!config) {
    return
  }

  if (config.writeSuccess) {
    if (this._pendingUpdates[step.sender.uuid] === undefined) {
      this._pendingUpdates[step.sender.uuid] = {
        socketWrapper: step.sender,
        versions: [step.version]
      }
    } else {
      const update = this._pendingUpdates[step.sender.uuid]
      update.versions.push(step.version)
    }
  }

  if (config.upsert) {
    step.upsert = true
  }
}

/**
 * Gets the config from an incoming Record message
 *
 * @param   {String} message
 *
 * @private
 * @throws {SyntaxError } If config not valid
 * @returns null or the given config
 */
RecordTransition._getRecordConfig = function (message) {
  let config
  if (message.action === C.ACTIONS.PATCH && message.data.length === 5) {
    config = message.data[4]
  } else if (message.action === C.ACTIONS.UPDATE && message.data.length === 4) {
    config = message.data[3]
  }

  if (!config) {
    return null
  }

  return JSON.parse(config)
}

/**
 * Callback for successfully retrieved records
 *
 * @param   {Object} record
 *
 * @private
 * @returns {void}
 */
RecordTransition.prototype._onRecord = function (step, record) {
  if (record === null && !step.upsert) {
    this._onFatalError(`Received update for non-existant record ${this._name}`)
  } else if (record === null && step.upsert) {
    const emptyRecord = {
      _v: 0,
      _d: {}
    }

    this._recordHandler._permissionAction(
      C.ACTIONS.CREATE,
      this._name,
      step.sender,
      this._processRecord.bind(this, emptyRecord)
    )
  } else {
    this._processRecord(record)
  }
}

/**
 * Callback used to process next update after record successfully returned or permissiom
 * check passed
 *
 * @param   {Object} record
 *
 * @private
 * @returns {void}
 */
RecordTransition.prototype._processRecord = function (record) {
  this._record = record
  this._flushVersionExists()
  this._next()
}


/**
 * Once the record is loaded this method is called recoursively
 * for every step in the queue of pending updates.
 *
 * It will apply every patch or update and - once done - either
 * call itself to process the next one or destroy the RecordTransition
 * of the queue has been drained
 *
 * @private
 * @returns {void}
 */
RecordTransition.prototype._next = function () {
  if (this.isDestroyed === true) {
    return
  }

  if (this._steps.length === 0) {
    if (this._cacheResponses === 0 && this._storageResponses === 0) {
      this.destroy()
    }
    return
  }

  this._currentStep = this._steps.shift()
  if (this._currentStep.version === -1) {
    const message = this._currentStep.message
    const version = this._record._v + 1
    this._currentStep.version = message.data[1] = version
    // Raw message is rebroadcast, needs to be rebuilt with new version number
    message.raw = messageBuilder.getMsg(message.topic, message.action, message.data)
  }

  if (this._record._v !== this._currentStep.version - 1) {
    this._cacheResponses--
    this.sendVersionExists(this._currentStep)
    this._next()
    return
  }

  this._record._v = this._currentStep.version

  if (this._currentStep.isPatch) {
    (new JsonPath(this._currentStep.path)).setValue(this._record._d, this._currentStep.data)
  } else {
    this._record._d = this._currentStep.data
  }

  /*
   * Please note: saving to storage is called first to allow for synchronous cache
   * responses to destroy the transition, it is however not on the critical path
   * and the transition will continue straight away, rather than wait for the storage response
   * to be returned.
   *
   * If the storage response is asynchronous and write acknowledgement is enabled, the transition
   * will not be destroyed until writing to storage is finished
   */
  if (!this._options.storageExclusion || !this._options.storageExclusion.test(this._name)) {
    this._storageResponses++
    this._options.storage.set(
      this._name,
      this._record,
      this._onStorageResponse.bind(this, this._currentStep)
    )
  }
  this._options.cache.set(
    this._name,
    this._record,
    this._onCacheResponse.bind(this, this._currentStep)
  )
}

/**
 * Send all the stored version exists errors once the record has been loaded.
 *
 * @private
 */
RecordTransition.prototype._flushVersionExists = function () {
  for (let i = 0; i < this._sendVersionExists.length; i++) {
    const conflict = this._sendVersionExists[i]
    this.sendVersionExists(conflict)
  }
  this._sendVersionExists = []
}

/**
 * Callback for responses returned by cache.set(). If an error
 * is returned the queue will be destroyed, otherwise
 * the update will be broadcast to other subscribers and the
 * next step invoked
 *
 * @param   {String} error
 *
 * @private
 * @returns {void}
 */
RecordTransition.prototype._onCacheResponse = function (currentStep, error) {
  this._cacheResponses--
  this._writeError = this._writeError || error
  if (error) {
    this._onFatalError(error)
  } else if (this.isDestroyed === false) {
    this._recordHandler._$broadcastUpdate(
      this._name,
      this._currentStep.message,
      false,
      this._currentStep.sender
    )
    this._next()
  } else if (
      this._cacheResponses === 0 &&
      this._storageResponses === 0 &&
      this._steps.length === 0
    ) {
    this.destroy()
  }
}

/**
 * Callback for responses returned by storage.set()
 *
 * @param   {String} error
 *
 * @private
 * @returns {void}
 */
RecordTransition.prototype._onStorageResponse = function (currentStep, error) {
  this._storageResponses--
  this._writeError = this._writeError || error
  if (error) {
    this._onFatalError(error)
  } else if (
      this._cacheResponses === 0 &&
      this._storageResponses === 0 &&
      this._steps.length === 0
    ) {
    this.destroy()
  }
}

/**
 * Sends all write acknowledgement messages at the end of a transition
 *
 * @param   {String} error any error message that occurred while storing the
 *                         record data
 *
 * @private
 * @returns {void}
 */
RecordTransition.prototype._sendWriteAcknowledgements = function (errorMessage) {
  errorMessage = errorMessage === undefined ? null : errorMessage // eslint-disable-line
  for (const uid in this._pendingUpdates) {
    const update = this._pendingUpdates[uid]

    update.socketWrapper.sendMessage(C.TOPIC.RECORD, C.ACTIONS.WRITE_ACKNOWLEDGEMENT, [
      this._name,
      update.versions,
      messageBuilder.typed(errorMessage)
    ])
  }
}

/**
 * Generic error callback. Will destroy the queue and notify the senders of all pending
 * transitions
 *
 * @param   {String} errorMessage
 *
 * @private
 * @returns {void}
 */
RecordTransition.prototype._onFatalError = function (errorMessage) {
  if (this.isDestroyed === true) {
    /* istanbul ignore next */
    return
  }
  this._options.logger.log(C.LOG_LEVEL.ERROR, C.EVENT.RECORD_UPDATE_ERROR, errorMessage)

  for (let i = 0; i < this._steps.length; i++) {
    if (this._steps[i].sender !== C.SOURCE_MESSAGE_CONNECTOR) {
      this._steps[i].sender.sendError(
        C.TOPIC.RECORD,
        C.EVENT.RECORD_UPDATE_ERROR,
        this._steps[i].version
      )
    }
  }

  if (this._cacheResponses === 0 && this._storageResponses === 0) {
    this.destroy(errorMessage)
  }
}

module.exports = RecordTransition

'use strict'
/* eslint-disable no-param-reassign */
const SPLIT_REG_EXP = /[.[\]]/g

/**
 * This class allows to set or get specific
 * values within a json data structure using
 * string-based paths
 *
 * @param {String} path A path, e.g. users[2].firstname
 *
 * @constructor
 */
const JsonPath = function (path) {
  this._path = path
  this._tokens = []
  this._tokenize()
}

/**
 * Sets the value of the path. If the path (or parts
 * of it) doesn't exist yet, it will be created
 *
 * @param {Object} node
 * @param {Mixed} value
 *
 * @public
 * @returns {void}
 */
JsonPath.prototype.setValue = function (node, value) {
  let i = 0

  for (i = 0; i < this._tokens.length - 1; i++) {
    if (node[this._tokens[i]] !== undefined) {
      node = node[this._tokens[i]]
    } else if (this._tokens[i + 1] && !isNaN(this._tokens[i + 1])) {
      node = node[this._tokens[i]] = []
    } else {
      node = node[this._tokens[i]] = {}
    }
  }

  node[this._tokens[i]] = value
}

/**
 * Parses the path. Splits it into
 * keys for objects and indices for arrays.
 *
 * @private
 * @returns {void}
 */
JsonPath.prototype._tokenize = function () {
  const parts = this._path.split(SPLIT_REG_EXP)
  let part
  let i

  for (i = 0; i < parts.length; i++) {
    part = parts[i].trim()

    if (part.length === 0) {
      continue
    }

    if (!isNaN(part)) {
      this._tokens.push(parseInt(part, 10))
      continue
    }

    this._tokens.push(part)
  }
}

module.exports = JsonPath

'use strict'

const messageBuilder = require('../message/message-builder')
const C = require('../constants/constants')

/**
 * This class represents the deletion of a single record. It handles it's removal
 * from cache and storage and handles errors and timeouts
 *
 * @param {Object}      options           deepstream options
 * @param {SocketWrapper}   socketWrapper     the sender of the delete message
 * @param {Object}      message           parsed and validated deletion message
 * @param {Function}    successCallback   callback for succesful deletions
 *
 * @constructor
 */
const RecordDeletion = function (options, socketWrapper, message, successCallback) {
  this._options = options
  this._socketWrapper = socketWrapper
  this._message = message
  this._successCallback = successCallback
  this._recordName = message.data[0]
  this._completed = 0
  this._isDestroyed = false

  this._cacheTimeout = setTimeout(
    this._handleError.bind(this, 'cache timeout'),
    this._options.cacheRetrievalTimeout
  )
  this._options.cache.delete(
    this._recordName,
    this._checkIfDone.bind(this, this._cacheTimeout)
  )

  if (!this._options.storageExclusion || !this._options.storageExclusion.test(this._recordName)) {
    this._storageTimeout = setTimeout(
      this._handleError.bind(this, 'storage timeout'),
      this._options.storageRetrievalTimeout
    )
    this._options.storage.delete(
      this._recordName,
      this._checkIfDone.bind(this, this._storageTimeout)
    )
  } else {
    this._checkIfDone(null)
  }
}

/**
 * Callback for completed cache and storage interactions. Will invoke
 * _done() once both are completed
 *
 * @param   {String} error     Error message or null
 * @param   {Number} timeoutId The id of the timeout that was associated with the request
 *
 * @returns {void}
 */
RecordDeletion.prototype._checkIfDone = function (timeoutId, error) {
  clearTimeout(timeoutId)
  this._completed++

  if (this._isDestroyed) {
    return
  }

  if (error) {
    this._handleError(error.toString())
    return
  }

  if (this._completed === 2) {
    this._done()
  }
}

/**
 * Callback for successful deletions. Notifies the original sender and calls
 * the callback to allow the recordHandler to broadcast the deletion
 *
 * @private
 * @returns {void}
 */
RecordDeletion.prototype._done = function () {
  this._options.logger.log(C.LOG_LEVEL.INFO, C.EVENT.RECORD_DELETION, this._recordName)

  const ackMessage = {
    topic: C.TOPIC.RECORD,
    action: C.ACTIONS.ACK,
    data: [C.ACTIONS.DELETE, this._recordName],
    raw: messageBuilder.getMsg(C.TOPIC.RECORD, C.ACTIONS.ACK, [C.ACTIONS.DELETE, this._recordName])
  }

  this._socketWrapper.send(ackMessage.raw)
  this._successCallback(this._recordName, ackMessage, this._socketWrapper)
  this._destroy()
}

/**
 * Destroyes the class and null down its dependencies
 *
 * @private
 * @returns {void}
 */
RecordDeletion.prototype._destroy = function () {
  clearTimeout(this._cacheTimeout)
  clearTimeout(this._storageTimeout)
  this._options = null
  this._socketWrapper = null
  this._message = null
  this._isDestroyed = true
}

/**
 * Handle errors that occured during deleting the record
 *
 * @param   {String} errorMsg
 *
 * @private
 * @returns {void}
 */
RecordDeletion.prototype._handleError = function (errorMsg) {
  this._socketWrapper.sendError(C.TOPIC.RECORD, C.EVENT.RECORD_DELETE_ERROR, errorMsg)
  this._options.logger.log(C.LOG_LEVEL.ERROR, C.EVENT.RECORD_DELETE_ERROR, errorMsg)
  this._destroy()
}

module.exports = RecordDeletion

'use strict'

const C = require('../constants/constants')
const SubscriptionRegistry = require('../utils/subscription-registry')
const DistributedStateRegistry = require('../cluster/distributed-state-registry')
const messageBuilder = require('../message/message-builder')

/**
 * This class handles incoming and outgoing messages in relation
 * to deepstream presence. It provides a way to inform clients
 * who else is logged into deepstream
 *
 * @param {Object} options    deepstream options
 * @param {Connection} connection
 * @param {Client} client
 * @public
 * @constructor
 */
module.exports = class PresenceHandler {

  constructor (options) {
    this._options = options
    this._localClients = new Map()
    this._connectionEndpoint = options.connectionEndpoint
    this._connectionEndpoint.on('client-connected', this._handleJoin.bind(this))
    this._connectionEndpoint.on('client-disconnected', this._handleLeave.bind(this))

    this._presenceRegistry = new SubscriptionRegistry(options, C.TOPIC.PRESENCE)

    this._connectedClients = new DistributedStateRegistry(C.TOPIC.ONLINE_USERS, options)
    this._connectedClients.on('add', this._onClientAdded.bind(this))
    this._connectedClients.on('remove', this._onClientRemoved.bind(this))
  }

  /**
  * The main entry point to the presence handler class.
  *
  * Handles subscriptions, unsubscriptions and queries
  *
  * @param   {SocketWrapper} socketWrapper the socket that send the request
  * @param   {Object} message parsed and validated message
  *
  * @public
  * @returns {void}
  */
  handle (socketWrapper, message) {
    if (message.action === C.ACTIONS.SUBSCRIBE) {
      this._presenceRegistry.subscribe(C.TOPIC.PRESENCE, socketWrapper)
    } else if (message.action === C.ACTIONS.UNSUBSCRIBE) {
      this._presenceRegistry.unsubscribe(C.TOPIC.PRESENCE, socketWrapper)
    } else if (message.action === C.ACTIONS.QUERY) {
      this._handleQuery(socketWrapper)
    } else {
      this._options.logger.log(C.LOG_LEVEL.WARN, C.EVENT.UNKNOWN_ACTION, message.action)

      if (socketWrapper !== C.SOURCE_MESSAGE_CONNECTOR) {
        socketWrapper.sendError(C.TOPIC.EVENT, C.EVENT.UNKNOWN_ACTION, `unknown action ${message.action}`)
      }
    }
  }

  /**
  * Called whenever a client has succesfully logged in with a username
  *
  * @param   {Object} socketWrapper the socketWrapper of the client that logged in
  *
  * @private
  * @returns {void}
  */
  _handleJoin (socketWrapper) {
    let currentCount = this._localClients.get(socketWrapper.user)
    if (currentCount === undefined) {
      this._localClients.set(socketWrapper.user, 1)
      this._connectedClients.add(socketWrapper.user)
    } else {
      currentCount++
      this._localClients.set(socketWrapper.user, currentCount)
    }
  }

  /**
  * Called whenever a client has disconnected
  *
  * @param   {Object} socketWrapper the socketWrapper of the client that disconnected
  *
  * @private
  * @returns {void}
  */
  _handleLeave (socketWrapper) {
    let currentCount = this._localClients.get(socketWrapper.user)
    if (currentCount === 1) {
      this._localClients.delete(socketWrapper.user)
      this._connectedClients.remove(socketWrapper.user)
    } else {
      currentCount--
      this._localClients.set(socketWrapper.user, currentCount)
    }
  }

  /**
  * Handles finding clients who are connected and splicing out the client
  * querying for users
  *
  * @param   {Object} socketWrapper the socketWrapper of the client that is querying
  *
  * @private
  * @returns {void}
  */
  _handleQuery (socketWrapper) {
    const clients = this._connectedClients.getAll()
    const index = clients.indexOf(socketWrapper.user)
    if (index !== -1) {
      clients.splice(index, 1)
    }
    socketWrapper.sendMessage(C.TOPIC.PRESENCE, C.ACTIONS.QUERY, clients)
  }

  /**
  * Alerts all clients who are subscribed to
  * PRESENCE_JOIN that a new client has been added.
  *
  * @param   {String} username the username of the client that joined
  *
  * @private
  * @returns {void}
  */
  _onClientAdded (username) {
    const addMsg = messageBuilder.getMsg(C.TOPIC.PRESENCE, C.ACTIONS.PRESENCE_JOIN, [username])
    this._presenceRegistry.sendToSubscribers(C.TOPIC.PRESENCE, addMsg)
  }

  /**
  * Alerts all clients who are subscribed to
  * PRESENCE_LEAVE that the client has left.
  *
  * @param   {String} username the username of the client that left
  *
  * @private
  * @returns {void}
  */
  _onClientRemoved (username) {
    const removeMsg = messageBuilder.getMsg(C.TOPIC.PRESENCE, C.ACTIONS.PRESENCE_LEAVE, [username])
    this._presenceRegistry.sendToSubscribers(C.TOPIC.PRESENCE, removeMsg)
  }
}

'use strict'

const C = require('../constants/constants')
const utils = require('util')

const EventEmitter = require('events').EventEmitter

/**
 * This class is used to track the initialisation of
 * an individual dependency (cache connector, persistance connector,
 * message connector, logger)
 *
 * @param {Object} options deepstream options
 * @param {String} name    the key of the dependency within the options
 *
 * @constructor
 */
const DependencyInitialiser = function (options, name) {
  this.isReady = false

  this._options = options
  this._dependency = options[name]
  this._name = name
  this._timeout = null

  if (typeof this._dependency.on !== 'function' && typeof this._dependency.isReady === 'undefined') {
    const errorMessage = `${this._name} needs to implement isReady or be an emitter`
    this._options.logger.log(C.LOG_LEVEL.ERROR, C.EVENT.PLUGIN_INITIALIZATION_ERROR, errorMessage)
    const error = new Error(errorMessage)
    error.code = 'PLUGIN_INITIALIZATION_ERROR'
    throw error
  }

  if (this._dependency.isReady) {
    this._onReady()
  } else {
    this._timeout = setTimeout(
      this._onTimeout.bind(this),
      this._options.dependencyInitialisationTimeout
    )
    this._dependency.once('ready', this._onReady.bind(this))
    this._dependency.on('error', this._onError.bind(this))

    if (this._dependency.init) {
      this._dependency.init()
    }
  }
}

utils.inherits(DependencyInitialiser, EventEmitter)

/**
 * Returns the underlying dependency (e.g. the Logger, StorageConnector etc.)
 *
 * @public
 * @returns {Dependency}
 */
DependencyInitialiser.prototype.getDependency = function () {
  return this._dependency
}

/**
 * Callback for succesfully initialised dependencies
 *
 * @private
 * @returns {void}
 */
DependencyInitialiser.prototype._onReady = function () {
  if (this._timeout) {
    clearTimeout(this._timeout)
  }

  const dependencyType = this._dependency.type ? `: ${this._dependency.type}` : ''
  this._options.logger.log(C.LOG_LEVEL.INFO, C.EVENT.INFO, `${this._name} ready${dependencyType}`)
  process.nextTick(this._emitReady.bind(this))
}

/**
 * Callback for dependencies that weren't initialised in time
 *
 * @private
 * @returns {void}
 */
DependencyInitialiser.prototype._onTimeout = function () {
  const message = `${this._name} wasn't initialised in time`
  this._logError(message)
  const error = new Error(message)
  error.code = C.EVENT.PLUGIN_INITIALIZATION_TIMEOUT
  throw error
}

/**
* Handles errors emitted by the dependency at startup.
*
* Plugin errors that occur at runtime are handled by the deepstream.io main class
*
* @param {Error|String} error
*
* @private
* @returns {void}
*/
DependencyInitialiser.prototype._onError = function (error) {
  if (this.isReady !== true) {
    this._logError(`Error while initialising ${this._name}: ${error.toString()}`)
    error.code = C.EVENT.PLUGIN_INITIALIZATION_ERROR
    throw error
  }
}

/**
 * Emits the ready event after a one tick delay
 *
 * @private
 * @returns {void}
 */
DependencyInitialiser.prototype._emitReady = function () {
  this.isReady = true
  this.emit('ready')
}

/**
 * Logs error messages
 *
 * Since the logger is a dependency in its own right, it can't be relied upon
 * here. If it is available, it will be used, otherwise the error will be logged
 * straight to the console
 *
 * @param   {String} message the error message
 *
 * @private
 * @returns {void}
 */
DependencyInitialiser.prototype._logError = function (message) {
  if (this._options.logger && this._options.logger.isReady) {
    this._options.logger.log(C.LOG_LEVEL.ERROR, C.EVENT.PLUGIN_ERROR, message)
  } else {
    console.error('Error while initialising dependency')
    console.error(message)
  }
}

module.exports = DependencyInitialiser

'use strict'

const C = require('../constants/constants')
const EventEmitter = require('events').EventEmitter

/**
 * This class maintains a list of all nodes that are
 * currently present within the cluster.
 *
 * It provides status messages on a predefined interval
 * and keeps track of incoming status messages.
 *
 * @emits add <serverName>
 * @emits remove <serverName>
 */
module.exports = class ClusterRegistry extends EventEmitter {
  /**
   * Creates the class, initialises all intervals and publishes the
   * initial status message that notifies other nodes within this
   * cluster of its presence.
   *
   * @param   {Object} options            deepstream options
   * @param   {ConnectionEndpoint}    connectionEndpoint deepstream connection endpoint
   *
   * @constructor
   */
  constructor (options, connectionEndpoint) {
    super()
    this._options = options
    this._connectionEndpoint = connectionEndpoint
    this._inCluster = false
    this._nodes = {}

    this._leaderScore = Math.random()
    this.setMaxListeners(12)

    this._onMessageFn = this._onMessage.bind(this)
    this._leaveClusterFn = this.leaveCluster.bind(this)
    this._options.messageConnector.subscribe(C.TOPIC.CLUSTER, this._onMessageFn)
    this._publishStatus()
    this._publishInterval = setInterval(
      this._publishStatus.bind(this),
      this._options.clusterKeepAliveInterval
    )
    this._checkInterval = setInterval(
      this._checkNodes.bind(this),
      this._options.clusterActiveCheckInterval
    )
    process.on('beforeExit', this._leaveClusterFn)
    process.on('exit', this._leaveClusterFn)
  }

  /**
   * Prompts this node to leave the cluster, either as a result of a server.close()
   * call or due to the process exiting.
   * This sends out a leave message to all other nodes and destroys this class.
   *
   * @public
   * @returns {[type]}
   */
  leaveCluster () {
    if (this._inCluster === false) {
      return
    }
    this._options.logger.log(C.LOG_LEVEL.INFO, C.EVENT.CLUSTER_LEAVE, this._options.serverName)
    this._options.messageConnector.publish(C.TOPIC.CLUSTER, {
      topic: C.TOPIC.CLUSTER,
      action: C.ACTIONS.REMOVE,
      data: [this._options.serverName]
    })

    // TODO: If a message connector doesn't close this is required to avoid an error
    // being thrown during shutdown
    // this._options.messageConnector.unsubscribe( C.TOPIC.CLUSTER, this._onMessageFn );

    process.removeListener('beforeExit', this._leaveClusterFn)
    process.removeListener('exit', this._leaveClusterFn)
    clearInterval(this._publishInterval)
    clearInterval(this._checkInterval)
    this._nodes = {}
    this._inCluster = false
  }

  /**
   * Returns the serverNames of all nodes currently present within the cluster
   *
   * @public
   * @returns {Array} serverNames
   */
  getAll () {
    return Object.keys(this._nodes)
  }

  /**
   * Returns true if this node is the cluster leader
   * @return {Boolean} [description]
   */
  isLeader () {
    return this._options.serverName === this.getCurrentLeader()
  }

  /**
  * Returns the name of the current leader
  * @return {String}
  */
  getCurrentLeader () {
    let maxScore = 0
    let serverName
    let leader = null

    for (serverName in this._nodes) {
      if (this._nodes[serverName].leaderScore > maxScore) {
        maxScore = this._nodes[serverName].leaderScore
        leader = serverName
      }
    }

    return leader
  }

  /**
   * Returns the public url of the least utilized node within the cluster.
   *
   * @todo this currently only takes memory usage into account, but ignores the
   *       amount of connections. Improve?
   *
   * @public
   * @returns {String} public URL
   */
  getLeastUtilizedExternalUrl () {
    let minMemory = Infinity
    let serverName
    let minNode

    for (serverName in this._nodes) {
      if (this._nodes[serverName].memory < minMemory) {
        minMemory = this._nodes[serverName].memory
        minNode = this._nodes[serverName]
      }
    }

    return minNode.externalUrl
  }

  /**
   * Distributes incoming messages on the cluster topic
   *
   * @param   {Object} message parsed deepstream message object
   *
   * @private
   * @returns {void}
   */
  _onMessage (message) {
    const data = message.data[0]

    if (!data) {
      this._options.logger.log(C.LOG_LEVEL.WARN, C.EVENT.INVALID_MESSAGE_DATA, message.data)
    } else if (message.action === C.ACTIONS.STATUS) {
      this._updateNode(data)
    } else if (message.action === C.ACTIONS.REMOVE) {
      this._removeNode(data)
    } else {
      this._options.logger.log(C.LOG_LEVEL.WARN, C.EVENT.UNKNOWN_ACTION, message.action)
    }
  }

  /**
   * Called on an interval defined by clusterActiveCheckInterval to check if all nodes
   * within the cluster are still alive.
   *
   * Being alive is defined as having received a status message from that node less than
   * <clusterNodeInactiveTimeout> milliseconds ago.
   *
   * @private
   * @returns {void}
   */
  _checkNodes () {
    const now = Date.now()
    let serverName

    for (serverName in this._nodes) {
      if (now - this._nodes[serverName].lastStatusTime > this._options.clusterNodeInactiveTimeout) {
        this._removeNode(serverName)
      }
    }
  }

  /**
   * Updates the status of a node with incoming status data and resets its lastStatusTime.
   *
   * If the remote node doesn't exist yet, it is added and an add event is emitted / logged
   *
   * @param   {Object} data node status data as generated by _publishStatus
   *
   * @private
   * @returns {void}
   */
  _updateNode (data) {
    const isNew = !this._nodes[data.serverName]
    this._nodes[data.serverName] = data
    this._nodes[data.serverName].lastStatusTime = Date.now()
    if (isNew) {
      this._options.logger.log(C.LOG_LEVEL.INFO, C.EVENT.CLUSTER_JOIN, data.serverName)
      this.emit('add', data.serverName)
    }
  }

  /**
   * Removes a remote node from this registry if it exists.
   *
   * Logs/emits remove
   *
   * @param   {String} serverName
   *
   * @private
   * @returns {void}
   */
  _removeNode (serverName) {
    if (this._nodes[serverName]) {
      delete this._nodes[serverName]
      this._options.logger.log(C.LOG_LEVEL.INFO, C.EVENT.CLUSTER_LEAVE, serverName)
      this.emit('remove', serverName)
    }
  }

  /**
   * Publishes this node's status on the message bus
   *
   * @private
   * @returns {void}
   */
  _publishStatus () {
    this._inCluster = true
    const memoryStats = process.memoryUsage()

    const data = {
      serverName: this._options.serverName,
      connections: this._connectionEndpoint.getConnectionCount(),
      memory: memoryStats.heapUsed / memoryStats.heapTotal,
      leaderScore: this._leaderScore,
      externalUrl: this._options.externalUrl
    }

    this._updateNode(data)

    this._options.messageConnector.publish(C.TOPIC.CLUSTER, {
      topic: C.TOPIC.CLUSTER,
      action: C.ACTIONS.STATUS,
      data: [data]
    })
  }
}

'use strict'

const C = require('../constants/constants')
const utils = require('../utils/utils')
const EventEmitter = require('events').EventEmitter

const SUPPORTED_ACTIONS = {}
SUPPORTED_ACTIONS[C.ACTIONS.LOCK_RESPONSE] = true
SUPPORTED_ACTIONS[C.ACTIONS.LOCK_REQUEST] = true
SUPPORTED_ACTIONS[C.ACTIONS.LOCK_RELEASE] = true

/**
 * The unique registry is responsible for maintaing a single source of truth
 * within the cluster, used mainly for issuing cluster wide locks when an operation
 * that stretches over multiple nodes are required.
 *
 * For example, distributed listening requires a leader to drive the nodes in sequence,
 * so issuing a lock prevents multiple nodes from assuming the lead.
 *
 */
module.exports = class UniqueRegistry {
  /**
  * The unique registry is a singleton and is only created once
  * within deepstream.io. It is passed via
  * via the options object.
  *
  * @param  {Object} options                     The options deepstream was created with
  * @param  {ClusterRegistry} clusterRegistry    The cluster registry, used to get the
  *                                              cluster leader
  *
  * @constructor
  */
  constructor (options, clusterRegistry) {
    this._options = options
    this._clusterRegistry = clusterRegistry
    this._locks = {}
    this._timeouts = {}
    this._responseEventEmitter = new EventEmitter()
    this._onPrivateMessageFn = this._onPrivateMessage.bind(this)
    this._localTopic = this._getPrivateTopic(this._options.serverName)
    this._options.messageConnector.subscribe(this._localTopic, this._onPrivateMessageFn)
  }

  /**
  * Requests a lock, if the leader ( whether local or distributed ) has the lock availble
  * it will invoke the callback with true, otherwise false.
  *
  * @param  {String}   name     the lock name that is desired
  * @param  {Function} callback the callback to be told if the lock has been reserved succesfully
  *
  * @public
  * @returns {void}
  */
  get (name, callback) {
    const leaderServerName = this._clusterRegistry.getCurrentLeader()

    if (this._options.serverName === leaderServerName) {
      callback(this._getLock(name))
    } else if (!this._timeouts[name]) {
      this._getRemoteLock(name, leaderServerName, callback)
    } else {
      callback(false)
    }
  }

  /**
  * Release a lock, allowing other resources to request it again
  *
  * @param  {String}   name     the lock name that is desired
  *
  * @public
  * @returns {void}
  */
  release (name) {
    const leaderServerName = this._clusterRegistry.getCurrentLeader()

    if (this._options.serverName === leaderServerName) {
      this._releaseLock(name)
    } else {
      this._releaseRemoteLock(name, leaderServerName)
    }
  }

  /**
  * Called when the current node is not the leader, issuing a lock request
  * via the message bus
  *
  * @param  {String}   name             The lock name
  * @param  {String}   leaderServerName The leader of the cluster
  * @param  {Function} callback         The callback to invoke once a response
  *                                     from the server is retrieved
  * @private
  * @returns {void}
  */
  _getRemoteLock (name, leaderServerName, callback) {
    this._timeouts[name] = utils.setTimeout(
            this._onLockRequestTimeout.bind(this, name),
            this._options.lockRequestTimeout
        )

    this._responseEventEmitter.once(name, callback)

    const remoteTopic = this._getPrivateTopic(leaderServerName)

    this._options.messageConnector.publish(remoteTopic, {
      topic: remoteTopic,
      action: C.ACTIONS.LOCK_REQUEST,
      data: [{
        name,
        responseTopic: this._localTopic
      }]
    })
  }

  /**
  * Notifies a remote leader keeping a lock that said lock is no longer required
  *
  * @param  {String}   name             The lock name
  * @param  {String}   leaderServerName The leader of the cluster
  *
  * @private
  * @returns {void}
  */
  _releaseRemoteLock (name, leaderServerName) {
    const remoteTopic = this._getPrivateTopic(leaderServerName)

    this._options.messageConnector.publish(remoteTopic, {
      topic: remoteTopic,
      action: C.ACTIONS.LOCK_RELEASE,
      data: [{
        name
      }]
    })
  }

  /**
  * Called when a message is recieved on the message bus.
  * This could mean the leader responded to a request or that you're currently
  * the leader and recieved a request.
  *
  * @param  {Object} message Object from message bus
  *
  * @private
  * @returns {void}
  */
  _onPrivateMessage (message) {
    if (!SUPPORTED_ACTIONS[message.action]) {
      this._options.logger.log(C.LOG_LEVEL.WARN, C.EVENT.UNKNOWN_ACTION, message.action)
      return
    }

    if (!message.data || !message.data[0]) {
      this._options.logger.log(C.LOG_LEVEL.WARN, C.EVENT.INVALID_MESSAGE_DATA, message.data)
      return
    }

    if (message.action === C.ACTIONS.LOCK_RESPONSE) {
      this._handleRemoteLockResponse(message.data[0])
      return
    }

    if (this._clusterRegistry.isLeader() === false) {
      let remoteServerName = 'unknown-server'
      if (message.data[0].responseTopic) {
        remoteServerName = message.data[0].responseTopic.replace(C.TOPIC.LEADER_PRIVATE, '')
      }

      this._options.logger.log(
                C.LOG_LEVEL.WARN,
                C.EVENT.INVALID_LEADER_REQUEST,
                `server ${remoteServerName} assumes this node '${this._options.serverName}' is the leader`
            )

      return
    }

    if (message.action === C.ACTIONS.LOCK_REQUEST) {
      this._handleRemoteLockRequest(message.data[0])
    } else if (message.action === C.ACTIONS.LOCK_RELEASE) {
      this._handleRemoteLockRelease(message.data[0])
    }
  }

  /**
  * Called when a remote lock request is received
  *
  * @param  {Object} data messageData
  *
  * @private
  * @returns {void}
  */
  _handleRemoteLockRequest (data) {
    this._options.messageConnector.publish(data.responseTopic, {
      topic: data.responseTopic,
      action: C.ACTIONS.LOCK_RESPONSE,
      data: [{
        name: data.name,
        result: this._getLock(data.name)
      }]
    })
  }

  /**
  * Called when a remote lock response is received
  *
  * @param  {Object} data messageData
  *
  * @private
  * @returns {void}
  */
  _handleRemoteLockResponse (data) {
    clearTimeout(this._timeouts[data.name])
    delete this._timeouts[data.name]
    this._responseEventEmitter.emit(data.name, data.result)
  }

  /**
  * Called when a remote node notifies the cluster that a lock has been removed
  *
  * @param  {Object} data messageData
  *
  * @private
  * @returns {void}
  */
  _handleRemoteLockRelease (data) {
    clearTimeout(this._timeouts[data.name])
    delete this._timeouts[data.name]
    delete this._locks[data.name]
  }

  /**
  * Generates a private topic to allow routing requests directly
  * to this node
  *
  * @param  {String} serverName The server of this server
  *
  * @private
  * @returns {String} privateTopic
  */
  _getPrivateTopic (serverName) { // eslint-disable-line
    return C.TOPIC.LEADER_PRIVATE + serverName
  }

  /**
  * Returns true if reserving lock was possible otherwise returns false
  *
  * @param  {String}   name     Name of lock
  *
  * @private
  * @return {boolean}
  */
  _getLock (name) {
    if (this._locks[name] === true) {
      return false
    }

    this._timeouts[name] = utils.setTimeout(
      this._onLockTimeout.bind(this, name),
      this._options.lockTimeout
    )
    this._locks[name] = true
    return true
  }

  /**
  * Called when a lock is no longer required and can be released. This is triggered either by
  * a timeout if a remote release message wasn't received in time or when release was called
  * locally.
  *
  * Important note: Anyone can release a lock. It is assumed that the cluster is trusted
  * so maintaining who has the lock is not required. This may need to change going forward.
  *
  * @param  {String} name Lock name
  *
  * @private
  * @returns {void}
  */
  _releaseLock (name) {
    clearTimeout(this._timeouts[name])
    delete this._timeouts[name]
    delete this._locks[name]
  }

  /**
  * Called when a timeout occurs on a lock that has been reserved for too long
  *
  * @param  {String} name The lock name
  *
  * @private
  * @returns {void}
  */
  _onLockTimeout (name) {
    this._releaseLock(name)
    this._options.logger.log(C.LOG_LEVEL.WARN, C.EVENT.TIMEOUT, `lock ${name} released due to timeout`)
  }

  /**
  * Called when a remote request has timed out, resulting in notifying the client that
  * the lock wasn't able to be reserved
  *
  * @param  {String} name The lock name
  *
  * @private
  * @returns {void}
  */
  _onLockRequestTimeout (name) {
    this._handleRemoteLockResponse({ name, result: false })
    this._options.logger.log(C.LOG_LEVEL.WARN, C.EVENT.TIMEOUT, `request for lock ${name} timed out`)
  }
}

