'use strict'

const FileAuthenticationHandler = require('../src/authentication/file-based-authentication-handler')
const jsYamlLoader = require('../src/config/js-yaml-loader')

module.exports = function (program) {
  program
		.command('hash [password]')
		.description('Generate a hash from a plaintext password using file auth configuration settings')
		.option('-c, --config [file]', 'configuration file containing file auth and hash settings')
		.action(hash)
}

function hash(password) {
  global.deepstreamCLI = this
  const config = jsYamlLoader.loadConfigWithoutInitialisation().config

  if (config.auth.type !== 'file') {
    console.error('Error: Can only use hash with file authentication as auth type')
    process.exit(1)
  }

  if (!config.auth.options.hash) {
    console.error('Error: Can only use hash with file authentication')
    process.exit(1)
  }

  config.auth.options.path = ''

  if (!password) {
    console.error('Error: Must provide password to hash')
    process.exit(1)
  }

	// Mock file loading since a users.yml file is not required
  jsYamlLoader.readAndParseFile = function () {}

  const fileAuthenticationHandler = new FileAuthenticationHandler(config.auth.options)
  fileAuthenticationHandler.createHash(password, (err, hash) => {
    if (err) {
      console.error('Hash could not be created', err)
      process.exit(1)
    }
    console.log('Password hash:', hash)
  })
}

'use strict'
/* eslint-disable valid-typeof */

const crypto = require('crypto')
const jsYamlLoader = require('../config/js-yaml-loader')
const utils = require('../utils/utils')
const EventEmitter = require('events').EventEmitter

const STRING = 'string'
const STRING_CHARSET = 'base64'

/**
 * This authentication handler reads a list of users and their associated password (either
 * hashed or in cleartext ) from a json file. This can be useful to authenticate smaller amounts
 * of clients with static credentials, e.g. backend provider that write to publicly readable records
 *
 * @public
 * @extends {EventEmitter}
 */
module.exports = class FileBasedAuthenticationHandler extends EventEmitter {
  /**
  * Creates the class, reads and validates the users.json file
  *
  * @param   {Object} settings
  * @param   {String} settings.path path to the user file
  * @param   {String} settings.hash the name of a HMAC digest algorithm, a.g. 'sha512'
  * @param   {Int} settings.iterations the amount of times the algorithm should be applied
  * @param   {Int} settings.keyLength the length of the resulting key
  *
  * @constructor
  * @returns {void}
  */
  constructor (settings) {
    super()
    this.isReady = false
    this.type = `file using ${settings.path}`
    this._validateSettings(settings)
    this._settings = settings
    this._base64KeyLength = 4 * Math.ceil(this._settings.keyLength / 3)
    jsYamlLoader.readAndParseFile(settings.path, this._onFileLoad.bind(this))
  }

  /**
  * Main interface. Authenticates incoming connections
  *
  * @param   {Object}   connectionData
  * @param   {Object}   authData
  * @param   {Function} callback
  *
  * @public
  * @implements {PermissionHandler.isValidUser}
  * @returns {void}
  */
  isValidUser (connectionData, authData, callback) {
    if (typeof authData.username !== STRING) {
      callback(false, { clientData: 'missing authentication parameter username' })
      return
    }

    if (typeof authData.password !== STRING) {
      callback(false, { clientData: 'missing authentication parameter password' })
      return
    }

    const userData = this._data[authData.username]

    if (!userData) {
      callback(false)
      return
    }

    if (this._settings.hash) {
      this._isValid(
        authData.password,
        userData.password,
        authData.username,
        userData.serverData,
        userData.clientData,
        callback
      )
    } else if (authData.password === userData.password) {
      callback(true, {
        username: authData.username,
        serverData: typeof userData.serverData === 'undefined' ? null : userData.serverData,
        clientData: typeof userData.clientData === 'undefined' ? null : userData.clientData
      })
    } else {
      callback(false)
    }
  }

  /**
  * Utility method for creating hashes including salts based on
  * the provided parameters
  *
  * @todo  this needs to be exposed to users, maybe via CLI?
  *
  * @param   {String}   password the password that should be hashed
  * @param   {Function} callback will be invoked with error, hash once hashing is completed
  *
  * @public
  * @returns {void}
  */
  createHash (password, callback) {
    const salt = crypto.randomBytes(16).toString(STRING_CHARSET)

    crypto.pbkdf2(
            password,
            salt,
            this._settings.iterations,
            this._settings.keyLength,
            this._settings.hash,
            (err, hash) => {
              callback(err || null, hash.toString(STRING_CHARSET) + salt)
            }
        )
  }

  /**
  * Callback for loaded JSON files. Makes sure that
  * no errors occured and every user has an associated password
  *
  * @param   {Error}     error an error that occured during loading or parsing the file
  * @param   {Object}    data  parsed contents of the file
  *
  * @private
  * @returns {void}
  */
  _onFileLoad (error, data) {
    if (error) {
      this.emit('error', `Error loading file ${this._settings.path}: ${error.toString()}`)
      return
    }

    this._data = data

    if (Object.keys(data).length === 0) {
      this.emit('error', 'no users present in user file')
      return
    }

    for (const username in this._data) {
      if (typeof this._data[username].password !== STRING) {
        this.emit('error', `missing password for ${username}`)
      }
    }

    this.isReady = true
    this.emit('ready')
  }

  /**
  * Called initially to validate the user provided settings
  *
  * @param   {Object} settings
  *
  * @private
  * @returns {void}
  */
  _validateSettings (settings) { // eslint-disable-line
    if (!settings.hash) {
      utils.validateMap(settings, true, {
        path: 'string'
      })
      return
    }

    utils.validateMap(settings, true, {
      path: 'string',
      hash: 'string',
      iterations: 'number',
      keyLength: 'number'
    })

    if (crypto.getHashes().indexOf(settings.hash) === -1) {
      throw new Error(`Unknown Hash ${settings.hash}`)
    }
  }

  /**
  * Extracts hash and salt from a string and runs a hasing function
  * against it
  *
  * @param   {String}   password             the cleartext password the user provided
  * @param   {String}   passwordHashWithSalt the hash+salt combination from the users.json file
  * @param   {String}   username             as provided by user
  * @param   {Object}   serverData           arbitrary authentication data that will be passed on
  *                                          to the permission handler
  * @param   {Object}   clientData           arbitrary authentication data that will be passed on
  *                                          to the client
  * @param   {Function} callback             callback that will be invoked once hash is created
  *
  * @private
  * @returns {void}
  */
  _isValid (password, passwordHashWithSalt, username, serverData, clientData, callback) {
    const expectedHash = passwordHashWithSalt.substr(0, this._base64KeyLength)
    const salt = passwordHashWithSalt.substr(this._base64KeyLength)

    crypto.pbkdf2(
      password,
      salt,
      this._settings.iterations,
      this._settings.keyLength,
      this._settings.hash,
      this._compareHashResult.bind(this, expectedHash, username, serverData, clientData, callback)
    )
  }

  /**
  * Callback once hashing is completed
  *
  * @param   {String}   expectedHash     has as retrieved from users.json
  * @param   {Object}   serverData       arbitrary authentication data that will be passed on to the
  *                                      permission handler
  * @param   {Object}   clientData       arbitrary authentication data that will be passed on to the
  *                                      client
  * @param   {Function} callback         callback from isValidUser
  * @param   {Error}    error            error that occured during hashing
  * @param   {Buffer}   actualHashBuffer the buffer containing the bytes for the new hash
  *
  * @private
  * @returns {void}
  */
  // eslint-disable-next-line
  _compareHashResult (
    expectedHash, username, serverData, clientData, callback, error, actualHashBuffer
  ) {
    if (expectedHash === actualHashBuffer.toString(STRING_CHARSET)) {
      // todo log error
      callback(true, {
        username,
        serverData: serverData || null,
        clientData: clientData || null
      })
    } else {
      callback(false)
    }
  }
}

'use strict'

const fs = require('fs')
const path = require('path')
const os = require('os')
const glob = require('glob')
const jsYamlLoader = require('../src/config/js-yaml-loader')

module.exports = function (program) {
  program
		.command('info')
		.description('print meta information about build and runtime')
		.option('-c, --config [file]', 'configuration file containing lib directory')
		.option('-l, --lib-dir [directory]', 'directory of libraries')
		.action(printMeta)
}

function printMeta() {
  if (!this.libDir) {
    try {
      global.deepstreamCLI = this
      jsYamlLoader.loadConfigWithoutInitialisation()
      this.libDir = global.deepstreamLibDir
    } catch (e) {
      console.log(e)
      console.error('Please provide a libDir or a configFile to provide the relevant install information')
      process.exit(1)
    }
  }

  let meta
  try {
    meta = require('../meta.json')
  } catch (err) {
		// if deepstream is not installed as binary (source or npm)
    pkg = require('../package.json')
    meta = {
      deepstreamVersion: pkg.version,
      ref: pkg.gitHead || pkg._resolved || 'N/A',
      buildTime: 'N/A'
    }
  }
  meta.platform = os.platform()
  meta.arch = os.arch()
  meta.nodeVersion = process.version
  fetchLibs(this.libDir, meta)
  console.log(JSON.stringify(meta, null, 2))
}

function fetchLibs(libDir, meta) {
  const directory = libDir || 'lib'
  const files = glob.sync(path.join(directory, '*', 'package.json'))
  meta.libs = files.map((filePath) => {
    const pkg = fs.readFileSync(filePath, 'utf8')
    const object = JSON.parse(pkg)
    return `${object.name}:${object.version}`
  })
}

'use strict'

const installer = require('./installer')
const jsYamlLoader = require('../src/config/js-yaml-loader')

module.exports = function (program) {
  program
		.command('install')
		.description('install connectors')
		.usage('<type> <name>[:version]')
		.option('-c, --config [file]', 'configuration file containing the lib directory')
		.option('-l, --lib-dir [directory]', 'directory where to extract the connector')
		.option('--verbose', 'more debug output')
		.option('--quiet', 'no output')
		.on('--help', () => {
  console.log('  Examples:')
  console.log('')
  console.log('    $ deepstream install cache redis')
  console.log('    $ deepstream install storage rethinkdb:0.1.0')
  console.log('')
  console.log('    list of available connectors: https://deepstream.io/download')
  console.log('')
})
		.action(action)
}

function action(type, nameAndVersion) {
  const installArgs = Array.prototype.slice.call(arguments, 0, arguments.length - 1)
  if (installArgs.length < 2) {
    this.help()
  }

  if (!this.libDir) {
    try {
      global.deepstreamCLI = this
      jsYamlLoader.loadConfigWithoutInitialisation()
      this.libDir = global.deepstreamLibDir
    } catch (e) {
      console.error('Please provide a libDir or a configFile to provide the relevant install information')
      process.exit(1)
    }
  }

	/*
	 * Syntax:
	 * TYPE NAME:VERSION
	 * version is optional
	 */
  type = installArgs[0]
  nameAndVersion = installArgs[1].split(':')
  const name = nameAndVersion[0]
  const version = nameAndVersion[1]

  if (this.quiet) {
    process.env.QUIET = 1
  } else if (this.verbose) {
    process.env.VERBOSE = 1
  }

  installer({
    type,
    name,
    version,
    dir: this.libDir
  }, (err) => {
    if (err) {
      console.error(err.toString().red)
      process.exit(1)
    }
  })
}

'use strict'

const colors = require('colors')
const needle = require('needle')
const fs = require('fs')
const path = require('path')
const os = require('os')
const AdmZip = require('adm-zip')
const execSync = require('child_process').execSync
const mkdirp = require('mkdirp')

const CONFIG_EXAMPLE_FILE = 'example-config.yml'
const SYSTEM = {
  linux: 'linux',
  darwin: 'mac',
  win32: 'windows'
}
const platform = SYSTEM[os.platform()]

const getWebUrl = function (repo) {
  return `https://github.com/deepstreamIO/${repo}/releases`
}

/**
 * Download a release from GitHub releases API with with the deepstream connector
 * name convention: deepstreamIO/deepstream.io-TYPE-NAME
 *
 * @param  {array}    releases JSON array of the GitHub REST API for list releases
 * @param  {string}   type Connector type: {cache|message|storage}
 * @param  {string}   name Name of the connector
 * @param  {string}   version Version of the connector (optional)
 * @param  {string}   outputDir Path to directory where to install and extract the connector
 * @callback callback
 * @param {error} error
 * @param {Object} {{archive: String, name: String, version: String}}
 * @return {void}
 */
const downloadRelease = function (releases, type, name, version, outputDir, callback) {
  outputDir = outputDir == null ? 'lib' : outputDir
  const repo = `deepstream.io-${type}-${name}`
  const filteredReleases = releases.filter((item) => {
    if (version == null) {
      return true
    }
    return item.tag_name === version || item.tag_name === `v${version}`
  })
  if (filteredReleases.length === 0) {
    return callback(new Error(`${repo} ${version} not found, see ${getWebUrl(repo)}`))
  }
  const release = filteredReleases[0]
  version = version == null ? release.tag_name : version
  const releaseForMachine = release.assets.filter(item => item.name.indexOf(platform) !== -1)
  if (releaseForMachine.length === 0) {
    return callback(new Error(`Release for your platform not found, see ${getWebUrl(repo)}`))
  }

  const downloadUrl = releaseForMachine[0].browser_download_url
  const extension = path.extname(downloadUrl)
  const basename = path.basename(downloadUrl, extension).replace('deepstream.io-', '')
  const urlBase = 'https://github.com'
  const urlPath = downloadUrl.substr(urlBase.length)
  const basenameWithVersion = `${basename}-${version}${extension}`
  const outputFile = path.join(outputDir, basenameWithVersion)
  mkdirp.sync(outputDir)

  if (process.env.VERBOSE) {
    console.log(`Downloading version ${version}`)
  }
  const outStream = fs.createWriteStream(outputFile)
  downloadArchive(urlPath, outStream, (err) => {
    if (err) {
      return callback(err)
    }
    callback(null, {
      archive: outputFile,
      name: repo,
      version
    })
  })
}

/**
 * Downloads an archive usually zip or tar.gz from a URL which comes from the GitHub
 * release API.
 *
 * @param  {String}   urlPath URL where to download the archive
 * @param  {Stream}   writeable output stream to save the archive
 * @param  {Function} callback Callback (err)
 * @return {void}
 */
const downloadArchive = function (urlPath, outStream, callback) {
  needle.get(`https://github.com${urlPath}`, {
    follow_max: 5,
    headers: { 'User-Agent': 'nodejs-client' }
  }, (error, response) => {
    if (error) {
      return callback(error)
    }
    outStream.write(response.body)
    outStream.end()
    if (process.env.VERBOSE) {
      process.stdout.clearLine()
      process.stdout.cursorTo(0)
      process.stdout.write('Download complete' + '\n')
    }
    return callback()
  })
}

/**
 * Fetch a JSON array from GitHub Release API which contains all meta data
 * for a specific reposotiry.
 *
 * @param  {String}   type Connector type: {cache|message|storage}
 * @param  {String}   name Name of the connector
 * @callback callback
 * @param {error} error
 * @param {Object} JSON
 * @return {void}
 */
const fetchReleases = function (type, name, callback) {
  const repo = `deepstream.io-${type}-${name}`
  const urlPath = `/repos/deepstreamIO/${repo}/releases`
  if (process.env.VERBOSE) {
    console.log(`searching for ${repo}`)
  }
  needle.get(`https://api.github.com${urlPath}`, {
    headers: { 'User-Agent': 'nodejs-client' },
  }, (error, response) => {
    if (error) {
      return callback(error)
    }
    if (response.statusCode === 404) {
      return callback(new Error('Not found, see available connectors on https://deepstream.io/download'))
    }
    if (response.statusCode == 403) {
			// API rate limit
      return callback(new Error(response.body.message))
    }
    callback(null, response.body)
  })
}

/**
 * Fetch a JSON array from GitHub Release API which contains all meta data
 * for a specific reposotiry.
 *
 * @param  {Object}   data Contains archive: contains the archive path, name: contains the name of the  connector
 * @param  {String}   platform The current platform (windows, linux or mac)
 * @return {String}   outPath The directory where the connector was extracted to
 */
const extract = function (data, platform) {
  const archivePath = data.archive
  const outputParent = path.dirname(archivePath)
  const outPath = path.join(outputParent, data.name)
  try {
    if (platform === 'linux') {
      execSync(`mkdir -p ${outPath} && tar -xzf ${archivePath} -C ${outPath}`)
    } else {
      extractZip(archivePath, outPath)
    }
  } catch (err) {
    if (process.env.VERBOSE) {
      console.error(err)
    }
    throw new Error('Could not extract archive')
  }
  if (!process.env.QUIET) {
    console.log(colors.green(`${data.name} ${data.version} was installed to ${outputParent}`))
  }
  return outPath
}

/**
 * Extracts an archive to a specific directory
 *
 * @param  {String}   archivePath
 * @param {String}   outputDirectory
 * @return {void}
 */
const extractZip = function (archivePath, outputDirectory) {
  const zip = new AdmZip(archivePath)
  zip.extractAllTo(outputDirectory, true)
}

/**
 * Prints out the config snippet of a extract connector to the stdout.
 * Output is indented and grey colored.
 *
 * @param  {String}   directory where to lookup for CONFIG_EXAMPLE_FILE
 * @return {void}
 */
const showConfig = function (directory) {
  try {
    const content = fs.readFileSync(path.join(directory, CONFIG_EXAMPLE_FILE), 'utf8')
    if (process.env.VERBOSE) {
      console.log('You need to configure the connector in your deepstream configuration file')
    }
    if (!process.env.QUIET) {
      console.log(`Example configuration:\n${colors.grey(content)}`)
    }
  } catch (err) {
    if (!process.env.QUIET) {
      console.log('Example configuration not found')
    }
  }
}

/**
 * Download, extract and show configuration for deepstream connector
 *
 * @param  {Object}   opts {{type: String, name: string, version: String, dir: String}}
 * @param  {Function} callback Callback (err)
 * @return {void}
 */
module.exports = function (opts, callback) {
  if (opts.type === 'message') {
    opts.type = 'msg'
  }
  fetchReleases(opts.type, opts.name, (error, releases) => {
    if (error) {
      return callback(error)
    }
    downloadRelease(releases, opts.type, opts.name, opts.version, opts.dir, (error, result) => {
      if (error) {
        return callback(error)
      }
      try {
        const extractedDirectory = extract(result, platform)
        showConfig(extractedDirectory)
        callback()
      } catch (error) {
        callback(error)
      }
    })
  })
}

'use strict'

const colors = require('colors')
const fs = require('fs')
const os = require('os')
const path = require('path')
const child_process = require('child_process')
const C = require('../src/constants/constants.js')
const pidHelper = require('./pid-helper')

module.exports = function (program) {
  program
		.command('start')
		.description('start a deepstream server')

		.option('-c, --config [file]', 'configuration file, parent directory will be used as prefix for other config files')
		.option('-l, --lib-dir [directory]', 'path where to lookup for plugins like connectors and logger')
		.option('-d, --detach', 'detach the deepstream server process')

		.option('--server-name <name>', 'Each server within a cluster needs a unique name')
		.option('--host <host>', 'host for the HTTP/websocket server')
		.option('--port <port>', 'port for the HTTP/websocket server', parseInteger.bind(null, '--port'))
		.option('--disable-auth', 'Force deepstream to use "none" auth type')
		.option('--disable-permissions', 'Force deepstream to use "none" permissions')
		.option('--log-level <level>', 'Log messages with this level and above', parseLogLevel)
		.option('--colors [true|false]', 'Enable or disable logging with colors', parseBoolean.bind(null, '--colors'))
		.action(action)
}

function action() {
  global.deepstreamCLI = this

  if (this.detach) {
		// --detach is not supported for windows
    if (os.platform() === 'win32') {
      console.error('detached mode not supported on windows')
      process.exit(1)
    }
		// proxy arguments from commander to the spawing process
    const args = []
    if (this.config != null) {
      args.push('--config')
      args.push(this.config)
    }
    if (this.libDir != null) {
      args.push('--lib-dir')
      args.push(this.libDir)
    }
		// TODO: need to pass other options as well, which are accessable directly as properties of this
		//       but need to transform camelCase back to kebabCase, like disableAuth

		// ensure there is no pid file with a running process
    pidHelper.ensureNotRunning((err) => {
      if (err) {
        return pidHelper.exit(err)
      }
      const child = child_process.spawn(path.join(__dirname, 'deepstream'), ['start'].concat(args), {
        detached: true,
        stdio: ['ignore']
      })
      const WAIT_FOR_ERRORS = 3000
			// register handler if the child process will fail within WAIT_FOR_ERRORS period
      child.on('close', detachErrorHandler)
      child.on('exit', detachErrorHandler)
      child.unref()
			// wait, maybe ther is an error during startup
      setTimeout(() => {
        console.log(`process was detached with pid ${child.pid}`)
        process.exit(0)
      }, WAIT_FOR_ERRORS)
    })
  } else {
		// non-detach case
    const Deepstream = require('../src/deepstream.io.js')
    try {
      process.on('uncaughtException', pidHelper.exit)
      const ds = new Deepstream(null)
      ds.on('started', () => {
        pidHelper.save(process.pid)
      })
      ds.start()
    } catch (err) {
      console.error(err.toString())
      console.trace()
      process.exit(1)
    }
    process
			.removeAllListeners('SIGINT').on('SIGINT', pidHelper.exit)
			.removeAllListeners('SIGTERM').on('SIGTERM', pidHelper.exit)
  }
}

function detachErrorHandler() {
  console.error('Error during detaching the deepstream process, see logs or run without --detach'.red)
  process.exit(1)
}

/**
* Used by commander to parse the log level and and fails if invalid
* value is passed in
* @private
*/
function parseLogLevel(logLevel) {
  if (!/debug|info|warn|error|off/i.test(logLevel)) {
    console.error('Log level must be one of the following (debug|info|warn|error|off)')
    process.exit(1)
  }
  return logLevel.toUpperCase()
}

/**
* Used by commander to parse numbers and fails if invalid
* value is passed in
* @private
*/
function parseInteger(name, port) {
  const portNumber = Number(port)
  if (!portNumber) {
    console.error(`Provided ${name} must be an integer`)
    process.exit(1)
  }
  return portNumber
}

/**
* Used by commander to parse boolean and fails if invalid
* value is passed in
* @private
*/
function parseBoolean(name, enabled) {
  let isEnabled
  if (typeof enabled === 'undefined' || enabled === 'true') {
    isEnabled = true
  } else if (typeof enabled !== 'undefined' && enabled === 'false') {
    isEnabled = false
  } else {
    console.error(`Invalid argument for ${name}, please provide true or false`)
    process.exit(1)
  }
  return isEnabled
}

'use strict'

const colors = require('colors')
const fs = require('fs')
const path = require('path')
const os = require('os')

const DEFAULT_PID_FILE = path.join(os.tmpdir(), 'deepstream.pid')
const PID_FILE = process.env.DEEPSTREAM_PID_FILE || DEFAULT_PID_FILE

/**
 * Save a JSON (pid and timestamp) object to the temp directofy of the system
 *
 * @param  {String|Number} pid Connector type: {cache|message|storage}
 * @callback callback
 * @param {error} error
 * @return {void}
 */
const save = function (pid, callback) {
  if (callback == null) {
    callback = function () {}
  }
  if (pid == null) {
    return callback(new Error('pid is null'))
  }
  const now = new Date()
  const data = {
    pid,
    timestamp: now.getTime(),
    time: now.toString()
  }
  fs.writeFile(PID_FILE, JSON.stringify(data), (err) => {
    callback(err)
  })
}

/**
 * Try to read the pid file from the temp directory
 *
 * @callback callback
 * @param {error} error
 * @return {void}
 */
const read = function (callback) {
  fs.readFile(PID_FILE, 'utf8', (err, content) => {
    if (err) {
      return callback(err)
    }
    try {
      return callback(null, JSON.parse(content))
    } catch (err) {
      return callback(err)
    }
  })
}

/**
 * Ensure that there is no process running with the PID in the pid file,
 * otherwise throw an error.
 *
 * @callback callback
 * @param {error} error
 * @return {void}
 */
const ensureNotRunning = function (callback) {
  read((err, data) => {
    if (err) {
      if (err.code === 'ENOENT') {
        return callback()
      }
      return callback(err)
    }
    try {
      const pid = data.pid
      if (isRunning(pid)) {
        throw new Error(`A deepstream server is already running with PID ${pid}, see ${PID_FILE}`)
      } else {
				// pid file is there but process is not running anymore
        return callback()
      }
    } catch (err) {
      return callback(err)
    }
  })
}

/**
 * Exit the current process with a status code 0.
 * If you pass an error as argument, it will exit with 1 and the error will
 * printed to the stderr.
 *
 * @param  {Error} err Optional error object
 * @return {void}
 */
const exit = function (err) {
  if (err instanceof Error) {
    console.error(colors.red(err.toString()))
    console.error(err)
    process.exit(1)
  } else {
    remove(() => {
      process.exit(0)
    })
  }
}

/**
 * Try to find a process with the given pid and returns true if it is running.
 * Otherwise false.
 *
 * @param  {String|Number} pid
 * @return {Boolean}
 */
const isRunning = function (pid) {
  try {
    return process.kill(pid, 0)
  } catch (e) {
    return e.code === 'EPERM'
  }
}

/**
 * Delete the pid file
 *
 * @callback callback Optional callback
 * @param {error} error
 * @return {void}
 */
const remove = function (callback) {
  fs.unlink(PID_FILE, (err) => {
    if (callback != null) {
      return callback(err)
    }
    console.error(err)
  })
}

/**
 * Try to find a process from the pid file and kills it.
 * Prints out if a process or pid file was found or not for how long it was running.
 *
 * @return {void}
 */
const stop = function () {
  read((err, data) => {
    if (err) {
      return console.log('no pid file')
    } else {
      try {
        process.kill(data.pid)
      } catch (err) {
        return console.log(`No process found for PID ${data.pid}`)
      }
      const uptime = new Date().getTime() - data.timestamp
      console.log(`Deepstream was running for ${uptime / 1000} seconds`)
    }
  })
}

module.exports = {
  save,
  isRunning,
  ensureNotRunning,
  exit,
  stop,
  read,
  PID_FILE
}

