function doLevel(nodePath) {
  var pkg = require(path.join(nodePath, 'package.json'))
  if (topPkg.name !== pkg.name && moduleNames.indexOf(pkg.name) === -1) {
    return
  }
  var nodeModulesPath = path.join(nodePath, 'node_modules')
