'use strict'

const pathParser = require('./path-parser')
const ruleParser = require('./rule-parser')

/**
 * Compiles a pre-validated config into a format that allows for quicker access
 * and execution
 *
 * @param   {Object} config parsed and validated permission config
 *
 * @public
 * @returns {Object} compiledConfig
 */
exports.compile = function (config) {
  const compiledConfig = {}
  let compiledRuleset
  let section
  let path

  for (section in config) {
    compiledConfig[section] = []

    for (path in config[section]) {
      compiledRuleset = compileRuleset(path, config[section][path])
      compiledConfig[section].push(compiledRuleset)
    }
  }

  return compiledConfig
}

/**
 * Compiles an individual ruleset
 *
 * @param   {String} path
 * @param   {Object} rules
 *
 * @private
 * @returns {Object} compiledRuleset
 */
function compileRuleset (path, rules) {
  const ruleset = pathParser.parse(path)

  ruleset.rules = {}

  for (const ruleType in rules) {
    ruleset.rules[ruleType] = ruleParser.parse(
      rules[ruleType], ruleset.variables
    )
  }

  return ruleset
}

'use strict'

const WILDCARD_REGEXP = /\*/g
const WILDCARD_STRING = '.*'
const VARIABLE_REGEXP = /(\$[a-zA-Z0-9]+)/g
const VARIABLE_STRING = '([^/]+)'
const INVALID_VARIABLE_REGEXP = /(\$[^a-zA-Z0-9])/

/**
 * Checks a path for type and basic syntax errors
 *
 * @param   {String} path The path as specified in permission.json
 *
 * @public
 * @returns {String|Boolean} true if path is valid, string error message if not
 */
exports.validate = function (path) {
  if (typeof path !== 'string') {
    return 'path must be a string'
  }

  if (path.length === 0) {
    return 'path can\'t be empty'
  }

  if (path[0] === '/') {
    return 'path can\'t start with /'
  }

  const invalidVariableNames = path.match(INVALID_VARIABLE_REGEXP)

  if (invalidVariableNames !== null) {
    return `invalid variable name ${invalidVariableNames[0]}`
  }

  return true
}

/**
 * Parses a path and returns a regexp matcher with capture groups for
 * variable names and a list of variable names in the same order.
 * The path is assumed to be valid when its passed to this method
 *
 * @param   {String} path The path as specified in permission.json
 *
 * @public
 * @returns {Object} { variables: <String variableNames>[], regexp: <RegExp>}
 */
exports.parse = function (path) {
  const variables = []
  let regExp = path.replace(WILDCARD_REGEXP, WILDCARD_STRING)

  regExp = regExp.replace(VARIABLE_REGEXP, (variableName) => {
    variables.push(variableName)
    return VARIABLE_STRING
  })

  return {
    variables,
    path,
    regexp: new RegExp(`^${regExp}$`)
  }
}

'use strict'

const rulesMap = require('./rules-map')

// TODO: any of these are fine inside a string or comment context...
const FUNCTION_REGEXP = /([\w]+(?:['"`]\])?)\s*\(/g
const USER_FUNCTION_REGEXP = /[^\w$]function[^\w$]|=>/g
const NEW_REGEXP = /(^|[^\w$])new[^\w$]/
const OLD_DATA_REGEXP = /(^|[^\w~])oldData[^\w~]/
const DATA_REGEXP = /(^|[^\w.~])data($|[^\w~])/

const SUPPORTED_FUNCTIONS = [
  '_',
  'startsWith',
  'endsWith',
  'includes',
  'indexOf',
  'match',
  'toUpperCase',
  'toLowerCase',
  'trim'
]

/**
 * Validates a rule. Makes sure that the rule is either a boolean or a string,
 * that it doesn't contain the new keyword or unsupported function invocations
 * and that it can be compiled into a javascript function
 *
 * @param   {String|Boolean} rule the rule as read from permissions.json
 * @param   {String} section record, event or rpc
 * @param   {String} type read, write, publish, subscribe etc...
 *
 * @public
 * @returns {Boolean} isValid
 */
exports.validate = function (rule, section, type) {
  if (typeof rule === 'boolean') {
    return true
  }

  if (typeof rule !== 'string') {
    return 'rule must be a string'
  }

  if (rule.length === 0) {
    return 'rule can\'t be empty'
  }

  if (rule.match(NEW_REGEXP)) {
    return 'rule can\'t contain the new keyword'
  }

  if (rule.match(USER_FUNCTION_REGEXP)) {
    return 'rule can\'t contain user functions'
  }

  const functions = rule.match(FUNCTION_REGEXP)
  let functionName
  let i

  // TODO _ cross references are only supported for section record
  if (functions) {
    for (i = 0; i < functions.length; i++) {
      functionName = functions[i].replace(/\s*\($/, '')
      if (SUPPORTED_FUNCTIONS.indexOf(functionName) === -1) {
        return `function ${functionName} is not supported`
      }
    }
  }

  try {
    // eslint-disable-next-line
    new Function(rule)
  } catch (e) {
    return e.toString()
  }

  if (!!rule.match(OLD_DATA_REGEXP) && !rulesMap.supportsOldData(type)) {
    return `rule ${type} for ${section} does not support oldData`
  }

  if (!!rule.match(DATA_REGEXP) && !rulesMap.supportsData(type)) {
    return `rule ${type} for ${section} does not support data`
  }

  return true
}

/**
 * Cross References:
 *
 * Cross references are denoted with an underscore function _()
 * They can take path variables: _($someId)
 * variables from data: _(data.someValue)
 * or strings: _('user/egon')
 *
 * @param   {[type]} rule      [description]
 * @param   {[type]} variables [description]
 *
 * @returns {[type]}
 */
exports.parse = function (rule, variables) {
  if (rule === true || rule === false) {
    return {
      fn: rule === true ? function () { return true } : function () { return false },
      hasOldData: false,
      hasData: false
    }
  }
  const ruleObj = {}
  const args = ['_', 'user', 'data', 'oldData', 'now', 'action'].concat(variables)
  args.push(`return ${rule};`)

  ruleObj.fn = Function.apply(this, args)
  ruleObj.hasOldData = !!rule.match(OLD_DATA_REGEXP)
  ruleObj.hasData = !!rule.match(DATA_REGEXP)

  return ruleObj
}

'use strict'

const C = require('../constants/constants')
const utils = require('../utils/utils')

const actionToKey = utils.reverseMap(C.ACTIONS)
const RULES_MAP = {}
const RULE_TYPES = {}

/**
 * Different rule types support different features. Generally, all rules can
 * use cross referencing _() to reference records, but only record writes, incoming events
 * or RPC requests carry data and only existing records have a concept of oldData
 *
 * @type {Object}
 */
RULE_TYPES.CREATE = { name: 'create', data: false, oldData: false }
RULE_TYPES.READ = { name: 'read', data: false, oldData: true }
RULE_TYPES.WRITE = { name: 'write', data: true, oldData: true }
RULE_TYPES.DELETE = { name: 'delete', data: false, oldData: true }
RULE_TYPES.LISTEN = { name: 'listen', data: false, oldData: false }
RULE_TYPES.PUBLISH = { name: 'publish', data: true, oldData: false }
RULE_TYPES.SUBSCRIBE = { name: 'subscribe', data: true, oldData: false }
RULE_TYPES.PROVIDE = { name: 'provide', data: false, oldData: false }
RULE_TYPES.REQUEST = { name: 'request', data: true, oldData: false }
RULE_TYPES.ALLOW = { name: 'allow', data: false, oldData: false }

/**
 * This class maps topic / action combinations to applicable
 * rules. It combines actions of a similar character (e.g. READ,
 * SNAPSHOT, HAS) into high level permissions (e.g. read)
 *
 * Lower level permissioning on a per action basis can still be achieved
 * by virtue of using the action variable within the rule, e.g.
 *
 * {
 *    //allow read, but not listen
 *    'read': 'user.id === $userId && action !== LISTEN'
 * }
 */
RULES_MAP[C.TOPIC.RECORD] = {}
RULES_MAP[C.TOPIC.RECORD].section = 'record'
RULES_MAP[C.TOPIC.RECORD].actions = {}
RULES_MAP[C.TOPIC.RECORD].actions[C.ACTIONS.READ] = RULE_TYPES.READ
RULES_MAP[C.TOPIC.RECORD].actions[C.ACTIONS.HAS] = RULE_TYPES.READ
RULES_MAP[C.TOPIC.RECORD].actions[C.ACTIONS.SNAPSHOT] = RULE_TYPES.READ
RULES_MAP[C.TOPIC.RECORD].actions[C.ACTIONS.HEAD] = RULE_TYPES.READ
RULES_MAP[C.TOPIC.RECORD].actions[C.ACTIONS.LISTEN] = RULE_TYPES.LISTEN
RULES_MAP[C.TOPIC.RECORD].actions[C.ACTIONS.LISTEN_SNAPSHOT] = RULE_TYPES.LISTEN
RULES_MAP[C.TOPIC.RECORD].actions[C.ACTIONS.CREATE] = RULE_TYPES.CREATE
RULES_MAP[C.TOPIC.RECORD].actions[C.ACTIONS.UPDATE] = RULE_TYPES.WRITE
RULES_MAP[C.TOPIC.RECORD].actions[C.ACTIONS.PATCH] = RULE_TYPES.WRITE
RULES_MAP[C.TOPIC.RECORD].actions[C.ACTIONS.DELETE] = RULE_TYPES.DELETE

RULES_MAP[C.TOPIC.EVENT] = {}
RULES_MAP[C.TOPIC.EVENT].section = 'event'
RULES_MAP[C.TOPIC.EVENT].actions = {}
RULES_MAP[C.TOPIC.EVENT].actions[C.ACTIONS.LISTEN] = RULE_TYPES.LISTEN
RULES_MAP[C.TOPIC.EVENT].actions[C.ACTIONS.SUBSCRIBE] = RULE_TYPES.SUBSCRIBE
RULES_MAP[C.TOPIC.EVENT].actions[C.ACTIONS.EVENT] = RULE_TYPES.PUBLISH

RULES_MAP[C.TOPIC.RPC] = {}
RULES_MAP[C.TOPIC.RPC].section = 'rpc'
RULES_MAP[C.TOPIC.RPC].actions = {}
RULES_MAP[C.TOPIC.RPC].actions[C.ACTIONS.SUBSCRIBE] = RULE_TYPES.PROVIDE
RULES_MAP[C.TOPIC.RPC].actions[C.ACTIONS.REQUEST] = RULE_TYPES.REQUEST

RULES_MAP[C.TOPIC.PRESENCE] = {}
RULES_MAP[C.TOPIC.PRESENCE].section = 'presence'
RULES_MAP[C.TOPIC.PRESENCE].actions = {}
RULES_MAP[C.TOPIC.PRESENCE].actions[C.ACTIONS.SUBSCRIBE] = RULE_TYPES.ALLOW
RULES_MAP[C.TOPIC.PRESENCE].actions[C.ACTIONS.QUERY] = RULE_TYPES.ALLOW


/**
 * Returns a map of applicable rule-types for a topic
 * action combination
 *
 * @param   {Message} message a parsed and validated deepstream.io message
 *
 * @public
 * @returns {Object} ruleTypes a map with <String> section, <Array> rules, <String> action
 */
exports.getRulesForMessage = function (message) {
  if (RULES_MAP[message.topic] === undefined) {
    return null
  }

  if (RULES_MAP[message.topic].actions[message.action] === undefined) {
    return null
  }

  return {
    section: RULES_MAP[message.topic].section,
    type: RULES_MAP[message.topic].actions[message.action].name,
    action: actionToKey[message.action]
  }
}

/**
 * Returns true if a given rule supports references to incoming data
 *
 * @param   {String} type one of RULE_TYPES
 *
 * @public
 * @returns {Boolean}
 */
exports.supportsData = function (type) {
  return RULE_TYPES[type.toUpperCase()].data
}

/**
 * Returns true if a given rule supports references to existing data
 *
 * @param   {String} type one of RULE_TYPES
 *
 * @public
 * @returns {Boolean}
 */
exports.supportsOldData = function (type) {
  return RULE_TYPES[type.toUpperCase()].oldData
}

'use strict'
/* eslint-disable valid-typeof */
const configValidator = require('./config-validator')
const configCompiler = require('./config-compiler')
const rulesMap = require('./rules-map')
const RuleApplication = require('./rule-application')
const RuleCache = require('./rule-cache')
const events = require('events')
const utils = require('util')
const jsYamlLoader = require('../config/js-yaml-loader')

const STRING = 'string'
const UNDEFINED = 'undefined'

/**
 * A permission handler that reads a rules config YAML or JSON, validates
 * its contents, compiles it and executes the permissions that it contains
 * against every incoming message.
 *
 * This is the standard permission handler that deepstream exposes, in conjunction
 * with the default permission.yml it allows everything, but at the same time provides
 * a convenient starting point for permission declarations.
 *
 * @author deepstreamHub GmbH
 * @license [https://github.com/deepstreamIO/deepstream.io/blob/master/LICENSE] MIT
 *
 * @constructor
 * @extends {EventEmitter}
 *
 * @param {Object} options deepstream options
 * @param {[Object]} config  Optional config. If no config is provided, the
 *                           ConfigPermissionHandler will attempt
 *                           to load it from the path provided in options.path.
 */
const ConfigPermissionHandler = function (options, config) {
  this._ruleCache = new RuleCache(options)
  this._options = options
  this._permissionOptions = options.permission.options
  this._config = null
  this._recordHandler = null
  this.isReady = false
  this.type = `valve permissions loaded from ${this._permissionOptions.path}`
  this._optionsValid = true

  const maxRuleIterations = options.permission.options.maxRuleIterations
  if (maxRuleIterations !== undefined && maxRuleIterations < 1) {
    this._optionsValid = false
    process.nextTick(() => this.emit('error', 'Maximum rule iteration has to be at least one '))
  } else if (config) {
    this.useConfig(config)
  }
}

utils.inherits(ConfigPermissionHandler, events.EventEmitter)

/**
 * Will be invoked with the initialised recordHandler instance by deepstream.io
 *
 * @param {RecordHandler} recordHandler
 *
 * @public
 * @returns {void}
 */
ConfigPermissionHandler.prototype.setRecordHandler = function (recordHandler) {
  this._recordHandler = recordHandler
}

/**
 * Will be called by the dependency initialiser once server.start() is called.
 * This gives users a chance to change the path using server.set()
 * first
 *
 * @public
 * @returns {void}
 */
ConfigPermissionHandler.prototype.init = function () {
  if (this._config === null && this._optionsValid) {
    this.loadConfig(this._permissionOptions.path)
  }
}

/**
 * Load a configuration file. This will either load a configuration file for the first time at
 * startup or reload the configuration at runtime
 *
 * CLI loadConfig <path>
 *
 * @todo expose this method via the command line interface
 *
 * @param   {String} path the filepath of the permission.yml file
 *
 * @public
 * @returns {void}
 */
ConfigPermissionHandler.prototype.loadConfig = function (filePath) {
  jsYamlLoader.readAndParseFile(filePath, this._onConfigLoaded.bind(this, filePath))
}

/**
 * Validates and compiles a loaded config. This can be called as the result
 * of a config being passed to the permissionHandler upon initialisation,
 * as a result of loadConfig or at runtime
 *
 * CLI useConfig <config>
 *
 * @todo expose this method via the command line interface
 *
 * @param   {Object} config deepstream permissionConfig
 *
 * @public
 * @returns {void}
 */
ConfigPermissionHandler.prototype.useConfig = function (config) {
  const validationResult = configValidator.validate(config)

  if (validationResult !== true) {
    this.emit('error', `invalid permission config - ${validationResult}`)
    return
  }

  this._config = configCompiler.compile(config)
  this._ruleCache.reset()
  this._ready()
}

/**
 * Implements the permissionHandler's canPerformAction interface
 * method
 *
 * This is the main entry point for permissionOperations and will
 * be called for every incoming message. This method executes four steps
 *
 * - Check if the incoming message conforms to basic specs
 * - Check if the incoming message requires permissions
 * - Load the applicable permissions
 * - Apply them
 *
 * @param   {String}   username the name of the connected user, as specified in isValidUser
 * @param   {Object}   message  a parsed deepstream message
 * @param   {Function} callback the callback to provide the result
 * @param   {[Object]}   authData additional optional authData as passed to isValidUser
 *
 * @public
 * @interface
 * @returns {void}
 */
ConfigPermissionHandler.prototype.canPerformAction = function (
  username, message, callback, authData) {
  if (typeof message.data[0] !== STRING) {
    callback('invalid message', false)
    return
  }

  const ruleSpecification = rulesMap.getRulesForMessage(message)
  const name = message.data[0]

  if (ruleSpecification === null) {
    callback(null, true)
    return
  }

  const ruleData = this._getCompiledRulesForName(name, ruleSpecification)

  // eslint-disable-next-line
  new RuleApplication({
    recordHandler: this._recordHandler,
    username,
    authData,
    path: ruleData,
    ruleSpecification,
    message,
    action: ruleSpecification.action,
    regexp: ruleData.regexp,
    rule: ruleData.rule,
    name,
    callback,
    logger: this._options.logger,
    permissionOptions: this._permissionOptions,
    options: this._options
  })
}

/**
 * Evaluates the rules within a section and returns the matching rule for a path.
 * Takes basic specificity (as deduced from the path length) into account and
 * caches frequently used rules for faster access
 *
 * @param   {String} name              the name of the record, event or rpc the rule applies to
 * @param   {Object} ruleSpecification a ruleSpecification as provided by the rules-map
 *
 * @private
 * @returns {Object} compiled rules
 */
ConfigPermissionHandler.prototype._getCompiledRulesForName = function (name, ruleSpecification) {
  if (this._ruleCache.has(ruleSpecification.section, name, ruleSpecification.type)) {
    return this._ruleCache.get(ruleSpecification.section, name, ruleSpecification.type)
  }

  const section = this._config[ruleSpecification.section]
  let i = 0
  let pathLength = 0
  let result = null

  for (i; i < section.length; i++) {
    if (
      typeof section[i].rules[ruleSpecification.type] !== UNDEFINED &&
      section[i].path.length >= pathLength &&
      section[i].regexp.test(name)
    ) {
      pathLength = section[i].path.length
      result = {
        path: section[i].path,
        regexp: section[i].regexp,
        rule: section[i].rules[ruleSpecification.type]
      }
    }
  }

  if (result) {
    this._ruleCache.set(ruleSpecification.section, name, ruleSpecification.type, result)
  }

  return result
}

/**
 * Callback for loadConfig. Parses the incoming configuration string and forwards
 * it to useConfig if no errors occured
 *
 * @param   {Error} loadError a FileSystem Error that occured during the loading of the file
 * @param   {String} data     the content of the permission.yml file as utf-8 encoded string
 *
 * @private
 * @returns {void}
 */
ConfigPermissionHandler.prototype._onConfigLoaded = function (filePath, loadError, config) {
  if (loadError) {
    this.emit('error', `error while loading config: ${loadError.toString()}`)
    return
  }
  this.emit('config-loaded', filePath)
  this.useConfig(config)
}

/**
 * Sets this permissionHandler to ready. Occurs once the config has been successfully loaded,
 * parsed and compiled
 *
 * @private
 * @returns {void}
 */
ConfigPermissionHandler.prototype._ready = function () {
  if (this.isReady === false) {
    this.isReady = true
    this.emit('ready')
  }
}

module.exports = ConfigPermissionHandler

'use strict'

const pathParser = require('./path-parser')
const ruleParser = require('./rule-parser')
const SCHEMA = require('./config-schema')

const validationSteps = {}

/**
 * Validates a configuration object. This method runs through multiple
 * individual validation steps. If any of them returns false,
 * the validation fails
 *
 * @param   {Object} config parsed permission config
 *
 * @public
 * @returns {Boolean|String} validationResult Only true is treated as pass.
 */
exports.validate = function (config) {
  let validationStepResult
  let key

  for (key in validationSteps) {
    validationStepResult = validationSteps[key](config)

    if (validationStepResult !== true) {
      return validationStepResult
    }
  }

  return true
}

/**
 * Checks if the configuration is an object
 *
 * @param   {Object} config parsed permission config
 *
 * @private
 * @returns {Boolean}
 */
validationSteps.isValidType = function (config) {
  if (typeof config === 'object') {
    return true
  }

  return `config should be an object literal, but was of type ${typeof config}`
}

/**
 * Makes sure all sections (record, event, rpc) are present
 *
 * @param   {Object} config parsed permission config
 *
 * @private
 * @returns {Boolean}
 */
validationSteps.hasRequiredTopLevelKeys = function (config) {
  for (const key in SCHEMA) {
    if (typeof config[key] !== 'object') {
      return `missing configuration section "${key}"`
    }
  }

  return true
}

/**
 * Makes sure no unsupported sections were added
 *
 * @param   {Object} config parsed permission config
 *
 * @private
 * @returns {Boolean}
 */
validationSteps.doesNotHaveAdditionalTopLevelKeys = function (config) {
  for (const key in config) {
    if (typeof SCHEMA[key] === 'undefined') {
      return `unexpected configuration section "${key}"`
    }
  }

  return true
}

/**
 * Checks if the configuration contains valid path definitions
 *
 * @param   {Object} config parsed permission config
 *
 * @private
 * @returns {Boolean}
 */
validationSteps.doesOnlyContainValidPaths = function (config) {
  let key
  let path
  let result

  for (key in SCHEMA) {
    // Check empty
    if (Object.keys(config[key]).length === 0) {
      return `empty section "${key}"`
    }

    // Check valid
    for (path in config[key]) {
      result = pathParser.validate(path)
      if (result !== true) {
        return `${result} for path ${path} in section ${key}`
      }
    }
  }

  return true
}

/**
 * Each section must specify a generic permission ("*") that
 * will be applied if no other permission is applicable
 *
 * @param   {Object} config parsed permission config
 *
 * @private
 * @returns {Boolean}
 */
validationSteps.doesHaveRootEntries = function (config) {
  let sectionName

  for (sectionName in SCHEMA) {
    if (!config[sectionName]['*']) {
      return `missing root entry "*" for section ${sectionName}`
    }
  }

  return true
}

/**
 * Runs the rule validator against every rule in each section
 *
 * @param   {Object} config parsed permission config
 *
 * @private
 * @returns {Boolean}
 */
validationSteps.hasValidRules = function (config) {
  let path
  let ruleType
  let section
  let validationResult

  for (section in config) {
    for (path in config[section]) {
      for (ruleType in config[section][path]) {
        if (SCHEMA[section][ruleType] !== true) {
          return `unknown rule type ${ruleType} in section ${section}`
        }

        validationResult = ruleParser.validate(config[section][path][ruleType], section, ruleType)
        if (validationResult !== true) {
          return validationResult
        }
      }
    }
  }

  return true
}

